<style>
  body {
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif;
    background-color: #fafafa;
    color: #0a0a0a;
    line-height: 1.5;
  }

  .main-container {
    background: #ffffff;
    border-radius: 12px;
    border: 1px solid #e4e4e7;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
  }

  .form-section {
    background: #ffffff;
    border-radius: 8px;
    border: 1px solid #e4e4e7;
    transition: box-shadow 0.2s;
  }

  .form-section:hover {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  }

  .preview-section {
    background: #ffffff;
    border-radius: 8px;
    border: 1px solid #e4e4e7;
    transition: box-shadow 0.2s;
  }

  .preview-section:hover {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  }

  .form-header {
    background: #0a0a0a;
    color: #fafafa;
    border-radius: 8px 8px 0 0;
    padding: 20px 24px;
    border-bottom: 1px solid #e4e4e7;
  }

  .form-header h2 {
    margin: 0;
    font-size: 1.25rem;
    font-weight: 600;
  }

  .form-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 20px;
  }

  .form-group {
    position: relative;
  }

  .form-label {
    display: block;
    font-size: 0.875rem;
    font-weight: 500;
    color: #0a0a0a;
    margin-bottom: 6px;
  }

  input[type="text"],
  input[type="email"],
  input[type="tel"],
  select {
    width: 100%;
    padding: 12px 16px;
    border: 1px solid #d4d4d8;
    border-radius: 6px;
    font-size: 14px;
    background: #ffffff;
    transition: border-color 0.2s, box-shadow 0.2s;
    box-sizing: border-box;
  }

  input[type="text"]:focus,
  input[type="email"]:focus,
  input[type="tel"]:focus,
  select:focus {
    outline: none;
    border-color: #0a0a0a;
    box-shadow: 0 0 0 2px rgba(10, 10, 10, 0.1);
  }

  .copy-button {
    width: 100%;
    background: #0a0a0a;
    color: #fafafa;
    border: none;
    padding: 12px 24px;
    border-radius: 6px;
    cursor: pointer;
    font-weight: 500;
    font-size: 14px;
    transition: background-color 0.2s;
  }

  .copy-button:hover {
    background: #18181b;
  }

  .copy-button.success {
    background: #16a34a;
  }

  .copy-button.success:hover {
    background: #15803d;
  }

  .instructions {
    background: #f4f4f5;
    border: 1px solid #e4e4e7;
    border-radius: 8px;
    padding: 20px;
    margin-top: 24px;
  }

  .instructions h3 {
    color: #0a0a0a;
    margin-bottom: 16px;
    font-weight: 600;
    font-size: 1rem;
  }

