---
import Layout from "../layouts/Layout.astro";
---

<style>
  body {
    font-family: sans-serif;
  }
  input[type="text"],
  input[type="email"] {
    padding: 10px;
    border: 1px solid #e2e8f0;
    border-radius: 4px;
    box-sizing: border-box;
    width: 100%;
    background: #f8fafc;
  }
</style>

<script>
  document.addEventListener("DOMContentLoaded", () => {
    const namePreview = document.getElementById("acquira-name");
    const rolePreview = document.getElementById("acquira-role");
    const emailPreview = document.getElementById("acquira-email");
    const linkedinPreview = document.getElementById("acquira-linkedin");

    const nameInput = document.getElementById("name");
    const roleInput = document.getElementById("role");
    const emailInput = document.getElementById("email");
    const linkedinInput = document.getElementById("linkedin");

    // array of fields to map
    const mappedFields = [
      {
        name: nameInput,
        preview: namePreview,
      },
      {
        name: roleInput,
        preview: rolePreview,
      },
      {
        name: emailInput,
        preview: emailPreview,
      },
    ];

    // All fields but linkedin
    mappedFields.forEach((field) => {
      field.name.addEventListener("keyup", (e) => {
        field.preview.innerHTML = e.target.value;
      });
    });
    linkedinInput.addEventListener("keyup", (e) => {
      linkedinPreview.href = e.target.value;
    });
  });
</script>

