import Layout from "../layouts/Layout.astro";
---

<style>
  body {
    font-family: sans-serif;
  }
  input[type="text"],
  input[type="email"],
  input[type="tel"],
  select {
    padding: 10px;
    border: 1px solid #e2e8f0;
    border-radius: 4px;
    box-sizing: border-box;
    width: 100%;
    background: #f8fafc;
  }
  .copy-button {
    background: #2563eb;
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 4px;
    cursor: pointer;
    font-weight: 500;
  }
  .copy-button:hover {
    background: #1d4ed8;
  }
  .copy-button.success {
    background: #059669;
  }
  .instructions {
    background: #f0f9ff;
    border: 1px solid #0ea5e9;
    border-radius: 8px;
    padding: 20px;
    margin-top: 20px;
  }
</style>

<script>
  document.addEventListener("DOMContentLoaded", () => {
    const namePreview = document.getElementById("signature-name");
    const phonePreview = document.getElementById("signature-phone");
    const rolePreview = document.getElementById("signature-role");
    const emailPreview = document.getElementById("signature-email");
    const websitePreview = document.getElementById("signature-website");
    const calendlyPreview = document.getElementById("signature-calendly");
    const logoPreview = document.getElementById("signature-logo");

    const nameInput = document.getElementById("name");
    const phoneInput = document.getElementById("phone");
    const roleInput = document.getElementById("role");
    const emailInput = document.getElementById("email");
    const websiteInput = document.getElementById("website");
    const calendlyInput = document.getElementById("calendly");
    const logoSelect = document.getElementById("logo");
    const copyButton = document.getElementById("copy-signature");

    // Update preview fields
    const mappedFields = [
      { input: nameInput, preview: namePreview },
      { input: phoneInput, preview: phonePreview },
      { input: roleInput, preview: rolePreview },
      { input: emailInput, preview: emailPreview },
      { input: websiteInput, preview: websitePreview },
      { input: calendlyInput, preview: calendlyPreview, attribute: 'href' },
    ];

    mappedFields.forEach((field) => {
      field.input.addEventListener("input", (e) => {
        if (field.attribute) {
          field.preview[field.attribute] = e.target.value;
          field.preview.textContent = e.target.value.replace('https://', '').replace('http://', '');
        } else {
          field.preview.textContent = e.target.value;
        }
      });
    });

    // Handle logo selection
    logoSelect.addEventListener("change", (e) => {
      const logos = {
        light: "https://acquira.com/wp-content/uploads/2025/06/fav-light.png",
        dark: "https://acquira.com/wp-content/uploads/2025/06/fav-dark.jpg"
      };
      logoPreview.src = logos[e.target.value] || logos.light;
    });

    // Copy signature functionality
    copyButton.addEventListener("click", async () => {
      const signatureHtml = document.getElementById("signature-preview").innerHTML;

      try {
        // Create a temporary div to get clean HTML
        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = signatureHtml;

        // Copy to clipboard
        await navigator.clipboard.writeText(tempDiv.innerHTML);

        // Update button state
        copyButton.textContent = "Copied!";
        copyButton.classList.add("success");

        setTimeout(() => {
          copyButton.textContent = "Copy Signature";
          copyButton.classList.remove("success");
        }, 2000);
      } catch (err) {
        alert("Unable to copy signature. Please select and copy the signature manually.");
      }
    });

    // Initialize with default values
    nameInput.dispatchEvent(new Event('input'));
    phoneInput.dispatchEvent(new Event('input'));
    roleInput.dispatchEvent(new Event('input'));
    emailInput.dispatchEvent(new Event('input'));
    websiteInput.dispatchEvent(new Event('input'));
    calendlyInput.dispatchEvent(new Event('input'));
    logoSelect.dispatchEvent(new Event('change'));
  });
</script>

<Layout title="Acquira Email Signature Generator">
  <div class='w-full max-w-6xl mx-auto'>
    <section class='grid grid-cols-1 lg:grid-cols-2 gap-10'>
      <!-- Form Section -->
      <div>
        <div class='bg-white border border-zinc-200 shadow-lg rounded-md'>
          <div class='px-6 py-4 border-b border-zinc-200'>
            <h2 class='text-xl font-semibold text-gray-900'>Create Your Signature</h2>
          </div>
          <form class='px-6 py-5 space-y-5'>
            <div>
              <label for='name' class='block text-sm font-medium leading-6 text-gray-900'>
                Full Name
              </label>
              <div class='mt-2'>
                <input
                  type='text'
                  name='name'
                  id='name'
                  class='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6'
                  placeholder='Aloke Nanda'
                  value='Aloke Nanda'
                />
              </div>
            </div>

            <div>
              <label for='phone' class='block text-sm font-medium leading-6 text-gray-900'>
                Cell Phone
              </label>
              <div class='mt-2'>
                <input
                  type='tel'
                  name='phone'
                  id='phone'
                  class='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6'
                  placeholder='727-383-7535'
                  value='727-383-7535'
                />
              </div>
            </div>

            <div>
              <label for='role' class='block text-sm font-medium leading-6 text-gray-900'>
                Position/Role
              </label>
              <div class='mt-2'>
                <input
                  type='text'
                  name='role'
                  id='role'
                  class='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6'
                  placeholder='M&A Team'
                  value='M&A Team'
                />
              </div>
            </div>

            <div>
              <label for='email' class='block text-sm font-medium leading-6 text-gray-900'>
                Email Address
              </label>
              <div class='mt-2'>
                <input
                  type='email'
                  name='email'
                  id='email'
                  class='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6'
                  placeholder='aloke@acquira.com'
                  value='aloke@acquira.com'
                />
              </div>
            </div>

            <div>
              <label for='website' class='block text-sm font-medium leading-6 text-gray-900'>
                Website
              </label>
              <div class='mt-2'>
                <input
                  type='text'
                  name='website'
                  id='website'
                  class='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6'
                  placeholder='www.acquira.com'
                  value='www.acquira.com'
                />
              </div>
            </div>

            <div>
              <label for='calendly' class='block text-sm font-medium leading-6 text-gray-900'>
                Calendly URL
              </label>
              <div class='mt-2'>
                <input
                  type='text'
                  name='calendly'
                  id='calendly'
                  class='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6'
                  placeholder='https://calendly.com/your-name'
                  value='https://calendly.com/aloke-acquira/30min'
                />
              </div>
            </div>

            <div>
              <label for='logo' class='block text-sm font-medium leading-6 text-gray-900'>
                Company Logo
              </label>
              <div class='mt-2'>
                <select
                  name='logo'
                  id='logo'
                  class='block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-blue-600 sm:text-sm sm:leading-6'
                >
                  <option value='light'>Light Logo</option>
                  <option value='dark'>Dark Logo</option>
                </select>
              </div>
            </div>

            <div class='pt-4'>
              <button
                type='button'
                id='copy-signature'
                class='copy-button w-full'
              >
                Copy Signature
              </button>
            </div>
          </form>
        </div>
      </div>

      <!-- Preview Section -->
      <div>
        <div class='bg-white border border-zinc-200 shadow-lg rounded-md mb-5'>
          <div class='px-6 py-4 border-b border-zinc-200'>
            <h3 class='text-lg font-semibold text-gray-900'>Preview</h3>
          </div>
          <div class='p-6'>
            <div id='signature-preview' style='font-family: Arial, sans-serif; font-size: 14px; line-height: 1.4; color: #333;'>
              <table cellpadding='0' cellspacing='0' border='0' style='font-family: Arial, sans-serif; font-size: 14px; line-height: 1.4;'>
                <tr>
                  <td style='padding-right: 20px; vertical-align: top;'>
                    <div style='width: 60px; height: 60px; background-color: #1e3a8a; display: flex; align-items: center; justify-content: center; border-radius: 4px;'>
                      <span style='color: white; font-size: 24px; font-weight: bold;'>A</span>
                    </div>
                  </td>
                  <td style='vertical-align: top;'>
                    <div>
                      <div style='font-weight: bold; font-size: 16px; color: #333; margin-bottom: 2px;'>
                        <span id='signature-name'>Aloke Nanda</span>
                      </div>
                      <div style='color: #666; margin-bottom: 4px;'>
                        Cell: <span id='signature-phone'>727-383-7535</span>
                      </div>
                      <div style='color: #1e3a8a; font-weight: 500; margin-bottom: 8px;'>
                        <span id='signature-role'>M&A Team</span>
                      </div>
                      <div style='margin-bottom: 4px;'>
                        <a href='mailto:' id='signature-email' style='color: #1e3a8a; text-decoration: underline;'>aloke@acquira.com</a>
                      </div>
                      <div style='margin-bottom: 8px;'>
                        <a href='#' id='signature-website' style='color: #1e3a8a; text-decoration: underline;'>www.acquira.com</a>
                      </div>
                      <div style='margin-bottom: 8px;'>
                        <a href='#' id='signature-calendly' style='color: #1e3a8a; text-decoration: underline; font-weight: 500;'>Schedule a Meeting</a>
                      </div>
                      <div style='margin-top: 12px;'>
                        <img 
                          id='signature-logo'
                          src='https://acquira.com/wp-content/uploads/2025/06/fav-light.png' 
                          alt='Company Logo' 
                          style='height: 40px; width: auto;'
                        />
                      </div>
                    </div>
                  </td>
                </tr>
              </table>
            </div>
          </div>
        </div>

        <!-- Instructions Section -->
        <div class='instructions'>
          <h3 class='text-lg font-semibold text-blue-900 mb-3'>How to Add This Signature to Gmail</h3>
          <ol class='list-decimal list-inside space-y-2 text-sm text-blue-800'>
            <li>Click the "Copy Signature" button above</li>
            <li>Open Gmail and click the gear icon (‚öôÔ∏è) in the top right</li>
            <li>Select "See all settings"</li>
            <li>Go to the "General" tab</li>
            <li>Scroll down to the "Signature" section</li>
