import { BusinessProcessCore, BusinessProcessActivity } from '@/types';
import { SwimlaneGridChart } from '@/components/charts/swimlane-grid-chart';
import { route } from 'ziggy-js';
import { router } from '@inertiajs/react';
import { Workflow } from 'lucide-react';
import { Button } from '@/components/ui/button';

interface SwimlaneTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
        activities?: BusinessProcessActivity[];
    };
}

export function SwimlaneTabContent({ core }: SwimlaneTabContentProps) {
    const activities = core.activities || [];

    const handleActivityClick = (activity: BusinessProcessActivity) => {
        // Navigate to the activity blueprint or RACI tab
        const hasBlueprint = activity.blueprint !== undefined && activity.blueprint !== null;

        if (hasBlueprint) {
            const blueprintRoute = route('business-processes.activities.blueprint.show', {
                process: core.process.id,
                core: core.id,
                activity: activity.id,
            });
            router.visit(blueprintRoute);
        } else {
            // Could navigate to RACI tab with focus on this activity
            const currentUrl = new URL(window.location.href);
            currentUrl.searchParams.set('tab', 'raci');
            router.visit(currentUrl.toString());
        }
    };

    const handleManageActivities = () => {
        // Switch to RACI tab to manage activities
        const currentUrl = new URL(window.location.href);
        currentUrl.searchParams.set('tab', 'raci');
        router.visit(currentUrl.toString());
    };

    return (
        <div className="flex h-full flex-col">
            {/* Content Body */}
            <div className="flex-1 overflow-hidden">
                <div className="h-full">
                    {activities.length > 0 ? (
                        <SwimlaneGridChart
                            activities={activities}
                            onActivityClick={handleActivityClick}
                            coreId={core.id}
                            processId={core.process.id}
                            className="h-full"
                        />
                    ) : (
                        /* Empty State */
                        <div className="flex h-full flex-1 items-center justify-center bg-gray-50 p-6">
                            <div className="max-w-md text-center">
                                <Workflow className="mx-auto mb-4 h-16 w-16 text-gray-300" />
                                <h3 className="mb-2 text-lg font-medium text-gray-900">No Process Flow Available</h3>
                                <p className="mb-4 text-gray-500">
                                    Create activities with RACI assignments to generate a dynamic swimlane chart showing your cross-functional process
                                    flow.
                                </p>
                                <Button onClick={handleManageActivities} className="mt-4">
                                    Manage Activities
                                </Button>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

