    sopVersions?: (BusinessProcessContentVersion & { creator?: User; updater?: User })[];
    visualContent?: BusinessProcessContentVersion & { creator?: User; updater?: User };
    visualVersions?: (BusinessProcessContentVersion & { creator?: User; updater?: User })[];
    swimlaneContent?: BusinessProcessContentVersion & { creator?: User; updater?: User };
    swimlaneVersions?: (BusinessProcessContentVersion & { creator?: User; updater?: User })[];
    raciContent?: unknown;
    errors?: Record<string, string>;
}

export default function ShowCore({ core, sopContent, sopVersions, visualContent, errors }: CoreShowProps) {
    // Get tab from URL parameters
    const urlParams = new URLSearchParams(window.location.search);
    const initialTab = urlParams.get('tab') || 'sop';

    // State for active tab with URL sync
    const [activeTab, setActiveTab] = useState(initialTab);

    // Update URL when tab changes
    const handleTabChange = (tab: string) => {
        setActiveTab(tab);

        // Update URL without page reload
        const url = new URL(window.location.href);
        if (tab === 'sop') {
            // Remove tab parameter for default tab
            url.searchParams.delete('tab');
        } else {
            url.searchParams.set('tab', tab);
        }

        // Update URL without triggering navigation
        window.history.replaceState({}, '', url.toString());
    };

    // Listen for browser back/forward navigation
    useEffect(() => {
        const handlePopState = () => {
            const urlParams = new URLSearchParams(window.location.search);
            const tabFromUrl = urlParams.get('tab') || 'sop';
            setActiveTab(tabFromUrl);
        };

        window.addEventListener('popstate', handlePopState);
        return () => window.removeEventListener('popstate', handlePopState);
    }, []);

    // Deep linking examples:
    // /business-processes/1/cores/2 (default to SOP tab)
    // /business-processes/1/cores/2?tab=raci (RACI tab)
    // /business-processes/1/cores/2?tab=visual (Visual tab)
    // /business-processes/1/cores/2?tab=swimlane (Swimlane tab)

    // Define breadcrumbs
    const breadcrumbs: BreadcrumbItem[] = [
        {
            title: 'Dashboard',
            href: route('dashboard'),
        },
        {
            title: 'Business Processes',
            href: route('business-processes'),
        },
        ...(core.process.category
            ? [
                  {
                      title: core.process.category.name,
                      href: '#',
                  },
              ]
            : []),
        {
            title: core.process.name,
            href: route('business-processes.cores.index', core.process.id),
        },
        {
            title: core.name,
            href: route('business-processes.cores.show', [core.process.id, core.id]),
        },
    ];

    const { name, process } = core;
    const [isHtmlDrawerOpen, setIsHtmlDrawerOpen] = useState(false);

    // Delete confirmation dialog state
    const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
    const [isDeletingCore, setIsDeletingCore] = useState(false);

    // Empty activities object for RACI component
    const emptyActivities = {
        data: [],
        current_page: 1,
        first_page_url: '',
        from: 0,
        last_page: 1,
        last_page_url: '',
        links: [],
        next_page_url: null,
        path: '',
        per_page: 15,
        prev_page_url: null,
        to: 0,
        total: 0,
    };

    const confirmDeleteCore = () => {
        setIsDeletingCore(true);

        router.delete(route('business-processes.cores.destroy', [process.id, core.id]), {
            onSuccess: () => {
                toast.success('Core process deleted successfully');
            },
            onError: () => {
                toast.error('Failed to delete core process');
            },
            onFinish: () => {
                setIsDeletingCore(false);
                setDeleteDialogOpen(false);
            },
        });
    };

    const cancelDeleteCore = () => {
        setDeleteDialogOpen(false);
    };

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title={name || 'Show Core Process'} />

            <div className="flex h-[calc(100vh-4rem)] flex-col">
                <Tabs value={activeTab} onValueChange={handleTabChange} className="flex h-full w-full flex-col gap-0">
                    {/* Header with Aligned Tabs */}
                    <div className="flex-shrink-0 border-b border-gray-200 bg-white px-6 py-2">
                        <div className="flex items-center justify-between">
                            <h1 className="text-xl font-semibold text-gray-900">{core.name}</h1>

                            <TabsList className="flex rounded-md bg-gray-100 p-1">
                                <TabsTrigger
                                    value="sop"
                                    className="flex items-center gap-2 rounded-sm px-3 py-1.5 text-sm font-medium data-[state=active]:bg-white data-[state=active]:text-gray-900 data-[state=active]:shadow-sm"
                                >
                                    <FileText className="h-4 w-4" />
                                    SOP
                                </TabsTrigger>
                                <TabsTrigger
                                    value="raci"
                                    className="flex items-center gap-2 rounded-sm px-3 py-1.5 text-sm font-medium data-[state=active]:bg-white data-[state=active]:text-gray-900 data-[state=active]:shadow-sm"
                                >
                                    <Users className="h-4 w-4" />
                                    RACI
                                </TabsTrigger>
                                <TabsTrigger
                                    value="visual"
                                    className="flex items-center gap-2 rounded-sm px-3 py-1.5 text-sm font-medium data-[state=active]:bg-white data-[state=active]:text-gray-900 data-[state=active]:shadow-sm"
                                >
                                    <Workflow className="h-4 w-4" />
                                    Visual
                                </TabsTrigger>
                                <TabsTrigger
                                    value="swimlane"
                                    className="flex items-center gap-2 rounded-sm px-3 py-1.5 text-sm font-medium data-[state=active]:bg-white data-[state=active]:text-gray-900 data-[state=active]:shadow-sm"
                                >
                                    <Waypoints className="h-4 w-4" />
                                    Swimlane
                                </TabsTrigger>
                            </TabsList>
                        </div>
                    </div>

                    {/* Tab Content - takes remaining height */}
                    <div className="min-h-0 flex-1">
                        <TabsContent value="sop" className="m-0 h-full data-[state=inactive]:hidden">
                            {/* eslint-disable-next-line @typescript-eslint/no-explicit-any */}
                            <SOPTabContent core={core} content={sopContent as any} versions={sopVersions as any} errors={errors} showHeader={false} />
                        </TabsContent>
                        <TabsContent value="raci" className="m-0 h-full data-[state=inactive]:hidden">
                            <RaciTabContent core={core} activities={emptyActivities} />
                        </TabsContent>
                        <TabsContent value="visual" className="m-0 h-full data-[state=inactive]:hidden">
                            <VisualTabContent core={core} content={visualContent} />
                        </TabsContent>
                        <TabsContent value="swimlane" className="m-0 h-full data-[state=inactive]:hidden">
                            <SwimlaneTabContent core={core} />
                        </TabsContent>
                    </div>
                </Tabs>
            </div>

            <HtmlImportDialog
                isOpen={isHtmlDrawerOpen}
                onClose={() => setIsHtmlDrawerOpen(false)}
                diagramId={core.id}
                // Legacy props - provide dummy values for new required props
                open={isHtmlDrawerOpen}
                onOpenChange={(open) => setIsHtmlDrawerOpen(open)}
                onImport={() => Promise.resolve()}
            />

            {/* Delete Confirmation Dialog */}
            <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
                <AlertDialogContent>
                    <AlertDialogHeader>
                        <AlertDialogTitle>Are you sure you want to delete this core process?</AlertDialogTitle>
                        <AlertDialogDescription>
                            This action cannot be undone. This will permanently delete the core process "{core.name}" and all its activities from your
                            process.
                        </AlertDialogDescription>
                    </AlertDialogHeader>
                    <AlertDialogFooter>
                        <AlertDialogCancel onClick={cancelDeleteCore}>Cancel</AlertDialogCancel>
                        <AlertDialogAction onClick={confirmDeleteCore} disabled={isDeletingCore}>
                            {isDeletingCore ? 'Deleting...' : 'Delete'}
                        </AlertDialogAction>
                    </AlertDialogFooter>
                </AlertDialogContent>
            </AlertDialog>
        </AppLayout>
    );
}

