import { BusinessProcessActivity } from '@/types';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { FileText, Plus } from 'lucide-react';
import { route } from 'ziggy-js';
import { router } from '@inertiajs/react';
import { useEffect, useRef } from 'react';

interface SwimlaneGridChartProps {
    activities: BusinessProcessActivity[];
    onActivityClick?: (activity: BusinessProcessActivity) => void;
    className?: string;
    coreId: number;
    processId: number;
}

// Extract unique responsible people from activities
const extractResponsiblePeople = (activities: BusinessProcessActivity[]) => {
    const peopleSet = new Set<string>();

    activities.forEach((activity) => {
        const parseField = (field?: string) =>
            field
                ?.split(',')
                .map((s) => s.trim())
                .filter((s) => s) || [];

        // Only extract from responsible field
        parseField(activity.responsible).forEach((person) => peopleSet.add(person));
    });

    return Array.from(peopleSet).sort();
};

// Role color mapping (matching the example)
const ROLE_COLORS = [
    { bg: 'bg-blue-500', text: 'text-white' },
    { bg: 'bg-green-500', text: 'text-white' },
    { bg: 'bg-purple-500', text: 'text-white' },
    { bg: 'bg-orange-500', text: 'text-white' },
    { bg: 'bg-red-500', text: 'text-white' },
    { bg: 'bg-indigo-500', text: 'text-white' },
    { bg: 'bg-pink-500', text: 'text-white' },
    { bg: 'bg-gray-500', text: 'text-white' },
    { bg: 'bg-yellow-500', text: 'text-white' },
    { bg: 'bg-teal-500', text: 'text-white' },
];

const getPersonColor = (personName: string, people: string[]) => {
    const index = people.indexOf(personName);
    return ROLE_COLORS[index % ROLE_COLORS.length] || { bg: 'bg-gray-400', text: 'text-white' };
};

const parseRaciField = (field?: string): string[] => {
    return (
        field
            ?.split(',')
            .map((s) => s.trim())
            .filter((s) => s) || []
    );
};

export function SwimlaneGridChart({ activities, onActivityClick, className, coreId, processId }: SwimlaneGridChartProps) {
    const sortedActivities = [...activities].sort((a, b) => a.position - b.position);
    const responsiblePeople = extractResponsiblePeople(activities);

    const containerRef = useRef<HTMLDivElement>(null);
    const scrollContainerRef = useRef<HTMLDivElement>(null);
    const chartContentRef = useRef<HTMLTableElement>(null);

    useEffect(() => {
        // Log dimensions to help debug scroll issues
        if (containerRef.current) {
            console.log('Container dimensions:', {
                width: containerRef.current.offsetWidth,
                height: containerRef.current.offsetHeight,
                scrollWidth: containerRef.current.scrollWidth,
                scrollHeight: containerRef.current.scrollHeight,
                clientWidth: containerRef.current.clientWidth,
                clientHeight: containerRef.current.clientHeight,
                hasHorizontalScroll: containerRef.current.scrollWidth > containerRef.current.clientWidth,
                hasVerticalScroll: containerRef.current.scrollHeight > containerRef.current.clientHeight,
            });
        }

        if (scrollContainerRef.current) {
            console.log('Scroll container dimensions:', {
                width: scrollContainerRef.current.offsetWidth,
                height: scrollContainerRef.current.offsetHeight,
                scrollWidth: scrollContainerRef.current.scrollWidth,
                scrollHeight: scrollContainerRef.current.scrollHeight,
                clientWidth: scrollContainerRef.current.clientWidth,
                clientHeight: scrollContainerRef.current.clientHeight,
                hasHorizontalScroll: scrollContainerRef.current.scrollWidth > scrollContainerRef.current.clientWidth,
                hasVerticalScroll: scrollContainerRef.current.scrollHeight > scrollContainerRef.current.clientHeight,
            });
        }

        if (chartContentRef.current) {
            console.log('Chart content dimensions:', {
                width: chartContentRef.current.offsetWidth,
                height: chartContentRef.current.offsetHeight,
                scrollWidth: chartContentRef.current.scrollWidth,
                scrollHeight: chartContentRef.current.scrollHeight,
            });
        }

        // Check parent elements for overflow
        let parent = containerRef.current?.parentElement;
        let level = 0;
        while (parent && level < 5) {
            const styles = window.getComputedStyle(parent);
            console.log(`Parent level ${level}:`, {
                className: parent.className,
                id: parent.id,
                overflow: styles.overflow,
                overflowX: styles.overflowX,
                overflowY: styles.overflowY,
                width: parent.offsetWidth,
                height: parent.offsetHeight,
                scrollWidth: parent.scrollWidth,
                scrollHeight: parent.scrollHeight,
                clientWidth: parent.clientWidth,
                clientHeight: parent.clientHeight,
                hasHorizontalScroll: parent.scrollWidth > parent.clientWidth,
                hasVerticalScroll: parent.scrollHeight > parent.clientHeight,
            });
            parent = parent.parentElement;
            level++;
        }

        // Also check body and html elements
        // console.log('Body element:', {
        //     overflow: window.getComputedStyle(document.body).overflow,
        //     overflowX: window.getComputedStyle(document.body).overflowX,
        //     overflowY: window.getComputedStyle(document.body).overflowY,
        //     scrollWidth: document.body.scrollWidth,
        //     clientWidth: document.body.clientWidth,
        //     hasHorizontalScroll: document.body.scrollWidth > document.body.clientWidth
        // });

        // console.log('HTML element:', {
        //     overflow: window.getComputedStyle(document.documentElement).overflow,
        //     overflowX: window.getComputedStyle(document.documentElement).overflowX,
        //     overflowY: window.getComputedStyle(document.documentElement).overflowY,
        //     scrollWidth: document.documentElement.scrollWidth,
        //     clientWidth: document.documentElement.clientWidth,
        //     hasHorizontalScroll: document.documentElement.scrollWidth > document.documentElement.clientWidth
        // });
    }, [activities, responsiblePeople]);

    const handleBlueprintClick = (activity: BusinessProcessActivity, hasBlueprint: boolean) => {
        if (hasBlueprint) {
            // Navigate to existing blueprint
            const blueprintRoute = route('business-processes.activities.blueprint.show', {
                process: processId,
                core: coreId,
                activity: activity.id,
            });
            router.visit(blueprintRoute);
        } else {
            // Create new blueprint
            const createRoute = route('business-processes.activities.blueprint.create', {
                process: processId,
                core: coreId,
                activity: activity.id,
            });
            router.visit(createRoute);
        }
    };

    const isPersonResponsible = (activity: BusinessProcessActivity, personName: string) => {
        const responsible = parseRaciField(activity.responsible);
        return responsible.includes(personName);
    };

    if (activities.length === 0) {
        return (
            <div className={`rounded-lg bg-gray-50 p-8 text-center ${className}`}>
                <div className="text-gray-500">
                    <div className="mb-2 text-lg font-medium">No Activities Available</div>
                    <div className="text-sm">Add activities with RACI assignments to generate the swimlane chart.</div>
                </div>
            </div>
        );
    }

    return (
        <div ref={containerRef} className={`flex h-full w-full flex-col bg-gray-50 ${className}`}>
            <div className="flex h-full w-full flex-col overflow-hidden p-6">
                <div className="mb-6 flex-shrink-0">
                    <h1 className="mb-2 text-2xl font-bold text-gray-900">Process Swimlane Chart</h1>
                    <p className="text-gray-600">Process flow showing activities across responsible team members</p>
                </div>

                {/* Legend */}
                <div className="mb-6 flex-shrink-0 rounded-lg border bg-white p-4">
                    <h3 className="mb-3 font-semibold">Responsible People</h3>
                    <div className="flex flex-wrap gap-3">
                        {responsiblePeople.map((person) => {
                            const colors = getPersonColor(person, responsiblePeople);
                            return (
                                <div key={person} className="flex items-center gap-2">
                                    <div className={`h-4 w-4 rounded ${colors.bg}`}></div>
                                    <span className="text-sm font-medium">{person}</span>
                                </div>
                            );
                        })}
                    </div>
                </div>

                {/* Swimlane Chart with contained scrolling */}
                <div className="relative min-h-0 flex-1 overflow-hidden rounded border bg-white">
                    <div ref={scrollContainerRef} className="absolute inset-0 overflow-auto">
                        <table ref={chartContentRef} className="border-separate border-spacing-1">
                            <colgroup>
                                <col />
                                {sortedActivities.map((activity) => (
                                    <col key={activity.id} className="w-48" />
                                ))}
                            </colgroup>
                            <thead>
                                <tr>
                                    <th className="rounded bg-gray-800 p-3 text-center text-white">Responsible Person</th>
                                    {sortedActivities.map((activity) => (
                                        <th key={activity.id} className="w-48 rounded border bg-gray-100 p-2 text-center text-xs font-medium">
                                            Step {activity.position}
                                        </th>
                                    ))}
                                </tr>
                            </thead>
                            <tbody>
                                {responsiblePeople.map((person) => {
                                    const colors = getPersonColor(person, responsiblePeople);
                                    const personActivities = sortedActivities.filter((activity) => isPersonResponsible(activity, person));

                                    return (
                                        <tr key={person}>
                                            {/* Person Name Column */}
                                            <td className={`${colors.bg} ${colors.text} rounded p-3 text-center font-semibold`}>
                                                <div className="w-40">
                                                    <div className="font-bold">{person}</div>
                                                    <div className="mt-1 text-xs opacity-90">({personActivities.length} activities)</div>
                                                </div>
                                            </td>

                                            {/* Activity Columns */}
                                            {sortedActivities.map((activity) => {
                                                const isResponsible = isPersonResponsible(activity, person);
                                                const hasBlueprint = activity.blueprint !== undefined && activity.blueprint !== null;

                                                return (
                                                    <td key={`${person}-${activity.id}`} className="h-[120px] w-48 p-1 align-top">
                                                        {isResponsible ? (
                                                            <Card
                                                                className={`h-full ${colors.bg} ${colors.text} cursor-pointer transition hover:scale-105`}
                                                                onClick={() => onActivityClick?.(activity)}
                                                            >
                                                                <CardContent className="p-3">
                                                                    <div className="mb-3 text-sm leading-tight font-medium">{activity.name}</div>

                                                                    {/* Blueprint Actions */}
                                                                    <Button
                                                                        size="sm"
                                                                        variant={hasBlueprint ? 'default' : 'outline'}
                                                                        className="text-xs"
                                                                        onClick={(e) => {
                                                                            e.stopPropagation();
                                                                            handleBlueprintClick(activity, hasBlueprint);
                                                                        }}
                                                                    >
                                                                        {hasBlueprint ? (
                                                                            <span className="flex items-center justify-center">
                                                                                <FileText className="mr-1 h-3 w-3" />
                                                                                View Blueprint
                                                                            </span>
                                                                        ) : (
                                                                            <span className="flex items-center justify-center text-gray-700">
                                                                                <Plus className="mr-1 h-3 w-3" />
                                                                                Add Blueprint
                                                                            </span>
                                                                        )}
                                                                    </Button>
                                                                </CardContent>
                                                            </Card>
                                                        ) : (
                                                            <div className="flex h-full w-full items-center justify-center rounded border border-gray-200 bg-gray-50"></div>
                                                        )}
                                                    </td>
                                                );
                                            })}
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </div>
                </div>

                {/* Process Flow Indicators */}
                <div className="mt-6 flex flex-shrink-0 items-center justify-center gap-6 text-sm text-gray-600">
                    <div className="flex items-center gap-2">
                        <div className="h-4 w-4 rounded bg-blue-500"></div>
                        <span>Activity assigned to person</span>
                    </div>
                    <div className="flex items-center gap-2">
                        <div className="h-4 w-4 rounded border border-gray-300 bg-gray-100"></div>
                        <span>No responsibility</span>
                    </div>
                    <div className="flex items-center gap-2">
                        <Badge className="bg-blue-600 text-xs text-white">Responsible</Badge>
                        <span>Person is responsible for this activity</span>
                    </div>
                </div>
            </div>
        </div>
    );
}

