                {/* Legend */}
                <div className="mb-6 flex-shrink-0 rounded-lg border bg-white p-6 shadow-sm">
                    <h3 className="mb-4 text-lg font-semibold text-gray-900">Responsible People</h3>
                    <div className="flex flex-wrap gap-4">
                        {responsiblePeople.map((person) => {
                            const colors = getPersonColor(person, responsiblePeople);
                            return (
                                <div key={person} className="flex items-center gap-2">
                                    <div className={cn("h-4 w-4 rounded-lg", colors.bg)}></div>
                                    <span className="text-sm font-medium text-gray-700">{person}</span>
