import { BusinessProcessActivity } from '@/types';
import { Card, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { FileText, Plus } from 'lucide-react';
import { route } from 'ziggy-js';
import { router } from '@inertiajs/react';
import { cn } from '@/lib/utils';

interface SwimlaneGridChartProps {
    activities: BusinessProcessActivity[];
    onActivityClick?: (activity: BusinessProcessActivity) => void;
    className?: string;
    coreId: number;
    processId: number;
}

// Extract unique responsible people from activities
const extractResponsiblePeople = (activities: BusinessProcessActivity[]) => {
    const peopleSet = new Set<string>();

    activities.forEach((activity) => {
        const parseField = (field?: string) =>
            field
                ?.split(',')
                .map((s) => s.trim())
                .filter((s) => s) || [];

        // Only extract from responsible field
        parseField(activity.responsible).forEach((person) => peopleSet.add(person));
    });

    return Array.from(peopleSet).sort();
};

// Role color mapping (matching the example)
const ROLE_COLORS = [
    { bg: 'bg-blue-500', text: 'text-white' },
    { bg: 'bg-green-500', text: 'text-white' },
    { bg: 'bg-purple-500', text: 'text-white' },
    { bg: 'bg-orange-500', text: 'text-white' },
    { bg: 'bg-red-500', text: 'text-white' },
    { bg: 'bg-indigo-500', text: 'text-white' },
    { bg: 'bg-pink-500', text: 'text-white' },
    { bg: 'bg-gray-500', text: 'text-white' },
    { bg: 'bg-yellow-500', text: 'text-white' },
    { bg: 'bg-teal-500', text: 'text-white' },
];

const getPersonColor = (personName: string, people: string[]) => {
    const index = people.indexOf(personName);
    return ROLE_COLORS[index % ROLE_COLORS.length] || { bg: 'bg-gray-400', text: 'text-white' };
};

const parseRaciField = (field?: string): string[] => {
    return (
        field
            ?.split(',')
            .map((s) => s.trim())
            .filter((s) => s) || []
    );
};

export function SwimlaneGridChart({ activities, onActivityClick, className, coreId, processId }: SwimlaneGridChartProps) {
    const sortedActivities = [...activities].sort((a, b) => a.position - b.position);
    const responsiblePeople = extractResponsiblePeople(activities);

    const containerRef = useRef<HTMLDivElement>(null);
    const scrollContainerRef = useRef<HTMLDivElement>(null);
    const chartContentRef = useRef<HTMLTableElement>(null);

    // Production-ready component - debug logging removed

    const handleBlueprintClick = (activity: BusinessProcessActivity, hasBlueprint: boolean) => {
        if (hasBlueprint) {
            // Navigate to existing blueprint
            const blueprintRoute = route('business-processes.activities.blueprint.show', {
                process: processId,
                core: coreId,
                activity: activity.id,
            });
            router.visit(blueprintRoute);
        } else {
            // Create new blueprint
            const createRoute = route('business-processes.activities.blueprint.create', {
                process: processId,
                core: coreId,
                activity: activity.id,
            });
            router.visit(createRoute);
        }
    };

    const isPersonResponsible = (activity: BusinessProcessActivity, personName: string) => {
        const responsible = parseRaciField(activity.responsible);
        return responsible.includes(personName);
    };

    if (activities.length === 0) {
        return (
            <div className={cn("rounded-lg bg-gray-50 p-8 text-center", className)}>
                <div className="text-gray-500">
                    <div className="mb-2 text-lg font-medium text-gray-700">No Activities Available</div>
                    <div className="text-sm text-gray-600">Add activities with RACI assignments to generate the swimlane chart.</div>
                </div>
            </div>
        );
    }

    return (
        <div ref={containerRef} className={cn("flex h-full w-full flex-col bg-gray-50", className)}>
            <div className="flex h-full w-full flex-col overflow-hidden p-6">
                <div className="mb-6 flex-shrink-0">
                    <h1 className="mb-2 text-2xl font-bold text-gray-900">Process Swimlane Chart</h1>
                    <p className="text-sm text-gray-600">Process flow showing activities across responsible team members</p>
                </div>

                {/* Legend */}
                <div className="mb-6 flex-shrink-0 rounded-lg border bg-white p-6 shadow-sm">
                    <h3 className="mb-4 text-lg font-semibold text-gray-900">Responsible People</h3>
                    <div className="flex flex-wrap gap-4">
                        {responsiblePeople.map((person) => {
                            const colors = getPersonColor(person, responsiblePeople);
                            return (
                                <div key={person} className="flex items-center gap-2">
                                    <div className={cn("h-4 w-4 rounded-lg", colors.bg)}></div>
                                    <span className="text-sm font-medium text-gray-700">{person}</span>
                                </div>
                            );
                        })}
                    </div>
                </div>

                {/* Swimlane Chart with contained scrolling */}
                <div className="relative min-h-0 flex-1 overflow-hidden rounded-lg border bg-white shadow-sm">
                    <div ref={scrollContainerRef} className="absolute inset-0 overflow-auto">
                        <table ref={chartContentRef} className="w-full border-separate border-spacing-2">
                            <colgroup>
                                <col className="w-64" />
                                {sortedActivities.map((activity) => (
                                    <col key={activity.id} className="w-48" />
                                ))}
                            </colgroup>
                            <thead>
                                <tr>
                                    <th className="h-24 w-64 rounded-lg bg-gray-900 p-4 text-center font-semibold text-white shadow-sm">
                                        <div className="text-sm font-semibold">Responsible Person</div>
                                    </th>
                                    {sortedActivities.map((activity) => (
                                        <th key={activity.id} className="h-24 w-48 rounded-lg border border-gray-200 bg-gray-50 p-4 text-center shadow-sm">
                                            <div className="text-xs font-semibold text-gray-700">Step {activity.position}</div>
                                        </th>
                                    ))}
                                </tr>
                            </thead>
                            <tbody>
                                {responsiblePeople.map((person) => {
                                    const colors = getPersonColor(person, responsiblePeople);
                                    const personActivities = sortedActivities.filter((activity) => isPersonResponsible(activity, person));

                                    return (
                                        <tr key={person}>
                                            {/* Person Name Column */}
                                            <td className={cn(
                                                "h-32 w-64 rounded-lg p-4 text-center font-semibold shadow-sm",
                                                colors.bg,
                                                colors.text
                                            )}>
                                                <div className="flex h-full flex-col items-center justify-center">
                                                    <div className="text-sm font-bold">{person}</div>
                                                    <div className="mt-1 text-xs opacity-90">({personActivities.length} activities)</div>
                                                </div>
                                            </td>

                                            {/* Activity Columns */}
                                            {sortedActivities.map((activity) => {
                                                const isResponsible = isPersonResponsible(activity, person);
                                                const hasBlueprint = activity.blueprint !== undefined && activity.blueprint !== null;

                                                return (
                                                    <td key={`${person}-${activity.id}`} className="h-32 w-48 p-1 align-top">
                                                        {isResponsible ? (
                                                            <Card
                                                                className={cn(
                                                                    "flex h-full cursor-pointer flex-col rounded-lg border-2 border-gray-800 shadow-sm transition-all hover:shadow-md",
                                                                    colors.bg,
                                                                    colors.text
                                                                )}
                                                                onClick={() => onActivityClick?.(activity)}
                                                            >
                                                                <CardContent className="flex h-full flex-col justify-between p-3">
                                                                    <div className="text-xs font-semibold leading-tight">{activity.name}</div>

                                                                    <Button
                                                                        size="sm"
                                                                        variant={hasBlueprint ? 'default' : 'outline'}
                                                                        className={cn(
                                                                            "h-7 w-full text-xs font-medium transition-colors",
                                                                            hasBlueprint 
                                                                                ? "bg-white text-gray-900 hover:bg-gray-100" 
                                                                                : "border-white bg-transparent text-white hover:bg-white hover:text-gray-900"
                                                                        )}
                                                                        onClick={(e) => {
                                                                            e.stopPropagation();
                                                                            handleBlueprintClick(activity, hasBlueprint);
                                                                        }}
                                                                    >
                                                                        {hasBlueprint ? (
                                                                            <>
                                                                                <FileText className="mr-1 h-3 w-3" />
                                                                                View Blueprint
                                                                            </>
                                                                        ) : (
                                                                            <>
                                                                                <Plus className="mr-1 h-3 w-3" />
                                                                                Add Blueprint
                                                                            </>
                                                                        )}
                                                                    </Button>
                                                                </CardContent>
                                                            </Card>
                                                        ) : (
                                                            <div className="flex h-full w-full items-center justify-center rounded-lg border border-gray-200 bg-gray-50 shadow-sm">
                                                                <div className="text-xs text-gray-400">—</div>
                                                            </div>
                                                        )}
                                                    </td>
                                                );
                                            })}
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </div>
                </div>

                {/* Process Flow Indicators */}
                <div className="mt-6 flex flex-shrink-0 items-center justify-center gap-6 text-sm text-gray-600">
                    <div className="flex items-center gap-2">
                        <div className="h-4 w-4 rounded-lg bg-blue-500"></div>
                        <span className="text-sm text-gray-600">Activity assigned to person</span>
                    </div>
                    <div className="flex items-center gap-2">
                        <div className="h-4 w-4 rounded-lg border border-gray-300 bg-gray-100"></div>
                        <span className="text-sm text-gray-600">No responsibility</span>
                    </div>
                </div>
            </div>
        </div>
    );
}
