142â†’     */
143â†’    public function saveAssessmentAnswers(User $user, int $templateId, array $answers): UserAssessment
144â†’    {
145â†’        Log::info('ðŸ”„ AssessmentService::saveAssessmentAnswers started', [
146â†’            'user_id' => $user->id,
147â†’            'template_id' => $templateId,
148â†’            'answers_count' => count($answers),
149â†’            'sample_answers' => array_slice($answers, 0, 3, true),
150â†’        ]);
151â†’
152â†’        $assessment = UserAssessment::where('user_id', $user->id)
153â†’            ->where('assessment_template_id', $templateId)
154â†’            ->firstOrFail();
155â†’
156â†’        Log::info('âœ… User assessment found', [
157â†’            'assessment_id' => $assessment->id,
158â†’            'current_status' => $assessment->status->value,
159â†’            'current_answers_count' => count($assessment->answers ?? []),
160â†’        ]);
161â†’
162â†’        // Process answers to handle review flags
163â†’        $processedAnswers = $this->processAnswersWithReviewFlags($answers);
164â†’
165â†’        Log::info('ðŸ“ Answers processed with review flags', [
166â†’            'processed_count' => count($processedAnswers),
167â†’            'sample_processed' => array_slice($processedAnswers, 0, 2, true),
168â†’        ]);
169â†’
170â†’        // Validate answers
171â†’        $template = $assessment->assessmentTemplate;
172â†’        $validatedAnswers = $this->validateAnswers($template, $processedAnswers);
173â†’
174â†’        Log::info('âœ… Answers validated', [
175â†’            'validated_count' => count($validatedAnswers),
176â†’            'sample_validated' => array_slice($validatedAnswers, 0, 2, true),
177â†’        ]);
178â†’
179â†’        // Update assessment with new answer format
180â†’        $currentAnswers = $assessment->answers ?? [];
181â†’        $beforeCount = count($currentAnswers);
182â†’
183â†’        foreach ($validatedAnswers as $key => $answerData) {
184â†’            $currentAnswers[$key] = $answerData;
185â†’        }
186â†’        $assessment->answers = $currentAnswers;
187â†’
188â†’        Log::info('ðŸ’¾ Assessment answers updated', [
189â†’            'before_count' => $beforeCount,
190â†’            'after_count' => count($currentAnswers),
191â†’            'new_answers_added' => count($validatedAnswers),
192â†’        ]);
193â†’
194â†’        // Determine status based on completion
195â†’        $progress = $assessment->getProgress();
196â†’        if ($progress === 100) {
197â†’            $assessment->status = AssessmentStatus::COMPLETED;
198â†’            $assessment->completed_at = now();
199â†’        } elseif ($progress > 0) {
200â†’            $assessment->status = AssessmentStatus::IN_PROGRESS;
201â†’        }
202â†’
203â†’        $assessment->save();
204â†’
205â†’        Log::info('ðŸ’¾ Assessment saved to database', [
206â†’            'assessment_id' => $assessment->id,
207â†’            'final_status' => $assessment->status->value,
208â†’            'final_progress' => $progress,
209â†’            'completed_at' => $assessment->completed_at,
210â†’        ]);
211â†’
212â†’        // Calculate scores if completed
213â†’        if ($assessment->isCompleted()) {
214â†’            $assessment->calculateScores();
215â†’            $this->progressService->markStepCompleted($user, $templateId);
216â†’            Log::info('ðŸŽ¯ Assessment completed - scores calculated');
217â†’        }
218â†’
219â†’        Log::info('âœ… AssessmentService::saveAssessmentAnswers completed successfully');
220â†’        return $assessment;
221â†’    }
222â†’
223â†’    /**
224â†’     * Process incoming answers to handle review flags.
225â†’     */
226â†’    private function processAnswersWithReviewFlags(array $answers): array
227â†’    {
228â†’        $processedAnswers = [];
229â†’
230â†’        foreach ($answers as $key => $value) {
231â†’            // Skip meta fields like is_complete, assessment_template_id
232â†’            if (in_array($key, ['is_complete', 'assessment_template_id'])) {
233â†’                continue;
234â†’            }
235â†’
236â†’            // Check if this key has a corresponding review flag
237â†’            $reviewKey = $key . '_review_later';
238â†’            $reviewFlag = isset($answers[$reviewKey]) ? (bool) $answers[$reviewKey] : false;
239â†’
240â†’            // Store in new format
241â†’            $processedAnswers[$key] = [
242â†’                'value' => $value,
243â†’                'review_later' => $reviewFlag,
244â†’            ];
245â†’        }
246â†’
247â†’        return $processedAnswers;
248â†’    }
249â†’
250â†’    /**
251â†’     * Validate answers against question requirements.
252â†’     */
253â†’    private function validateAnswers(AssessmentTemplate $template, array $answers): array
254â†’    {
255â†’        $validatedAnswers = [];
256â†’
257â†’        // Load questions once and key by value_key for efficient lookup
258â†’        $questions = $template->questions()->get()->keyBy('value_key');
259â†’
260â†’        foreach ($answers as $key => $answerData) {
261â†’            $question = $questions->get($key);
262â†’
263â†’            if ($question) {
264â†’                // Extract the actual answer value for validation
265â†’                $answerValue = is_array($answerData) && isset($answerData['value'])
266â†’                    ? $answerData['value']
267â†’                    : $answerData;
268â†’
269â†’                if ($question->validateAnswer($answerValue)) {
270â†’                    // Store the complete answer data (value + review flag)
271â†’                    $validatedAnswers[$key] = is_array($answerData) ? $answerData : [
272â†’                        'value' => $answerData,
273â†’                        'review_later' => false,
274â†’                    ];
275â†’                }
276â†’            }
277â†’        }
278â†’
279â†’        Log::info('ðŸ” Validation complete', [
280â†’            'input_count' => count($answers),
281â†’            'validated_count' => count($validatedAnswers),
282â†’        ]);
283â†’
284â†’        return $validatedAnswers;
285â†’    }
286â†’
287â†’    /**
288â†’     * Complete an assessment and trigger progress updates.
289â†’     */
290â†’    public function completeAssessment(User $user, int $templateId, array $answers): UserAssessment
291â†’    {
292â†’        $assessment = $this->saveAssessmentAnswers($user, $templateId, $answers);
293â†’
294â†’        Log::info('ðŸ” Assessment after save', [
295â†’            'assessment_id' => $assessment->id,
296â†’            'status' => $assessment->status->value,
297â†’            'progress' => $assessment->getProgress(),
298â†’        ]);
299â†’
300â†’        if (!$assessment->isCompleted()) {
301â†’            // Force completion if all required questions are answered
302â†’            $allRequiredAnswered = $this->areAllRequiredQuestionsAnswered($assessment);
303â†’
304â†’            if ($allRequiredAnswered) {
305â†’                Log::info('âœ… Forcing completion - all required questions answered');
306â†’                $assessment->markCompleted();
307â†’            }
308â†’        }
309â†’
310â†’        return $assessment;
311â†’    }
