313â†’    /**
314â†’     * Get next available assessment for a user.
315â†’     */
316â†’    public function getNextAvailableAssessment(User $user): ?AssessmentTemplate
317â†’    {
318â†’        // Get all completed assessments to determine the actual highest completed step
319â†’        $completedSteps = UserAssessment::where('user_id', $user->id)
320â†’            ->where('status', AssessmentStatus::COMPLETED)
321â†’            ->join('assessment_templates', 'user_assessments.assessment_template_id', '=', 'assessment_templates.id')
322â†’            ->where('assessment_templates.is_active', true) // Only consider active assessments
323â†’            ->pluck('assessment_templates.step_number')
324â†’            ->toArray();
325â†’
326â†’        if (empty($completedSteps)) {
327â†’            // No assessments completed yet, return step 1 (profile) if it's active
328â†’            return AssessmentTemplate::active()->where('step_number', 1)->first();
329â†’        }
330â†’
331â†’        // Find the highest completed step
332â†’        $highestCompleted = max($completedSteps);
333â†’
334â†’        Log::info('ðŸ” Calculating next available assessment', [
335â†’            'user_id' => $user->id,
336â†’            'completed_steps' => $completedSteps,
337â†’            'highest_completed' => $highestCompleted,
338â†’        ]);
339â†’
340â†’        // Find the next active assessment with step number greater than highest completed
341â†’        // This will skip inactive assessments and find the next available active one
342â†’        return AssessmentTemplate::active()
343â†’            ->where('step_number', '>', $highestCompleted)
344â†’            ->orderBy('step_number')
345â†’            ->first();
346â†’    }
347â†’
348â†’    /**
349â†’     * Check if all required questions are answered.
350â†’     */
351â†’    private function areAllRequiredQuestionsAnswered(UserAssessment $assessment): bool
352â†’    {
353â†’        $requiredQuestions = $assessment->assessmentTemplate
354â†’            ->questions()
355â†’            ->where('required', true)
356â†’            ->get();
357â†’
358â†’        foreach ($requiredQuestions as $question) {
359â†’            $answerValue = $assessment->getAnswerValue($question->value_key);
360â†’            if ($answerValue === null || $answerValue === '') {
361â†’                return false;
362â†’            }
363â†’        }
364â†’
365â†’        return true;
366â†’    }
367â†’
368â†’    /**
369â†’     * Get assessment completion statistics for a user.
370â†’     */
371â†’    public function getUserCompletionStats(User $user): array
372â†’    {
373â†’        // Check cache first
374â†’        $cacheKey = 'completion_stats_' . $user->id;
375â†’        if (isset($this->userCompletionCache[$cacheKey])) {
376â†’            return $this->userCompletionCache[$cacheKey];
377â†’        }
378â†’
379â†’        $progress = UserAssessmentProgress::where('user_id', $user->id)->first();
380â†’
381â†’        // Get all assessments based on user type
382â†’        $allAssessments = $user->isSuperAdmin()
383â†’            ? AssessmentTemplate::all()
384â†’            : AssessmentTemplate::active()->get();
385â†’
386â†’        $totalAssessments = $allAssessments->count();
387â†’
388â†’        // Get all completed assessments for this user in one query to avoid N+1
389â†’        $completedAssessmentIds = UserAssessment::where('user_id', $user->id)
390â†’            ->where('status', AssessmentStatus::COMPLETED)
391â†’            ->whereIn('assessment_template_id', $allAssessments->pluck('id'))
392â†’            ->pluck('assessment_template_id')
393â†’            ->toArray();
394â†’
395â†’        // Count only assessments that the user can actually access and has completed
396â†’        $completedAssessments = 0;
397â†’        foreach ($allAssessments as $template) {
398â†’            // Check if user can access this assessment AND has completed it
399â†’            if (
400â†’                $this->canUserAccessAssessment($user, $template) &&
401â†’                in_array($template->id, $completedAssessmentIds)
402â†’            ) {
403â†’                $completedAssessments++;
404â†’            }
405â†’        }
406â†’
407â†’        $result = [
408â†’            'total_assessments' => $totalAssessments,
409â†’            'completed_assessments' => $completedAssessments,
410â†’            'current_step' => $progress?->current_step ?? 1,
411â†’            'completion_percentage' => $totalAssessments > 0 ? round(($completedAssessments / $totalAssessments) * 100) : 0,
412â†’            'last_activity' => $progress?->last_activity_at,
413â†’        ];
414â†’
415â†’        // Cache the result
416â†’        $this->userCompletionCache[$cacheKey] = $result;
417â†’
418â†’        return $result;
419â†’    }
420â†’
421â†’    /**
422â†’     * Clear cached data for a user (call when assessments are updated).
423â†’     */
424â†’    public function clearUserCache(int $userId): void
425â†’    {
426â†’        $cacheKey = 'completion_stats_' . $userId;
427â†’        unset($this->userCompletionCache[$cacheKey]);
428â†’
429â†’        // Clear template access cache for this user
430â†’        foreach (array_keys($this->templateAccessCache) as $key) {
431â†’            if (str_starts_with($key, "access_{$userId}_")) {
432â†’                unset($this->templateAccessCache[$key]);
433â†’            }
434â†’        }
435â†’    }
436â†’
437â†’    /**
438â†’     * Clear all cached data.
439â†’     */
440â†’    public function clearAllCache(): void
441â†’    {
442â†’        $this->userCompletionCache = [];
443â†’        $this->templateAccessCache = [];
444â†’    }
445â†’}
