import { LucideIcon } from 'lucide-react';
import type { Config } from 'ziggy-js';

export interface Auth {
    user: User;
    isAdmin: boolean;
}

export interface BreadcrumbItem {
    title: string;
    href: string;
}

export interface NavGroup {
    title: string;
    items: NavItem[];
}

export interface NavItem {
    title: string;
    href: string;
    icon?: LucideIcon | null;
    isActive?: boolean;
    feature?: string | null;
}

export interface SharedData {
    name: string;
    quote: { message: string; author: string };
    auth: Auth;
    ziggy: Config & { location: string };
    sidebarOpen: boolean;
    categories: Category[];
    [key: string]: unknown;
}

export interface User {
    id: number;
    name: string;
    email: string;
    avatar?: string;
    email_verified_at: string | null;
    created_at: string;
    updated_at: string;
    [key: string]: unknown; // This allows for additional properties...
}

export interface Category {
    id: number;
    name: string;
    description: string | null;
    position: number;
    created_at: string;
    updated_at: string;
    processes?: Process[]; // If you eager load processes
}

export interface Process {
    id: number;
    name: string;
    category_id: number;
    position: number;
    description: string | null;
    created_at: string;
    updated_at: string;
    diagrams?: Diagram[]; // If you eager load diagrams
}

export interface Diagram {
    id: number;
    name: string;
    description: string | null;
    status: string;
    owner: string | null;
    version: string;
    process_id: number;
    created_at: string;
    updated_at: string;
    activities?: Activity[];
    activities_count?: number;
}

export interface SOP {
    id: number;
    title: string;
    activity_id: number;
    is_archived: boolean;
    created_at: string;
    updated_at: string;

    activity: {
        id: number;
        name: string;
        diagram: {
            id: number;
            name: string;
            process: {
                id: number;
                name: string;
                category?: {
                    id: number;
                    name: string;
                };
            };
        };
    };
    creator: {
        id: number;
        name: string;
    };
    updater?: {
        id: number;
        name: string;
    };
    tags: Array<{
        id: number;
        tag_name: string;
    }>;

    // For frontend compatibility - these come from the current/latest version
    content?: string;
    version_number?: number;
    version_label?: string;
    status?: number;
    change_summary?: string;
    total_versions?: number;

    // Version metadata (for compatibility)
    date_of_last_test?: string;
    tested_by?: number;
    test_notes?: string;
    reviewed_by_worker?: boolean;
    date_of_review?: string;
    reviewer_id?: number;
    review_notes?: string;
    approval_date?: string;
    approved_by?: number;
    approval_notes?: string;
    tester?: {
        id: number;
        name: string;
    };
    reviewer?: {
        id: number;
        name: string;
    };
    approver?: {
        id: number;
        name: string;
    };
}

export interface SOPVersion {
    id: number;
    sop_id: number;
    content: string;
    version_number: number;
    version_label: string;
    status: number;
    change_summary?: string;
    created_at: string;
    updated_at: string;

    // Metadata
    date_of_last_test?: string;
    tested_by?: number;
    test_notes?: string;
    reviewed_by_worker: boolean;
    date_of_review?: string;
    reviewer_id?: number;
    review_notes?: string;
    approval_date?: string;
    approved_by?: number;
    approval_notes?: string;

    creator: {
        id: number;
        name: string;
    };
    updater?: {
        id: number;
        name: string;
    };
    tester?: {
        id: number;
        name: string;
    };
    reviewer?: {
        id: number;
        name: string;
    };
    approver?: {
        id: number;
        name: string;
    };

    sop?: SOP;
}

export interface Activity {
    id: number;
    name: string;
    diagram_id: number;
    position: number;
    current_future: number; // Consider an enum here too (e.g., CurrentFutureStatus)
    responsible: string | null;
    accountable: string | null;
    consulted: string | null;
    informed: string | null;
    process_link: string | null;
    created_at: string;
    updated_at: string;
    sop?: SOP; // Changed to singular - one SOP per activity
    sops?: SOP[]; // Keep for backward compatibility during transition
    diagram?: {
        id: number;
        name: string;
    };
}

export type PageProps<T extends Record<string, unknown> = Record<string, unknown>> = T & {
    auth: Auth;
    ziggy: Config & { location: string };
    categories: Category[];
    enabledFeatures: string[];
    features: Record<string, { canShow: boolean; isEnabled: boolean }>;
    flash?: {
        success?: string;
        error?: string;
        warning?: string;
        info?: string;
    };
};

export interface PaginatedResponse<T> {
    current_page: number;
    data: T[];
    first_page_url: string;
    from: number;
    last_page: number;
    last_page_url: string;
    links: Array<{
        url: string | null;
        label: string;
        active: boolean;
    }>;
    next_page_url: string | null;
    path: string;
    per_page: number;
    prev_page_url: string | null;
    to: number;
    total: number;
}

// Business Process Framework Types
export interface BusinessProcessCategory {
    id: number;
    name: string;
    description: string | null;
    position: number;
    created_at: string;
    updated_at: string;
    processes?: BusinessProcessProcess[];
}

export interface BusinessProcessProcess {
    id: number;
    name: string;
    category_id: number;
    position: number;
    description: string | null;
    owner_id: number | null;
    created_at: string;
    updated_at: string;
    category?: BusinessProcessCategory;
    owner?: User;
    cores?: BusinessProcessCore[];
    cores_count?: number;
}

export interface BusinessProcessCore {
    id: number;
    name: string;
    description: string | null;
    process_id: number;
    owner_id: number | null;
    status: number; // 0=Draft, 1=Active, 2=Archived
    version: string;
    created_at: string;
    updated_at: string;
    process?: BusinessProcessProcess;
    owner?: User;
    activities?: BusinessProcessActivity[];
    activities_count?: number;
    contents?: BusinessProcessContent[];
}

export interface BusinessProcessActivity {
    id: number;
    name: string;
    core_id: number;
    position: number;
    current_future: number; // 0=Current, 1=Future
    responsible: string | null;
    accountable: string | null;
    consulted: string | null;
    informed: string | null;
    process_link: string | null;
    created_at: string;
    updated_at: string;
    core?: BusinessProcessCore;
    blueprint?: BusinessProcessBlueprint;
}

export interface BusinessProcessContent {
    id: number;
    core_id: number;
    type: string; // 'sop', 'visual_diagram', 'swimlane'
    creator_id: number;
    updater_id: number | null;
    created_at: string;
    updated_at: string;
    core?: BusinessProcessCore;
    creator?: User;
    updater?: User;
    versions?: BusinessProcessContentVersion[];
    current_version?: BusinessProcessContentVersion;
}

export interface BusinessProcessContentVersion {
    id: number;
    content_id: number;
    title: string;
    content: string;
    version_number: number;
    version_label: string;
    status: number; // 0=Draft, 1=Active, 2=Archived
    change_summary: string | null;
    creator_id: number;
    updater_id: number | null;
    created_at: string;
    updated_at: string;
    content?: BusinessProcessContent;
    creator?: User;
    updater?: User;
}

export interface BusinessProcessBlueprint {
    id: number;
    activity_id: number;
    title: string;
    content: string;
    status: number; // 0=Draft, 1=Active, 2=Archived
    creator_id: number;
    updater_id: number | null;
    created_at: string;
    updated_at: string;
    activity?: BusinessProcessActivity;
    creator?: User;
    updater?: User;
}

