import AssessmentQuickAccess from '@/components/dashboard/assessment-quick-access';
import BusinessProcessQuickAccess from '@/components/dashboard/business-process-quick-access';
import AppLayout from '@/layouts/app-layout';
import { BreadcrumbItem, PageProps } from '@/types';
import { Head } from '@inertiajs/react';

// Define breadcrumbs for AppLayout
const breadcrumbs: BreadcrumbItem[] = [
    {
        title: 'Dashboard',
        href: route('dashboard'),
    },
];

interface DashboardProps extends PageProps {
    totalProcesses: number;
    totalCategories: number;
    totalDiagrams: number;
}

export default function Dashboard({ totalProcesses, totalCategories, totalDiagrams }: DashboardProps) {
    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title="Dashboard" />
            <div className="py-12">
                <div className="mx-auto max-w-7xl space-y-8 sm:px-6 lg:px-8">
                    <div className="mb-6 flex items-center justify-between">
                        <div>
                            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">Dashboard</h1>
                            <p className="text-gray-500 dark:text-gray-400">Welcome to your business process management system</p>
                        </div>
                    </div>

                    {/* Business Process Quick Access - includes both card and stats */}
                    <BusinessProcessQuickAccess totalProcesses={totalProcesses} totalCategories={totalCategories} totalDiagrams={totalDiagrams} />

                    {/* Assessment Quick Access - includes card and future assessment stats */}
                    <AssessmentQuickAccess />
                </div>
            </div>
        </AppLayout>
    );
}

