import { Question } from '@/hooks/useAssessmentForm';

export interface QuestionProps {
    question: Question;
    value: string;
    onChange: (value: string) => void;
    error?: string;
    disabled?: boolean;
    onAutoSave: () => void;
}

export interface DependentOption {
    label: string;
    value: string;
    children?: DependentOption[];
}

// Question types enum matching backend
export enum QuestionType {
    MCQ = 1,
    NUMBER = 2,
    TEXT = 3,
    TEXTAREA = 4,
    EMAIL = 5,
    SELECT = 6,
    SELECT_DEPENDENT = 7,
}

// Assessment status enum matching backend
export enum AssessmentStatus {
    DRAFT = 1,
    IN_PROGRESS = 2,
    COMPLETED = 3,
}

// New answer format that supports review flags
export interface AnswerData {
    value: string | number | null;
    review_later: boolean;
}

// Review information for assessments
export interface ReviewInfo {
    has_questions_for_review: boolean;
    review_questions_count: number;
}

export interface AssessmentTemplate {
    id: number;
    name: string;
    slug: string;
    description: string;
    estimated_duration: number;
    step_number: number;
    display_step_number?: number;
    is_required_first: boolean;
    // Added for assessment cards
    user_status?: string | null;
    is_completed?: boolean;
    is_unlocked?: boolean;
    completed_at?: string | null;
    review_info?: ReviewInfo;
}

export interface UserAssessment {
    id: number;
    user_id: number;
    assessment_template_id: number;
    status: AssessmentStatus;
    answers: Record<string, string | AnswerData>;
    completed_at: string | null;
    created_at: string;
    updated_at: string;
}

export interface FormConfiguration {
    assessmentSlug: string;
    title: string;
    description: string;
    estimatedDuration: number;
    stepNumber: number;
    totalSteps: number;
    template: AssessmentTemplate;
    questions: Question[];
    dependencies: Record<string, string[]>;
    stats: QuestionStats;
}

export interface QuestionStats {
    total_questions: number;
    required_questions: number;
    optional_questions: number;
    vc_essential_questions: number;
    scoring_questions: number;
    question_types: Record<string, number>;
    scoring_areas: Record<string, number>;
}

export interface NaicsSubsector {
    id: number;
    code: string;
    name: string;
    value: string;
    label: string;
    sector_id: number;
}

