105â†’  // Group questions by main/subquestion pattern
106â†’  const questionGroups = useMemo(() => {
107â†’    const groups: QuestionGroup[] = [];
108â†’    const processedQuestions = new Set<string>();
109â†’
110â†’    questions
111â†’      .sort((a, b) => a.order_index - b.order_index)
112â†’      .forEach(question => {
113â†’        if (processedQuestions.has(question.question_id)) return;
114â†’
115â†’        // Identify main questions (process questions or standalone questions)
116â†’        const isProcessQuestion = question.question_id.includes('_process');
117â†’        const isOwnerQuestion = question.question_id.includes('_owner');
118â†’        const isSubQuestion = question.question_id.includes('_sub');
119â†’
120â†’        // Main questions are process questions OR questions that aren't owner/sub questions
121â†’        const isMainQuestion = isProcessQuestion || (!isOwnerQuestion && !isSubQuestion);
122â†’
123â†’        if (isMainQuestion) {
124â†’          let baseId: string;
125â†’          let subQuestions: Question[] = [];
126â†’
127â†’          if (isProcessQuestion) {
128â†’            // For process questions, extract base ID (e.g., "d2s_q1" from "d2s_q1_process")
129â†’            baseId = question.question_id.replace('_process', '');
130â†’
131â†’            // Find corresponding owner question
132â†’            const ownerQuestionId = baseId + '_owner';
133â†’            const ownerQuestion = questions.find(q => q.question_id === ownerQuestionId);
134â†’            if (ownerQuestion) {
135â†’              subQuestions = [ownerQuestion];
136â†’            }
137â†’          } else {
138â†’            // For standalone questions, use the question_id as base
139â†’            baseId = question.question_id;
140â†’
141â†’            // Look for any related subquestions (like dependent selects)
142â†’            subQuestions = questions.filter(q =>
143â†’              q.question_id.startsWith(baseId + '_') &&
144â†’              q.question_id !== question.question_id &&
145â†’              (q.question_id.includes('_sub') || q.depends_on === question.question_id)
146â†’            );
147â†’          }
148â†’
149â†’          groups.push({
150â†’            mainQuestion: question,
151â†’            subQuestions,
152â†’            groupId: baseId
153â†’          });
154â†’
155â†’          // Mark all questions in this group as processed
156â†’          processedQuestions.add(question.question_id);
157â†’          subQuestions.forEach(subQ => processedQuestions.add(subQ.question_id));
158â†’        }
159â†’      });
160â†’
161â†’    // Add any remaining ungrouped questions as individual groups
162â†’    questions.forEach(question => {
163â†’      if (!processedQuestions.has(question.question_id)) {
