165â†’          mainQuestion: question,
166â†’          subQuestions: [],
167â†’          groupId: question.question_id
168â†’        });
169â†’        processedQuestions.add(question.question_id);
170â†’      }
171â†’    });
172â†’
173â†’    return groups;
