import { useForm, router } from '@inertiajs/react';
import { useCallback, useMemo, useState, useRef, useEffect } from 'react';
import { toast } from 'sonner';
import type { AnswerData } from '@/types/assessment';

// Manual debounce implementation to avoid lodash dependency - DISABLED FOR NOW
// function debounce<T extends (...args: unknown[]) => void>(
//   func: T,
//   wait: number
// ): T & { cancel: () => void } {
//   let timeout: NodeJS.Timeout | null = null;

//   const debounced = ((...args: Parameters<T>) => {
//     if (timeout) clearTimeout(timeout);
//     timeout = setTimeout(() => func(...args), wait);
//   }) as T & { cancel: () => void };

//   debounced.cancel = () => {
//     if (timeout) {
//       clearTimeout(timeout);
//       timeout = null;
//     }
//   };

//   return debounced;
// }

export interface Question {
  id: number;
  question_id: string;
  assessment_template_id: number;
  question_text: string;
  question_type: number;
  value_key: string;
  options: Record<string, unknown> | null;
  scoring_area: string | null;
  required: boolean;
  is_essential_for_vc: boolean;
  help_text: string | null;
  depends_on: string | null;
  order_index: number;
}

export interface QuestionGroup {
  mainQuestion: Question;
  subQuestions: Question[];
  groupId: string;
}

interface UseAssessmentFormProps {
  assessmentSlug: string;
  initialAnswers: Record<string, string | AnswerData>;
  questions: Question[];
  assessmentId?: number;
}

export function useAssessmentForm({
  assessmentSlug,
  initialAnswers,
  questions,
  assessmentId,
}: UseAssessmentFormProps) {
  // Convert initial answers to include review flags
  const convertedInitialAnswers = useMemo(() => {
    const converted: Record<string, string | boolean> = {};

    // Process initial answers and extract review flags
    Object.entries(initialAnswers).forEach(([key, value]) => {
      if (typeof value === 'object' && value !== null && 'value' in value) {
        // New format with review flags
        const answerData = value as AnswerData;
        converted[key] = answerData.value?.toString() || '';
        converted[`${key}_review_later`] = answerData.review_later;
      } else {
        // Old format - just the value
        converted[key] = value?.toString() || '';
        converted[`${key}_review_later`] = false;
      }
    });

    return converted;
  }, [initialAnswers]);

  // Initialize Inertia form with converted answers
  const { data, setData, put, processing, errors, isDirty, hasErrors, wasSuccessful, recentlySuccessful } = useForm(convertedInitialAnswers);

  // Use ref to always access the latest data for auto-save
  const dataRef = useRef(data);
  useEffect(() => {
    dataRef.current = data;
  }, [data]);

  // Debounce timer ref to prevent rapid auto-save calls
  const debounceTimerRef = useRef<NodeJS.Timeout | null>(null);

  // Track whether user has attempted to submit (for showing validation errors)
  const [hasAttemptedSubmit, setHasAttemptedSubmit] = useState(false);

  // Track submission state for manual visits
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Auto-save status for visual indicator
  const [autoSaveStatus, setAutoSaveStatus] = useState<'idle' | 'saving' | 'saved' | 'error'>('idle');

  // Group questions by main/subquestion pattern
  const questionGroups = useMemo(() => {
    const groups: QuestionGroup[] = [];
    const processedQuestions = new Set<string>();

    questions
      .sort((a, b) => a.order_index - b.order_index)
      .forEach(question => {
        if (processedQuestions.has(question.question_id)) return;

        // Identify main questions (process questions or standalone questions)
        const isProcessQuestion = question.question_id.includes('_process');
        const isOwnerQuestion = question.question_id.includes('_owner');
        const isSubQuestion = question.question_id.includes('_sub');

        // Main questions are process questions OR questions that aren't owner/sub questions
        const isMainQuestion = isProcessQuestion || (!isOwnerQuestion && !isSubQuestion);

        if (isMainQuestion) {
          let baseId: string;
          let subQuestions: Question[] = [];

          if (isProcessQuestion) {
            // For process questions, extract base ID (e.g., "d2s_q1" from "d2s_q1_process")
            baseId = question.question_id.replace('_process', '');

            // Find corresponding owner question
            const ownerQuestionId = baseId + '_owner';
            const ownerQuestion = questions.find(q => q.question_id === ownerQuestionId);
            if (ownerQuestion) {
              subQuestions = [ownerQuestion];
            }
          } else {
            // For standalone questions, use the question_id as base
            baseId = question.question_id;

            // Look for any related subquestions (like dependent selects)
            subQuestions = questions.filter(q =>
              q.question_id.startsWith(baseId + '_') &&
              q.question_id !== question.question_id &&
              (q.question_id.includes('_sub') || q.depends_on === question.question_id)
            );
          }

          groups.push({
            mainQuestion: question,
            subQuestions,
            groupId: baseId
          });

          // Mark all questions in this group as processed
          processedQuestions.add(question.question_id);
          subQuestions.forEach(subQ => processedQuestions.add(subQ.question_id));
        }
      });

    // Add any remaining ungrouped questions as individual groups
    questions.forEach(question => {
      if (!processedQuestions.has(question.question_id)) {
        groups.push({
          mainQuestion: question,
          subQuestions: [],
          groupId: question.question_id
        });
        processedQuestions.add(question.question_id);
      }
    });

    return groups;
  }, [questions]);

  // Update answer for a specific question
  const updateAnswer = useCallback((questionId: string, value: string) => {
    // Find the question to get its value_key
    const question = questions.find(q => q.question_id === questionId);
    const key = question?.value_key || questionId;

    // Use the functional form of setData to preserve existing values
    setData((prevData: Record<string, string | boolean>) => {
      const newData = {
        ...prevData,
        [key]: value
      };
      return newData;
    });
  }, [setData, questions]);

  // Update review flag for a specific question
  const updateReviewFlag = useCallback((questionId: string, reviewLater: boolean) => {
    const question = questions.find(q => q.question_id === questionId);
    const key = question?.value_key || questionId;
    const reviewKey = `${key}_review_later`;

    setData((prevData: Record<string, string | boolean>) => ({
      ...prevData,
      [reviewKey]: reviewLater
    }));
  }, [setData, questions]);

  // Auto-save functionality with debouncing - DISABLED FOR NOW
  // const debouncedAutoSave = useMemo(
  //   () => debounce(() => {
  //     if (Object.keys(data).length > 0) {
  //       // Default auto-save via PUT request - matches pattern from password.tsx
  //       put(route('assessments.form.update', { slug: assessmentSlug }), {
  //         preserveScroll: true,
  //         preserveState: true,
  //         only: ['errors'], // Only reload errors on auto-save
  //       });
  //     }
  //   }, 1000),
  //   [assessmentSlug, put, data]
  // );

  // Trigger auto-save when answers change - DISABLED FOR NOW
  // useEffect(() => {
  //   if (Object.keys(data).length > 0) {
  //     debouncedAutoSave();
  //   }

  //   return () => {
  //     debouncedAutoSave.cancel();
  //   };
  // }, [data, debouncedAutoSave]);

  // Calculate form progress
  const progress = useMemo(() => {
    const totalQuestions = questions.filter(q => q.required).length;
    const answeredQuestions = questions.filter(q => {
      const key = q.value_key || q.question_id;
      return q.required && data[key] !== undefined && data[key] !== '';
    }).length;

    return {
      percentage: totalQuestions > 0 ? Math.round((answeredQuestions / totalQuestions) * 100) : 0,
      completed: answeredQuestions,
      total: totalQuestions,
      isComplete: answeredQuestions === totalQuestions
    };
  }, [questions, data]);

  // Calculate validation errors for all questions - only show after submit attempt
  const validationErrors = useMemo(() => {
    // Don't show validation errors until user has attempted to submit
    if (!hasAttemptedSubmit) {
      return {};
    }

    const errors: Record<string, string> = {};

    questions.forEach(question => {
      const key = question.value_key || question.question_id;
      const value = data[key];

      // Skip if this is not a string value (e.g., review flags)
      if (typeof value !== 'string') {
        return;
      }

      // Check required fields - only show error if field is empty
      if (question.required && (value === undefined || value === '')) {
        errors[key] = 'This field is required';
        return; // Don't check other validations if required field is empty
      }

      // Additional validation based on question type - only if field has value
      if (value && value !== '') {
        switch (question.question_type) {
          case 5: { // EMAIL type
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            if (!emailRegex.test(value)) {
              errors[key] = 'Please enter a valid email address';
            }
            break;
          }
          case 2: { // NUMBER type
            if (isNaN(Number(value))) {
              errors[key] = 'Please enter a valid number';
            }
            break;
          }
          // TEXT (3) and TEXTAREA (4) don't need additional validation beyond required
        }
      }
    });

    return errors;
  }, [questions, data, hasAttemptedSubmit]);

  // Check if form has validation errors
  const hasValidationErrors = useMemo(() => {
    return Object.keys(validationErrors).length > 0;
  }, [validationErrors]);

  // Validate specific question
  const validateQuestion = useCallback((questionId: string) => {
    const question = questions.find(q => q.question_id === questionId);
    if (!question) return true;

    const key = question.value_key || questionId;
    return !validationErrors[key];
  }, [questions, validationErrors]);

  // Submit the complete assessment using Inertia.js manual visits
  const submitAssessment = useCallback(() => {
    // Prevent double submission
    if (isSubmitting) return;

    // Check validation errors directly (independent of hasAttemptedSubmit state)
    const currentErrors: Record<string, string> = {};

    questions.forEach(question => {
      const key = question.value_key || question.question_id;
      const value = data[key];

      // Skip if this is not a string value (e.g., review flags)
      if (typeof value !== 'string') {
        return;
      }

      // Check required fields
      if (question.required && (value === undefined || value === '')) {
        currentErrors[key] = 'This field is required';
        return;
      }

      // Additional validation based on question type - only if field has value
      if (value && value !== '') {
        switch (question.question_type) {
          case 5: { // EMAIL type
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            if (!emailRegex.test(value)) {
              currentErrors[key] = 'Please enter a valid email address';
            }
            break;
          }
          case 2: { // NUMBER type
            if (isNaN(Number(value))) {
              currentErrors[key] = 'Please enter a valid number';
            }
            break;
          }
        }
      }
    });

    // If there are validation errors, show toast and mark as attempted
    if (Object.keys(currentErrors).length > 0) {
      setHasAttemptedSubmit(true);
      toast.error('Please complete all required fields before submitting the assessment.');
      return;
    }

    // No validation errors, proceed with submission using Inertia.js manual visits
    setIsSubmitting(true);

    const submissionData = {
      answers: data, // Backend expects answers as an object/array
    };

    // Use router.visit() for manual form submission
    router.visit(route('assessments.complete', { id: assessmentId }), {
      method: 'post',
      data: submissionData,
      onSuccess: () => {
        toast.success('Assessment submitted successfully!');
        setIsSubmitting(false);
        // Backend will handle redirect via to_route()
      },
      onError: (errors) => {
        toast.error('Failed to submit assessment. Please try again.');
        console.error('Submission errors:', errors);
        setIsSubmitting(false);
      },
      onFinish: () => {
        setIsSubmitting(false);
      }
    });
  }, [questions, data, isSubmitting, assessmentId]);

      // Get answer value for a specific question
  const getAnswer = useCallback((questionId: string) => {
    const question = questions.find(q => q.question_id === questionId);
    const key = question?.value_key || questionId;
    const value = data[key];

    // Handle new answer format {value, review_later}
    if (typeof value === 'object' && value !== null && 'value' in value) {
      return String((value as AnswerData).value || '');
    }

    // Handle legacy string format
    return typeof value === 'string' ? value : '';
  }, [data, questions]);

  // Get review flag for a specific question
  const getReviewFlag = useCallback((questionId: string): boolean => {
    const question = questions.find(q => q.question_id === questionId);
    const key = question?.value_key || questionId;
    const reviewKey = `${key}_review_later`;
    return Boolean(data[reviewKey]);
  }, [data, questions]);

    // Save progress without completing assessment
  const saveProgress = useCallback(() => {
    // Prevent double submission
    if (processing) return;

    // Clear existing debounce timer
    if (debounceTimerRef.current) {
      clearTimeout(debounceTimerRef.current);
    }

    // Set status to saving immediately
    setAutoSaveStatus('saving');

    // Debounce auto-save to prevent rapid-fire saves
    debounceTimerRef.current = setTimeout(() => {
      // Get the most current data using ref to avoid stale closures
      const currentData = dataRef.current;
      console.log('ðŸ’¾ Auto-save triggered with current data:', currentData);

      // Use router.put instead of form put to avoid form state binding issues
      router.put(route('assessments.form.update', { slug: assessmentSlug }), currentData, {
        preserveScroll: true,
        preserveState: true,
        onStart: () => {
          console.log('ðŸ”„ Save started with data being sent:', currentData);
        },
        onSuccess: () => {
          console.log('âœ… Auto-save successful');
          setAutoSaveStatus('saved');
          // Hide the saved indicator after 2 seconds
          setTimeout(() => setAutoSaveStatus('idle'), 2000);
        },
        onError: (errors) => {
          console.log('âŒ Save failed with errors:', errors);
          setAutoSaveStatus('error');
          // Hide the error indicator after 5 seconds
          setTimeout(() => setAutoSaveStatus('idle'), 5000);
          console.error('Save progress errors:', errors);
        },
      });
    }, 500); // 500ms debounce delay
  }, [assessmentSlug, processing]);

  // Cleanup debounce timer on unmount
  useEffect(() => {
    return () => {
      if (debounceTimerRef.current) {
        clearTimeout(debounceTimerRef.current);
      }
    };
  }, []);

  // Check if a question has errors (combine backend errors with validation errors)
  const getQuestionError = useCallback((questionId: string) => {
    const question = questions.find(q => q.question_id === questionId);
    const key = question?.value_key || questionId;
    return errors[key] || validationErrors[key];
  }, [errors, questions, validationErrors]);

  return {
    // Form state
    data,
    setData,
    put,
    processing: processing || isSubmitting, // Combine both processing states
    errors,

    // Question management
    questions,
    questionGroups,

    // Form actions
    updateAnswer,
    updateReviewFlag,
    getAnswer,
    getReviewFlag,
    submitAssessment,
    saveProgress,

    // Validation
    validateQuestion,
    getQuestionError,
    validationErrors,
    hasValidationErrors,

    // Progress tracking
    progress,

    // Auto-save status
    autoSaveStatus,

    // Form state helpers
    isDirty,
    hasErrors,
    wasSuccessful,
    recentlySuccessful,

    // Submission attempt tracking
    hasAttemptedSubmit,
    setHasAttemptedSubmit,
  };
}

