import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { RichTextEditor, RichTextEditorRef } from '@/components/ui/rich-text-editor';
import { BusinessProcessCore } from '@/types';
import { router } from '@inertiajs/react';
import { FileText, Save } from 'lucide-react';
import { useRef, useState } from 'react';
import { toast } from 'sonner';

interface SOPTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
    };
    content?: {
        id: number;
        title: string;
        content: string;
        version_number: number;
        version_label: string;
        status: number;
    };
    errors?: Record<string, string>;
}

const getStatusString = (status: number): string => {
    switch (status) {
        case 0:
            return 'Draft';
        case 1:
            return 'Active';
        case 2:
            return 'Archived';
        default:
            return 'Unknown';
    }
};

const getStatusBadgeVariant = (status: number) => {
    switch (status) {
        case 0:
            return 'outline';
        case 1:
            return 'default';
        case 2:
            return 'secondary';
        default:
            return 'outline';
    }
};

export function SOPTabContent({ core, content, errors }: SOPTabContentProps) {
    const editorRef = useRef<RichTextEditorRef>(null);
    const [isSaving, setIsSaving] = useState(false);
    const [editorContent, setEditorContent] = useState(content?.content || '');

    const handleSave = () => {
        setIsSaving(true);

        const payload = {
            core_id: core.id,
            content_type: 'sop',
            title: `${core.name} - SOP`,
            content: editorContent,
            version_label: content ? String(Number(content.version_number) + 1) : '1.0',
        };

        if (content) {
            // Update existing content
            router.put(route('business-processes.contents.update', [core.process.id, content.id]), payload, {
                onSuccess: () => {
                    toast.success('SOP saved successfully');
                    router.reload();
                },
                onError: () => {
                    toast.error('Failed to save SOP');
                },
                onFinish: () => {
                    setIsSaving(false);
                },
            });
        } else {
            // Create new content
            router.post(route('business-processes.contents.store', core.process.id), payload, {
                onSuccess: () => {
                    toast.success('SOP saved successfully');
                    router.reload();
                },
                onError: () => {
                    toast.error('Failed to save SOP');
                },
                onFinish: () => {
                    setIsSaving(false);
                },
            });
        }
    };

    const handleAutoSave = () => {
        // Auto-save functionality can be implemented here
        console.log('Auto-saving SOP content...');
    };

    return (
        <div className="space-y-6">
            {/* Header */}
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-3">
                    <FileText className="h-5 w-5 text-gray-500" />
                    <div>
                        <h3 className="text-lg font-semibold text-gray-900">Standard Operating Procedure</h3>
                        <p className="text-sm text-gray-500">Document the step-by-step procedures for this core process</p>
                    </div>
                </div>
                <div className="flex items-center gap-2">
                    {content && <Badge variant={getStatusBadgeVariant(content.status)}>{getStatusString(content.status)}</Badge>}
                    <Button onClick={handleSave} disabled={isSaving} size="sm">
                        <Save className="mr-2 h-4 w-4" />
                        {isSaving ? 'Saving...' : 'Save SOP'}
                    </Button>
                </div>
            </div>

            {/* Version Info */}
            {content && (
                <Card>
                    <CardHeader className="pb-3">
                        <CardTitle className="text-sm font-medium">Version Information</CardTitle>
                    </CardHeader>
                    <CardContent className="pt-0">
                        <div className="grid grid-cols-1 gap-4 text-sm md:grid-cols-3">
                            <div>
                                <span className="font-medium text-gray-500">Version:</span>
                                <p className="text-gray-900">
                                    {content.version_label} (v{content.version_number})
                                </p>
                            </div>
                            <div>
                                <span className="font-medium text-gray-500">Status:</span>
                                <p className="text-gray-900">{getStatusString(content.status)}</p>
                            </div>
                            <div>
                                <span className="font-medium text-gray-500">Title:</span>
                                <p className="text-gray-900">{content.title}</p>
                            </div>
                        </div>
                    </CardContent>
                </Card>
            )}

            {/* Editor */}
            <Card>
                <CardContent className="p-0">
                    <RichTextEditor
                        ref={editorRef}
                        content={editorContent}
                        onChange={setEditorContent}
                        placeholder="Start documenting your standard operating procedure..."
                        className="min-h-[500px]"
                        onAutoSave={handleAutoSave}
                    />
                </CardContent>
            </Card>

            {/* Errors */}
            {errors && Object.keys(errors).length > 0 && (
                <Card className="border-red-200 bg-red-50">
                    <CardContent className="p-4">
                        <div className="text-sm text-red-600">
                            {Object.entries(errors).map(([key, message]) => (
                                <p key={key} className="mb-1">
                                    <strong>{key}:</strong> {message}
                                </p>
                            ))}
                        </div>
                    </CardContent>
                </Card>
            )}
        </div>
    );
}

