public function test_user_can_only_view_own_assessments()
{
    $user = User::factory()->create();
    $otherUser = User::factory()->create();
    $assessment = UserAssessment::factory()->create(['user_id' => $otherUser->id]);

    $this->assertFalse($user->can('view', $assessment));
}
```

