import { Activity, Diagram, PaginatedResponse, Process, User } from '@/types';
import { DiagramInformationCard, EditableDiagramData } from './diagram-information-card';
import { ProcessActivitiesCard } from './process-activities-card';

interface InfoTabContentProps {
    diagram: Diagram & {
        user: Pick<User, 'name'>;
        process: Pick<Process, 'id' | 'name'>; // Ensure process is part of the Diagram type
    };
    activities: PaginatedResponse<Activity>;
    onSaveDiagramInfo: (dataToSave: EditableDiagramData) => Promise<void>;
    isSavingDiagramInfo: boolean;
    diagramUpdateErrors: Record<string, string>;
}

export function InfoTabContent({ diagram, activities, onSaveDiagramInfo, isSavingDiagramInfo, diagramUpdateErrors }: InfoTabContentProps) {
    return (
        <div className="grid grid-cols-1 gap-6 lg:grid-cols-3">
            <div className="space-y-6 lg:col-span-3">
                <DiagramInformationCard
                    diagram={diagram} // Pass the full diagram object including user
                    onSave={onSaveDiagramInfo}
                    isSaving={isSavingDiagramInfo}
                    errors={diagramUpdateErrors}
                />
                {/* Pass paginated activities to ProcessActivitiesCard */}
                <ProcessActivitiesCard diagram={diagram} process={diagram.process} activities={activities} />
            </div>
        </div>
    );
}

import MermaidRenderer from '@/components/mermaid-renderer';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Diagram } from '@/types';
import { Waypoints, Eye, Info } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';

interface VisualizationTabContentProps {
    diagram: Pick<Diagram, 'id'>;
    generatedMermaidCode: string;
}

export function VisualizationTabContent({ diagram, generatedMermaidCode }: VisualizationTabContentProps) {
    return (
        <Card>
            <CardHeader>
                <div className="flex items-center">
                    <Waypoints className="mr-2 h-5 w-5" />
                    <CardTitle>Process Visualization</CardTitle>
                </div>
                <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                    View your process diagram generated from activities. Use "Import from HTML" to add new activities.
                </p>
            </CardHeader>
            <CardContent className="space-y-6">
                <Alert>
                    <Info className="h-4 w-4" />
                    <AlertDescription>
                        This visualization is automatically generated from your activities. To modify the diagram, add or edit activities in the Info
                        tab, or use the "Import from HTML" feature.
                    </AlertDescription>
                </Alert>

                {/* Diagram Preview Section */}
                <div className="space-y-3">
                    <div className="flex items-center gap-2">
                        <Eye className="h-4 w-4 text-gray-600" />
                        <h3 className="text-sm font-medium text-gray-900 dark:text-gray-100">Process Flow Diagram</h3>
                    </div>
                    <div className="min-h-[400px] overflow-auto rounded-lg border border-gray-200 bg-white p-6 dark:border-gray-700 dark:bg-gray-950">
                        {generatedMermaidCode.trim() ? (
                            <MermaidRenderer mermaidCode={generatedMermaidCode} diagramId={`diagram-preview-${diagram.id}`} />
                        ) : (
                            <div className="flex h-[350px] flex-col items-center justify-center text-center">
                                <Waypoints className="h-16 w-16 text-gray-300 dark:text-gray-600" />
                                <p className="mt-4 text-lg font-medium text-gray-600 dark:text-gray-400">No Activities Yet</p>
                                <p className="mt-1 text-sm text-gray-500 dark:text-gray-500">
                                    Add activities in the Info tab or import from HTML to see your process diagram
                                </p>
                            </div>
                        )}
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

