import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import RichTextEditor, { RichTextEditorRef } from '@/components/ui/rich-text-editor';
import AppLayout from '@/layouts/app-layout';
import { Head, Link, useForm, router } from '@inertiajs/react';
import { CheckCircle, User, Save, GitCommit, AlertTriangle, Settings, ChevronLeft, ChevronRight } from 'lucide-react';
import { BreadcrumbItem, SOP, SOPVersion } from '@/types';
import { useState, useEffect, useRef } from 'react';
import React from 'react';

interface Props {
    sop: SOP;
    otherVersions: SOPVersion[];
    currentVersion: SOPVersion;
}

interface VersionCardProps {
    version: SOPVersion;
    isCurrentVersion: boolean;
    isViewingThisVersion: boolean;
    sopId: number;
    onStatusChange: (version: SOPVersion) => void;
    getStatusBadge: (status: number) => React.JSX.Element;
    SafeLink: React.ComponentType<{ href: string; className?: string; children: React.ReactNode }>;
}

// Centralized color system for version cards
const getVersionCardColors = (isViewingThisVersion: boolean, isCurrentVersion: boolean, isFutureVersion: boolean) => {
    if (isViewingThisVersion) {
        return {
            container: 'border-blue-200 bg-blue-50 ring-1 ring-blue-200',
            dot: 'bg-blue-600',
            title: 'text-blue-900',
            subtitle: 'text-blue-700',
            content: 'text-blue-600',
            button: 'border-blue-300 text-blue-700 hover:bg-blue-50',
        };
    }

    if (isCurrentVersion) {
        return {
            container: 'border-green-200 bg-green-50',
            dot: 'bg-green-600',
            title: 'text-green-900',
            subtitle: 'text-green-700',
            content: 'text-green-600',
            button: 'border-gray-200 text-green-700 hover:bg-gray-50',
        };
    }

    if (isFutureVersion) {
        return {
            container: 'border-purple-200 bg-purple-50',
            dot: 'bg-purple-600',
            title: 'text-purple-900',
            subtitle: 'text-purple-700',
            content: 'text-purple-600',
            button: 'border-gray-200 text-purple-700 hover:bg-gray-50',
        };
    }

    // WIP (default)
    return {
        container: 'border-gray-200 bg-white hover:bg-gray-50',
        dot: 'bg-gray-400',
        title: 'text-gray-900',
        subtitle: 'text-gray-500',
        content: 'text-gray-600',
        button: 'border-gray-300 text-gray-600 hover:bg-gray-50',
    };
};

const VersionCard = ({ version, isCurrentVersion, isViewingThisVersion, sopId, onStatusChange, getStatusBadge, SafeLink }: VersionCardProps) => {
    const isFutureVersion = version.status === 2;
    const colors = getVersionCardColors(isViewingThisVersion, isCurrentVersion, isFutureVersion);

    return (
        <div className={`rounded-lg border p-3 ${colors.container}`}>
            <SafeLink href={route('sops.versions.show', [sopId, version.id])} className="block">
                <div className="flex flex-col space-y-2 sm:flex-row sm:items-center sm:justify-between sm:space-y-0">
                    <div className="flex items-center space-x-2">
                        <div className={`h-2 w-2 rounded-full ${colors.dot}`}></div>
                        <span className={`font-medium ${colors.title}`}>Version {version.version_number}</span>
                    </div>
                    {getStatusBadge(version.status)}
                </div>
                <p className={`mt-1 text-xs ${colors.subtitle}`}>{version.created_at}</p>
                <p className={`mt-1 text-xs ${colors.subtitle}`}>{version.creator.name}</p>
                {isCurrentVersion && (
                    <div className="mt-2 flex items-center space-x-1">
                        <div className="h-1 w-1 rounded-full bg-green-500"></div>
                        <span className="text-xs text-green-700">current</span>
                    </div>
                )}
                {version.change_summary && <p className={`mt-1 line-clamp-2 text-xs ${colors.content}`}>{version.change_summary}</p>}

