import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { RichTextEditor, RichTextEditorRef } from '@/components/ui/rich-text-editor';
import { BusinessProcessCore } from '@/types';
import { router, useForm } from '@inertiajs/react';
import { CheckCircle, User, Save, GitCommit, AlertTriangle, Settings, ChevronLeft, ChevronRight, FileText } from 'lucide-react';
import { useState, useEffect, useRef } from 'react';
import { toast } from 'sonner';
import React from 'react';

interface ContentVersion {
    id: number;
    content: string;
    title: string;
    version_number: number;
    version_label: string;
    status: number;
    change_summary?: string;
    created_at: string;
    updated_at: string;
    creator: {
        id: number;
        name: string;
    };
}

interface SOPTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
    };
    content?: ContentVersion;
    versions?: ContentVersion[];
    errors?: Record<string, string>;
}

interface VersionCardProps {
    version: ContentVersion;
    isCurrentVersion: boolean;
    isViewingThisVersion: boolean;
    coreId: string;
    onStatusChange: (version: ContentVersion) => void;
    getStatusBadge: (status: number) => React.JSX.Element;
}

// Centralized color system for version cards
const getVersionCardColors = (isViewingThisVersion: boolean, isCurrentVersion: boolean, isFutureVersion: boolean) => {
    if (isViewingThisVersion) {
        return {
            container: 'border-blue-200 bg-blue-50 ring-1 ring-blue-200',
            dot: 'bg-blue-600',
            title: 'text-blue-900',
            subtitle: 'text-blue-700',
            content: 'text-blue-600',
            button: 'border-blue-300 text-blue-700 hover:bg-blue-50',
        };
    }

    if (isCurrentVersion) {
        return {
            container: 'border-green-200 bg-green-50',
            dot: 'bg-green-600',
            title: 'text-green-900',
            subtitle: 'text-green-700',
            content: 'text-green-600',
            button: 'border-gray-200 text-green-700 hover:bg-gray-50',
        };
    }

    if (isFutureVersion) {
        return {
            container: 'border-purple-200 bg-purple-50',
            dot: 'bg-purple-600',
            title: 'text-purple-900',
            subtitle: 'text-purple-700',
            content: 'text-purple-600',
            button: 'border-gray-200 text-purple-700 hover:bg-gray-50',
        };
    }

    // WIP (default)
    return {
        container: 'border-gray-200 bg-white hover:bg-gray-50',
        dot: 'bg-gray-400',
        title: 'text-gray-900',
        subtitle: 'text-gray-500',
        content: 'text-gray-600',
        button: 'border-gray-300 text-gray-600 hover:bg-gray-50',
    };
};

const VersionCard = ({ version, isCurrentVersion, isViewingThisVersion, coreId, onStatusChange, getStatusBadge }: VersionCardProps) => {
    const isFutureVersion = version.status === 2;
    const colors = getVersionCardColors(isViewingThisVersion, isCurrentVersion, isFutureVersion);

    const handleVersionClick = () => {
        router.reload({
            data: { version_id: version.id },
            preserveState: true,
        });
    };

    return (
        <div className={`rounded-lg border p-3 cursor-pointer ${colors.container}`} onClick={handleVersionClick}>
            <div className="flex flex-col space-y-2 sm:flex-row sm:items-center sm:justify-between sm:space-y-0">
                <div className="flex items-center space-x-2">
                    <div className={`h-2 w-2 rounded-full ${colors.dot}`}></div>
                    <span className={`font-medium ${colors.title}`}>Version {version.version_number}</span>
                </div>
                {getStatusBadge(version.status)}
            </div>
            <p className={`mt-1 text-xs ${colors.subtitle}`}>{version.created_at}</p>
            <p className={`mt-1 text-xs ${colors.subtitle}`}>{version.creator.name}</p>
            {isCurrentVersion && (
                <div className="mt-2 flex items-center space-x-1">
                    <div className="h-1 w-1 rounded-full bg-green-500"></div>
                    <span className="text-xs text-green-700">current</span>
                </div>
            )}
            {version.change_summary && <p className={`mt-1 line-clamp-2 text-xs ${colors.content}`}>{version.change_summary}</p>}
        </div>
    );
};

const getStatusString = (status: number): string => {
    switch (status) {
        case 0:
            return 'Draft';
        case 1:
            return 'Active';
        case 2:
            return 'Archived';
        default:
            return 'Unknown';
    }
};

const getStatusBadge = (status: number) => {
    const statusConfig = {
        0: { label: 'WIP', variant: 'secondary' as const, className: 'bg-orange-100 text-orange-800' },
        1: { label: 'Current', variant: 'default' as const, className: 'bg-green-100 text-green-800' },
        2: { label: 'Future', variant: 'outline' as const, className: 'bg-blue-100 text-blue-800' },
    };

    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig[0];

    return (
        <Badge variant={config.variant} className={config.className}>
            {config.label}
        </Badge>
    );
};

const formatDate = (dateString: string | undefined | null): string => {
    if (!dateString) return 'N/A';
    try {
        return new Date(dateString).toLocaleDateString();
    } catch {
        return dateString;
    }
};

export function SOPTabContent({ core, content, versions = [], errors }: SOPTabContentProps) {
    const editorRef = useRef<RichTextEditorRef>(null);
    const commitSummaryRef = useRef<HTMLTextAreaElement>(null);

    // State management
    const [editorContent, setEditorContent] = useState(content?.content || '');
    const [showMetadataSidebar, setShowMetadataSidebar] = useState(false);
    const [showCommitDialog, setShowCommitDialog] = useState(false);
    const [showUnsavedChangesDialog, setShowUnsavedChangesDialog] = useState(false);
    const [showStatusDialog, setShowStatusDialog] = useState(false);
    const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
    const [pendingNavigation, setPendingNavigation] = useState<string | null>(null);
    const [isCommitValid, setIsCommitValid] = useState(false);
    const [selectedVersionForStatus, setSelectedVersionForStatus] = useState<ContentVersion | null>(null);

    // Form management
    const { data: mainFormData, setData: setMainFormData, processing: mainFormProcessing, reset: resetMainForm } = useForm({
        content: editorContent,
        title: content?.title || `${core.name} - SOP`,
    });

    const { data: statusData, setData: setStatusData, processing: statusProcessing, reset: resetStatusData } = useForm({
        status: 0,
    });

    // Get current and viewing versions
    const currentVersion = content || versions.find(v => v.status === 1) || versions[0];
    const viewingVersion = content || currentVersion;
    const allVersions = versions.length > 0 ? versions : (content ? [content] : []);

    // Effect to track unsaved changes
    useEffect(() => {
        const hasChanges = editorContent !== (content?.content || '');
        setHasUnsavedChanges(hasChanges);
    }, [editorContent, content?.content]);

    // Event handlers
    const toggleMetadataSidebar = () => {
        setShowMetadataSidebar(!showMetadataSidebar);
    };

    const handleSave = () => {
        const payload = {
            core_id: core.id,
            content_type: 'sop',
            title: mainFormData.title,
            content: editorContent,
            version_label: content ? String(Number(content.version_number) + 1) : '1.0',
        };

        if (content) {
            // Update existing content
            router.put(route('business-processes.contents.update', [core.process.id, content.id]), payload, {
                onSuccess: () => {
                    toast.success('SOP saved successfully');
                    setHasUnsavedChanges(false);
                    router.reload();
                },
                onError: () => {
                    toast.error('Failed to save SOP');
                },
            });
        } else {
            // Create new content
            router.post(route('business-processes.contents.store', core.process.id), payload, {
                onSuccess: () => {
                    toast.success('SOP saved successfully');
                    setHasUnsavedChanges(false);
                    router.reload();
                },
                onError: () => {
                    toast.error('Failed to save SOP');
                },
            });
        }
    };

    const handleCommit = () => {
        if (!hasUnsavedChanges) {
            toast.info('No changes to commit');
            return;
        }
        setShowCommitDialog(true);
    };

    const handleCommitCancel = () => {
        setShowCommitDialog(false);
        setIsCommitValid(false);
    };

    const handleCommitConfirm = () => {
        const changeSummary = commitSummaryRef.current?.value?.trim() || '';

        const payload = {
            core_id: core.id,
            content_type: 'sop',
            title: mainFormData.title,
            content: editorContent,
            change_summary: changeSummary,
            version_label: String(Math.max(...allVersions.map(v => v.version_number), 0) + 1),
        };

        router.post(route('business-processes.contents.store', core.process.id), payload, {
            onSuccess: () => {
                toast.success('New version created successfully');
                setShowCommitDialog(false);
                setHasUnsavedChanges(false);
                setPendingNavigation(null);

                // Clear the commit textarea
                if (commitSummaryRef.current) {
                    commitSummaryRef.current.value = '';
                }
                setIsCommitValid(false);
                router.reload();
            },
            onError: () => {
                toast.error('Failed to create new version');
            },
        });
    };

    const handleDiscardChanges = () => {
        setEditorContent(content?.content || '');
        setShowUnsavedChangesDialog(false);
        setHasUnsavedChanges(false);
        setPendingNavigation(null);

        // Navigate to pending URL if there was one
        if (pendingNavigation) {
            router.visit(pendingNavigation);
        }
    };

    const handleSaveFromDialog = () => {
        setShowUnsavedChangesDialog(false);
        handleSave();

        // Navigate to pending URL after saving
        if (pendingNavigation) {
            router.visit(pendingNavigation);
        }
    };

    // Status change handlers
    const handleStatusChange = (version: ContentVersion) => {
        setSelectedVersionForStatus(version);
        setStatusData('status', version.status);
        setShowStatusDialog(true);
    };

    const handleStatusUpdate = () => {
        if (!selectedVersionForStatus) return;

        router.put(
            route('business-processes.contents.update-status', [core.process.id, selectedVersionForStatus.id]),
            {
                status: statusData.status,
            },
            {
                onSuccess: () => {
                    toast.success('Status updated successfully');
                    setShowStatusDialog(false);
                    setSelectedVersionForStatus(null);
                    resetStatusData();
                    router.reload();
                },
                onError: () => {
                    toast.error('Failed to update status');
                },
            },
        );
    };

    const getStatusOptions = () => [
        { value: 0, label: 'Work in Progress' },
        { value: 1, label: 'Current' },
        { value: 2, label: 'Future' },
    ];

    return (
        <div className="flex h-[calc(100vh-200px)] overflow-hidden">
            {/* Version History Sidebar */}
            <div className="w-80 flex-shrink-0 border-r border-gray-200 bg-gray-50">
                <div className="p-4">
                    <h3 className="text-lg font-semibold text-gray-900 mb-4">Version History</h3>
                    <div className="space-y-3">
                        {allVersions.map((version) => (
                            <VersionCard
                                key={version.id}
                                version={version}
                                isCurrentVersion={version.status === 1}
                                isViewingThisVersion={version.id === viewingVersion?.id}
                                coreId={core.id}
                                onStatusChange={handleStatusChange}
                                getStatusBadge={getStatusBadge}
                            />
                        ))}
                        {allVersions.length === 0 && (
                            <div className="text-center text-gray-500 py-8">
                                <FileText className="mx-auto h-12 w-12 text-gray-300 mb-4" />
                                <p>No versions yet</p>
                                <p className="text-sm">Create your first SOP version</p>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            {/* Main Content Area */}
            <div className="flex flex-1 flex-col overflow-hidden">
                {/* Header */}
                <div className="flex-shrink-0 border-b border-gray-200 bg-white px-6 py-4">
                    <div className="flex items-center justify-between">
                        <div className="flex items-center gap-3">
                            <FileText className="h-5 w-5 text-gray-500" />
                            <div>
                                <h3 className="text-lg font-semibold text-gray-900">Standard Operating Procedure</h3>
                                <p className="text-sm text-gray-500">
                                    {viewingVersion ? `Version ${viewingVersion.version_number}` : 'New Version'} • {core.name}
                                </p>
                            </div>
                        </div>
                        <div className="flex items-center gap-2">
                            {viewingVersion && <Badge variant={getStatusBadge(viewingVersion.status).props.variant}>{getStatusString(viewingVersion.status)}</Badge>}
                            {hasUnsavedChanges && (
                                <Badge variant="outline" className="bg-yellow-50 text-yellow-700">
                                    Unsaved changes
                                </Badge>
                            )}
                            <Button onClick={handleSave} disabled={mainFormProcessing} size="sm">
                                <Save className="mr-2 h-4 w-4" />
                                {mainFormProcessing ? 'Saving...' : 'Save'}
                            </Button>
                            <Button onClick={handleCommit} disabled={!hasUnsavedChanges || mainFormProcessing} size="sm" variant="outline">
                                <GitCommit className="mr-2 h-4 w-4" />
                                Commit Version
                            </Button>
                            <Button onClick={toggleMetadataSidebar} size="sm" variant="ghost">
                                <Settings className="h-4 w-4" />
                            </Button>
                        </div>
                    </div>
                </div>

                {/* Content Body - Scrollable */}
                <div className="min-h-0 flex-1 overflow-auto">
                    <div className="h-full">
                        {/* SOP Content */}
                        <div className="flex h-full flex-col bg-white">
                            {/* Content Area - Scrollable */}
                            <div className="h-full flex-1">
                                <RichTextEditor
                                    ref={editorRef}
                                    content={editorContent}
                                    onChange={setEditorContent}
                                    placeholder="Start documenting your standard operating procedure..."
                                    className="h-full"
                                />
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* Metadata Panel */}
            <div
                className={`bg-gray-50 transition-all duration-300 xl:flex xl:flex-col ${
                    showMetadataSidebar ? 'w-full xl:w-96' : 'w-10 xl:w-10'
                }`}
            >
                {showMetadataSidebar ? (
                    <>
                        {/* Metadata Header - Fixed */}
                        <div className="flex-shrink-0 p-4 sm:p-6">
                            <div className="flex items-center justify-between">
                                <h3 className="text-lg font-semibold text-gray-900">Metadata</h3>
                                <Button variant="ghost" size="sm" onClick={toggleMetadataSidebar} className="h-8 w-8 p-0">
                                    <ChevronRight className="h-4 w-4" />
                                </Button>
                            </div>
                        </div>

                        {/* Metadata Content - Scrollable */}
                        <div className="flex-1 overflow-y-auto px-4 pb-4 sm:px-6 sm:pb-6">
                            <div className="space-y-4">
                                {/* Version Information */}
                                {viewingVersion && (
                                    <div className="rounded-lg border border-gray-200 bg-white p-4">
                                        <div className="mb-3 flex items-center">
                                            <CheckCircle className="mr-2 h-4 w-4 text-gray-500" />
                                            <span className="font-medium">Version Info</span>
                                        </div>
                                        <div className="space-y-2 text-sm text-gray-600">
                                            <div className="flex justify-between">
                                                <span>Version:</span>
                                                <span>{viewingVersion.version_label}</span>
                                            </div>
                                            <div className="flex justify-between">
                                                <span>Created:</span>
                                                <span>{formatDate(viewingVersion.created_at)}</span>
                                            </div>
                                            <div className="flex justify-between">
                                                <span>Created by:</span>
                                                <span>{viewingVersion.creator.name}</span>
                                            </div>
                                        </div>
                                    </div>
                                )}

                                {/* Change Summary */}
                                {viewingVersion?.change_summary && (
                                    <div className="rounded-lg border border-gray-200 bg-white p-4">
                                        <div className="mb-3 flex items-center">
                                            <CheckCircle className="mr-2 h-4 w-4 text-blue-500" />
                                            <span className="font-medium">Change Summary</span>
                                        </div>
                                        <div className="text-sm text-gray-700">{viewingVersion.change_summary}</div>
                                    </div>
                                )}
                            </div>
                        </div>
                    </>
                ) : (
                    <div className="flex h-full items-center justify-center border-l border-gray-200">
                        <Button
                            variant="ghost"
                            size="sm"
                            onClick={toggleMetadataSidebar}
                            className="h-8 w-8 rotate-180 p-0"
                            title="Show Metadata"
                        >
                            <ChevronRight className="h-4 w-4" />
                        </Button>
                    </div>
                )}
            </div>

            {/* Commit Dialog */}
            <Dialog open={showCommitDialog} onOpenChange={setShowCommitDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <GitCommit className="mr-2 h-5 w-5" />
                            Commit New Version
                        </DialogTitle>
                        <DialogDescription>
                            You're about to create a new version of this SOP. Please provide a summary of what changed.
                        </DialogDescription>
                    </DialogHeader>

                    <div className="space-y-4">
                        <div>
                            <Label htmlFor="commit_summary">Change Summary *</Label>
                            <Textarea
                                id="commit_summary"
                                ref={commitSummaryRef}
                                onChange={() => {
                                    const text = commitSummaryRef.current?.value?.trim() || '';
                                    setIsCommitValid(text.length > 0);
                                }}
                                onBlur={() => {
                                    const text = commitSummaryRef.current?.value?.trim() || '';
                                    setIsCommitValid(text.length > 0);
                                }}
                                placeholder="Describe what changes were made..."
                                rows={4}
                                className="mt-1"
                            />
                        </div>

                        <div className="rounded-lg bg-blue-50 p-3">
                            <p className="text-sm text-blue-800">
                                A new version will be created: <strong>v{Math.max(...allVersions.map((v) => v.version_number), 0) + 1}</strong>
                            </p>
                        </div>
                    </div>

                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={handleCommitCancel}>
                            Cancel
                        </Button>
                        <Button type="button" onClick={handleCommitConfirm} disabled={!isCommitValid || mainFormProcessing}>
                            {mainFormProcessing ? 'Creating Version...' : 'Create Version'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Unsaved Changes Dialog */}
            <Dialog open={showUnsavedChangesDialog} onOpenChange={setShowUnsavedChangesDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <AlertTriangle className="mr-2 h-5 w-5" />
                            Unsaved Changes
                        </DialogTitle>
                        <DialogDescription>You have unsaved changes. What would you like to do?</DialogDescription>
                    </DialogHeader>

                    <DialogFooter>
                        <Button
                            type="button"
                            variant="outline"
                            onClick={() => {
                                setShowUnsavedChangesDialog(false);
                                setPendingNavigation(null);
                            }}
                        >
                            Cancel
                        </Button>
                        <Button type="button" variant="destructive" onClick={handleDiscardChanges}>
                            Discard Changes
                        </Button>
                        <Button type="button" onClick={handleSaveFromDialog}>
                            Save & Continue
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Status Change Dialog */}
            <Dialog open={showStatusDialog} onOpenChange={setShowStatusDialog}>
                <DialogContent>
                    <DialogHeader>
                        <DialogTitle className="flex items-center">
                            <Settings className="mr-2 h-5 w-5" />
                            Change Version Status
                        </DialogTitle>
                        <DialogDescription>
                            {selectedVersionForStatus && <>Change the status of Version {selectedVersionForStatus.version_number}</>}
                        </DialogDescription>
                    </DialogHeader>

                    <div className="space-y-4">
                        <div>
                            <Label htmlFor="status">Status</Label>
                            <Select value={statusData.status.toString()} onValueChange={(value) => setStatusData('status', parseInt(value))}>
                                <SelectTrigger className="mt-1">
                                    <SelectValue placeholder="Select status" />
                                </SelectTrigger>
                                <SelectContent>
                                    {getStatusOptions().map((option) => (
                                        <SelectItem key={option.value} value={option.value.toString()}>
                                            {option.label}
                                        </SelectItem>
                                    ))}
                                </SelectContent>
                            </Select>
                        </div>

                        {statusData.status === 1 && (
                            <div className="rounded-lg bg-yellow-50 p-3">
                                <p className="text-sm text-yellow-800">
                                    <strong>Note:</strong> Setting this version as "Current" will automatically change any other current version to
                                    "Work in Progress".
                                </p>
                            </div>
                        )}

                        {statusData.status === 2 && (
                            <div className="rounded-lg bg-purple-50 p-3">
                                <p className="text-sm text-purple-800">
                                    <strong>Note:</strong> Setting this version as "Future" will automatically change any other future version to
                                    "Work in Progress".
                                </p>
                            </div>
                        )}
                    </div>

                    <DialogFooter>
                        <Button type="button" variant="outline" onClick={() => setShowStatusDialog(false)}>
                            Cancel
                        </Button>
                        <Button type="button" onClick={handleStatusUpdate} disabled={statusProcessing}>
                            {statusProcessing ? 'Updating...' : 'Update Status'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Errors */}
            {errors && Object.keys(errors).length > 0 && (
                <div className="fixed bottom-4 right-4 z-50">
                    <Card className="border-red-200 bg-red-50 max-w-md">
                        <CardContent className="p-4">
                            <div className="text-sm text-red-600">
                                {Object.entries(errors).map(([key, message]) => (
                                    <p key={key} className="mb-1">
                                        <strong>{key}:</strong> {message}
                                    </p>
                                ))}
                            </div>
                        </CardContent>
                    </Card>
                </div>
            )}
        </div>
    );
}

