import { RichTextEditor } from '@/components/rich-text-editor';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { BusinessProcessCore } from '@/types';
import { router } from '@inertiajs/react';
import { Eye, Info, Save } from 'lucide-react';
import { useState } from 'react';
import { toast } from 'sonner';

interface VisualTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
    };
    content?: {
        id: number;
        title: string;
        content: string;
        version_number: number;
        version_label: string;
        status: number;
    };
}

export function VisualTabContent({ core, content }: VisualTabContentProps) {
    const [editorContent, setEditorContent] = useState(content?.content || '');
    const [isSaving, setIsSaving] = useState(false);

    const handleSave = () => {
        setIsSaving(true);

        const payload = {
            core_id: core.id,
            content_type: 'visual',
            title: `${core.name} - Visual Diagram`,
            content: editorContent,
            version_label: content ? String(Number(content.version_number) + 1) : '1.0',
        };

        const route_name = content ? 'business-processes.contents.update' : 'business-processes.contents.store';

        const route_params = content ? [core.process.id, content.id] : core.process.id;

        router.post(route(route_name, route_params), payload, {
            onSuccess: () => {
                toast.success('Visual diagram content saved successfully');
                router.reload();
            },
            onError: () => {
                toast.error('Failed to save visual diagram content');
            },
            onFinish: () => {
                setIsSaving(false);
            },
        });
    };

    return (
        <Card>
            <CardHeader>
                <div className="flex items-center justify-between">
                    <div className="flex items-center">
                        <Eye className="mr-2 h-5 w-5" />
                        <CardTitle>Visual Diagram</CardTitle>
                    </div>
                    <div className="flex items-center gap-2">
                        {content && <span className="text-sm text-gray-500">Version {content.version_number}</span>}
                        <Button onClick={handleSave} disabled={isSaving} size="sm">
                            <Save className="mr-2 h-4 w-4" />
                            {isSaving ? 'Saving...' : 'Save'}
                        </Button>
                    </div>
                </div>
                <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                    Create visual diagrams for your process using embedded diagrams or links to external tools.
                </p>
            </CardHeader>
            <CardContent className="space-y-6">
                <Alert>
                    <Info className="h-4 w-4" />
                    <AlertDescription>
                        <strong>Visual Diagram Guidelines:</strong> You can embed diagrams from tools like Lucidchart, Draw.io, or Miro. Use embedded
                        iframes, images, or links to external diagram tools. Consider including flowcharts, swimlane diagrams, or process maps that
                        visually represent your core process.
                    </AlertDescription>
                </Alert>

                <div className="space-y-3">
                    <div className="flex items-center gap-2">
                        <Eye className="h-4 w-4 text-gray-600" />
                        <h3 className="text-sm font-medium text-gray-900 dark:text-gray-100">Visual Content</h3>
                    </div>
                    <div className="min-h-[400px] rounded-lg border border-gray-200 dark:border-gray-700">
                        <RichTextEditor
                            content={editorContent}
                            onChange={setEditorContent}
                            placeholder="Add your visual diagrams here. You can embed diagrams from Lucidchart, Draw.io, Miro, or other tools..."
                        />
                    </div>
                </div>
            </CardContent>
        </Card>
    );
}

