import { Activity, Diagram, PaginatedResponse, Process, User } from '@/types';
import { DiagramInformationCard, EditableDiagramData } from './diagram-information-card';
import { ProcessActivitiesCard } from './process-activities-card';

interface InfoTabContentProps {
    diagram: Diagram & {
        user: Pick<User, 'name'>;
        process: Pick<Process, 'id' | 'name'>; // Ensure process is part of the Diagram type
    };
    activities: PaginatedResponse<Activity>;
    onSaveDiagramInfo: (dataToSave: EditableDiagramData) => Promise<void>;
    isSavingDiagramInfo: boolean;
    diagramUpdateErrors: Record<string, string>;
}

export function InfoTabContent({ diagram, activities, onSaveDiagramInfo, isSavingDiagramInfo, diagramUpdateErrors }: InfoTabContentProps) {
    return (
        <div className="grid grid-cols-1 gap-6 lg:grid-cols-3">
            <div className="space-y-6 lg:col-span-3">
                <DiagramInformationCard
                    diagram={diagram} // Pass the full diagram object including user
                    onSave={onSaveDiagramInfo}
                    isSaving={isSavingDiagramInfo}
                    errors={diagramUpdateErrors}
                />
                {/* Pass paginated activities to ProcessActivitiesCard */}
                <ProcessActivitiesCard diagram={diagram} process={diagram.process} activities={activities} />
            </div>
        </div>
    );
}

