    98→const VersionCard = ({ version, isCurrentVersion, isViewingThisVersion, coreId, onStatusChange, getStatusBadge }: VersionCardProps) => {
    99→    const isFutureVersion = version.status === 2;
   100→    const colors = getVersionCardColors(isViewingThisVersion, isCurrentVersion, isFutureVersion);
