   130→
   131→const getStatusString = (status: number): string => {
   132→    switch (status) {
   133→        case 0:
   134→            return 'Draft';
   135→        case 1:
   136→            return 'Active';
   137→        case 2:
   138→            return 'Archived';
   139→        default:
   140→            return 'Unknown';
   141→    }
   142→};
   143→
   144→const getStatusBadge = (status: number) => {
   145→    const statusConfig = {
   146→        0: { label: 'WIP', variant: 'secondary' as const, className: 'bg-orange-100 text-orange-800' },
   147→        1: { label: 'Current', variant: 'default' as const, className: 'bg-green-100 text-green-800' },
   148→        2: { label: 'Future', variant: 'outline' as const, className: 'bg-blue-100 text-blue-800' },
   149→    };
   150→
   151→    const config = statusConfig[status as keyof typeof statusConfig] || statusConfig[0];
   152→
   153→    return (
   154→        <Badge variant={config.variant} className={config.className}>
   155→            {config.label}
   156→        </Badge>
   157→    );
   158→};
   159→
   160→const formatDate = (dateString: string | undefined | null): string => {
   161→    if (!dateString) return 'N/A';
   162→    try {
   163→        return new Date(dateString).toLocaleDateString();
   164→    } catch {
   165→        return dateString;
   166→    }
   167→};
