import {
    AlertDialog,
    AlertDialogAction,
    AlertDialogCancel,
    AlertDialogContent,
    AlertDialogDescription,
    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import AppLayout from '@/layouts/app-layout';
import { BreadcrumbItem, BusinessProcessActivity, BusinessProcessCore, BusinessProcessProcess, PageProps, PaginatedResponse, User } from '@/types';
import { Head, router } from '@inertiajs/react';
import { FileText, Trash2, UploadCloud, Users, Waypoints, Workflow } from 'lucide-react';
import { useState } from 'react';
import { toast } from 'sonner';

import { HtmlImportDialog } from '@/components/diagrams/html-import-dialog';
import { SOPTabContent } from './partials/sop-tab-content';
import { VisualTabContent } from './partials/visual-tab-content';
import { SwimlaneTabContent } from './partials/swimlane-tab-content';

// Temporary placeholder for RACI tab (to be implemented later)
const RaciTabContent = ({ core }: { core: BusinessProcessCore }) => (
    <div className="p-8 text-center">
        <h3 className="text-lg font-medium">RACI Matrix</h3>
        <p className="text-gray-500">RACI content for {core.name} - Coming soon</p>
    </div>
);

interface CoreShowProps extends PageProps {
    core: BusinessProcessCore & {
        process: Pick<BusinessProcessProcess, 'id' | 'name'> & {
            category?: { id: number; name: string };
        };
        owner?: Pick<User, 'name'>;
    };
    activities: PaginatedResponse<BusinessProcessActivity>;
    sopContent?: {
        id: number;
        title: string;
        content: string;
        version_number: number;
        version_label: string;
        status: number;
    };
    raciContent?: unknown;
    visualContent?: unknown;
    swimlaneContent?: unknown;
    errors?: Record<string, string>;
}

const formatDate = (dateString: string | undefined | null): string => {
    if (!dateString) return 'N/A';
    return dateString;
};

export default function ShowCore({ core, sopContent }: CoreShowProps) {
    // Define breadcrumbs
    const breadcrumbs: BreadcrumbItem[] = [
        {
            title: 'Dashboard',
            href: route('dashboard'),
        },
        {
            title: 'Business Processes',
            href: route('business-processes'),
        },
        ...(core.process.category
            ? [
                  {
                      title: core.process.category.name,
                      href: '#',
                  },
              ]
            : []),
        {
            title: core.process.name,
            href: route('business-processes.cores.index', core.process.id),
        },
        {
            title: core.name,
            href: route('business-processes.cores.show', [core.process.id, core.id]),
        },
    ];

    const { name, updated_at, process } = core;
    const [isHtmlDrawerOpen, setIsHtmlDrawerOpen] = useState(false);

    // Delete confirmation dialog state
    const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
    const [isDeletingCore, setIsDeletingCore] = useState(false);

    const handleDeleteCore = () => {
        setDeleteDialogOpen(true);
    };

    const confirmDeleteCore = () => {
        setIsDeletingCore(true);

        router.delete(route('business-processes.cores.destroy', [process.id, core.id]), {
            onSuccess: () => {
                toast.success('Core process deleted successfully');
            },
            onError: () => {
                toast.error('Failed to delete core process');
            },
            onFinish: () => {
                setIsDeletingCore(false);
                setDeleteDialogOpen(false);
            },
        });
    };

    const cancelDeleteCore = () => {
        setDeleteDialogOpen(false);
    };

    return (
        <AppLayout breadcrumbs={breadcrumbs}>
            <Head title={name || 'Show Core Process'} />

            <div className="py-12">
                <div className="mx-auto max-w-7xl sm:px-6 lg:px-8">
                    <div className="border border-gray-200 bg-white sm:rounded-lg dark:border-gray-700 dark:bg-gray-800">
                        <div className="px-4 py-4 sm:px-6 lg:px-8">
                            <div className="flex flex-col items-start justify-between gap-4 sm:flex-row sm:items-center">
                                <div className="flex items-center gap-3">
                                    <div>
                                        <h1 className="text-xl font-bold text-gray-900 sm:text-2xl dark:text-gray-100">{name}</h1>
                                        <div className="mt-1 flex items-center gap-2 text-xs text-gray-500 dark:text-gray-400">
                                            <span>{process?.name || 'Process'}</span>
                                            <span>•</span>
                                            <span>Version {core.version}</span>
                                            <span>•</span>
                                            <span>Updated {formatDate(updated_at)}</span>
                                        </div>
                                    </div>
                                </div>
                                <div className="flex gap-2">
                                    <Button variant="outline" onClick={() => setIsHtmlDrawerOpen(true)}>
                                        <UploadCloud className="mr-2 h-4 w-4" />
                                        Import from HTMLsssss{' '}
                                    </Button>
                                    <Button variant="destructive" onClick={handleDeleteCore}>
                                        <Trash2 className="mr-2 h-4 w-4" />
                                        Delete Core Process
                                    </Button>
                                </div>
                            </div>
                        </div>

                        <Tabs defaultValue="sop" className="w-full">
                            <div className="border-t border-gray-200 dark:border-gray-700">
                                <TabsList className="mt-4 ml-4 rounded-none border-b-0 bg-transparent p-0">
                                    <TabsTrigger
                                        value="sop"
                                        className="rounded-md px-3 py-2 text-sm font-medium text-gray-600 hover:bg-gray-100 data-[state=active]:bg-blue-50 data-[state=active]:text-blue-600 data-[state=active]:shadow-none dark:text-gray-300 dark:hover:bg-gray-700/50 data-[state=active]:dark:bg-blue-900/50 data-[state=active]:dark:text-blue-400"
                                    >
                                        <FileText className="mr-2 h-4 w-4" /> SOP
                                    </TabsTrigger>
                                    <TabsTrigger
                                        value="raci"
                                        className="rounded-md px-3 py-2 text-sm font-medium text-gray-600 hover:bg-gray-100 data-[state=active]:bg-blue-50 data-[state=active]:text-blue-600 data-[state=active]:shadow-none dark:text-gray-300 dark:hover:bg-gray-700/50 data-[state=active]:dark:bg-blue-900/50 data-[state=active]:dark:text-blue-400"
                                    >
                                        <Users className="mr-2 h-4 w-4" /> RACI
                                    </TabsTrigger>
                                    <TabsTrigger
                                        value="visual"
                                        className="rounded-md px-3 py-2 text-sm font-medium text-gray-600 hover:bg-gray-100 data-[state=active]:bg-blue-50 data-[state=active]:text-blue-600 data-[state=active]:shadow-none dark:text-gray-300 dark:hover:bg-gray-700/50 data-[state=active]:dark:bg-blue-900/50 data-[state=active]:dark:text-blue-400"
                                    >
                                        <Waypoints className="mr-2 h-4 w-4" /> Visual Diagram
                                    </TabsTrigger>
                                    <TabsTrigger
                                        value="swimlane"
                                        className="rounded-md px-3 py-2 text-sm font-medium text-gray-600 hover:bg-gray-100 data-[state=active]:bg-blue-50 data-[state=active]:text-blue-600 data-[state=active]:shadow-none dark:text-gray-300 dark:hover:bg-gray-700/50 data-[state=active]:dark:bg-blue-900/50 data-[state=active]:dark:text-blue-400"
                                    >
                                        <Workflow className="mr-2 h-4 w-4" /> Swimlane
                                    </TabsTrigger>
                                </TabsList>
                            </div>

                            <div className="p-6">
                                <TabsContent value="sop" className="mt-0 w-full pt-0 focus-visible:ring-0 focus-visible:ring-offset-0">
                                    <SOPTabContent core={core} content={sopContent} />
                                </TabsContent>
                                <TabsContent value="raci" className="mt-0 pt-0 focus-visible:ring-0 focus-visible:ring-offset-0">
                                    <RaciTabContent core={core} />
                                </TabsContent>
                                <TabsContent value="visual" className="mt-0 pt-0 focus-visible:ring-0 focus-visible:ring-offset-0">
                                    <VisualTabContent core={core} />
                                </TabsContent>
                                <TabsContent value="swimlane" className="mt-0 pt-0 focus-visible:ring-0 focus-visible:ring-offset-0">
                                    <SwimlaneTabContent core={core} />
                                </TabsContent>
                            </div>
                        </Tabs>
                    </div>

                    <HtmlImportDialog isOpen={isHtmlDrawerOpen} onClose={() => setIsHtmlDrawerOpen(false)} diagramId={core.id} />

                    {/* Delete Confirmation Dialog */}
                    <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
                        <AlertDialogContent>
                            <AlertDialogHeader>
                                <AlertDialogTitle>Are you sure you want to delete this core process?</AlertDialogTitle>
                                <AlertDialogDescription>
                                    This action cannot be undone. This will permanently delete the core process "{core.name}" and all its activities
                                    from your process.
                                </AlertDialogDescription>
                            </AlertDialogHeader>
                            <AlertDialogFooter>
                                <AlertDialogCancel onClick={cancelDeleteCore}>Cancel</AlertDialogCancel>
                                <AlertDialogAction onClick={confirmDeleteCore} disabled={isDeletingCore}>
                                    {isDeletingCore ? 'Deleting...' : 'Delete'}
                                </AlertDialogAction>
                            </AlertDialogFooter>
                        </AlertDialogContent>
                    </AlertDialog>
                </div>
            </div>
        </AppLayout>
    );
}

