                                </TabsContent>
                                <TabsContent value="raci" className="mt-0 h-full pt-0 focus-visible:ring-0 focus-visible:ring-offset-0">
                                    <RaciTabContent core={core} />
                                </TabsContent>
                                <TabsContent value="visual" className="mt-0 h-full pt-0 focus-visible:ring-0 focus-visible:ring-offset-0">
                                    <VisualTabContent core={core} content={visualContent} versions={visualVersions} errors={errors} />
                                </TabsContent>
                                <TabsContent value="swimlane" className="mt-0 h-full pt-0 focus-visible:ring-0 focus-visible:ring-offset-0">
                                    <SwimlaneTabContent core={core} content={swimlaneContent} versions={swimlaneVersions} errors={errors} />
                                </TabsContent>
                            </div>
                        </Tabs>
                    </div>

                    <HtmlImportDialog isOpen={isHtmlDrawerOpen} onClose={() => setIsHtmlDrawerOpen(false)} diagramId={core.id} />

                    {/* Delete Confirmation Dialog */}
                    <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
                        <AlertDialogContent>
                            <AlertDialogHeader>
                                <AlertDialogTitle>Are you sure you want to delete this core process?</AlertDialogTitle>
                                <AlertDialogDescription>
                                    This action cannot be undone. This will permanently delete the core process "{core.name}" and all its activities
                                    from your process.
                                </AlertDialogDescription>
                            </AlertDialogHeader>
                            <AlertDialogFooter>
                                <AlertDialogCancel onClick={cancelDeleteCore}>Cancel</AlertDialogCancel>
                                <AlertDialogAction onClick={confirmDeleteCore} disabled={isDeletingCore}>
                                    {isDeletingCore ? 'Deleting...' : 'Delete'}
                                </AlertDialogAction>
                            </AlertDialogFooter>
                        </AlertDialogContent>
                    </AlertDialog>
                </div>
            </div>
        </AppLayout>
    );
}

