    12→    public function test_drag_and_drop_activity_reordering(): void
    13→    {
    14→        $this->browse(function (Browser $browser) {
    15→            // Create test activities with specific order
    16→            $activities = $this->createTestActivities($this->testCore, 4);
    17→
    18→            $this->loginAsAdmin($browser);
    19→            $this->navigateToTab($browser, 'raci');
    20→
    21→            // Take screenshot of initial order
    22→            $this->takeDebugScreenshot($browser, 'raci-initial-order');
    23→
    24→            // Verify initial order
    25→            $browser->assertSeeIn('[data-position="1"]', 'Test Activity 1')
    26→                   ->assertSeeIn('[data-position="2"]', 'Test Activity 2')
    27→                   ->assertSeeIn('[data-position="3"]', 'Test Activity 3')
    28→                   ->assertSeeIn('[data-position="4"]', 'Test Activity 4');
    29→
    30→            // Perform drag and drop operation - move Activity 1 to position 3
    31→            $browser->script("
    32→                // Find the sortable elements
    33→                const sourceElement = document.querySelector('[data-activity-id=\"{$activities[0]->id}\"]');
    34→                const targetElement = document.querySelector('[data-activity-id=\"{$activities[2]->id}\"]');
    35→                
    36→                if (sourceElement && targetElement) {
    37→                    // Create drag event simulation
    38→                    const dragStartEvent = new DragEvent('dragstart', {
    39→                        bubbles: true,
    40→                        cancelable: true,
    41→                        dataTransfer: new DataTransfer()
    42→                    });
    43→                    
    44→                    const dragOverEvent = new DragEvent('dragover', {
    45→                        bubbles: true,
    46→                        cancelable: true,
    47→                        dataTransfer: new DataTransfer()
    48→                    });
    49→                    
    50→                    const dropEvent = new DragEvent('drop', {
    51→                        bubbles: true,
    52→                        cancelable: true,
    53→                        dataTransfer: new DataTransfer()
    54→                    });
    55→                    
    56→                    // Set drag data
    57→                    dragStartEvent.dataTransfer.setData('text/plain', '{$activities[0]->id}');
    58→                    
    59→                    // Dispatch events
    60→                    sourceElement.dispatchEvent(dragStartEvent);
    61→                    targetElement.dispatchEvent(dragOverEvent);
    62→                    targetElement.dispatchEvent(dropEvent);
    63→                }
    64→            ");
    65→
    66→            // Wait for reorder animation and API call
