    protected function assertCanSeeVersionManagement(Browser $browser): void
    {
        $browser->assertPresent('[data-testid="version-card"]')
                ->assertSee('Version');
    }

    /**
     * Setup a new core process for testing
     * This method navigates through the full workflow: login → business processes → process → create core
     * 
     * @param Browser $browser
     * @param int $processId The ID of the seeded process to use (default: 1)
     * @return array Core process details including processId, coreId, coreName, coreUrl
     */
    protected function setupCoreProcess(Browser $browser, int $processId = 1): array
    {
        // Navigate to business processes dashboard
        $browser->visit('/business-processes')
                ->waitFor('[data-testid="business-process-grid"]', 10)
                ->pause(2000); // Wait for React components to fully load

        // Click on the process card to go to cores listing
        $browser->click("[data-testid=\"process-card-{$processId}\"]")
                ->waitForLocation("/business-processes/{$processId}/cores", 10)
                ->pause(2000); // Wait for cores page to load

        // Click create new core button
        $browser->click('[data-testid="create-core-button"]')
                ->waitFor('[data-testid="create-core-modal"]', 5);

        // Generate unique core name with timestamp
        $timestamp = time();
        $coreName = "Test Core Process {$timestamp}";
        $coreDescription = "Test core description for E2E testing - Created at " . date('Y-m-d H:i:s');

        // Fill in core creation form
        $browser->type('[data-testid="core-name-input"]', $coreName)
                ->type('[data-testid="core-description-input"]', $coreDescription)
                ->click('[data-testid="create-core-submit"]')
                ->waitForText($coreName, 10);

        // Wait for redirect to the new core page
        $browser->pause(2000); // Allow for redirect to complete

        // Extract core ID from current URL
        $currentUrl = $browser->driver->getCurrentURL();
        preg_match('/cores\/(\d+)/', $currentUrl, $matches);
        $coreId = $matches[1] ?? null;

        // Verify we're on the core page with 4 tabs
        $this->assertCanSeeCoreProcessTabs($browser);

        // Return core details for use in tests
        return [
            'processId' => $processId,
            'coreId' => $coreId,
            'coreName' => $coreName,
            'coreDescription' => $coreDescription,
            'coreUrl' => "/business-processes/{$processId}/cores/{$coreId}"
        ];
    }
}

