    /**
     * Test Phase 2: SOP Creation workflow
     */
    public function test_sop_creation_workflow(): void
    {
        $this->browse(function (Browser $browser) {
            $this->loginAsUser($browser);

            // Navigate to cores page and create a core first
            $browser->visit('/dashboard')
                ->waitForLocation('/dashboard')
                ->clickLink('View Processes')
                ->waitForLocation('/business-processes')
                ->clickLink('Environmental Scanning')
                ->pause(500);

            // Click create core button
            $browser->click('#create-core-button')
                ->waitFor('input[name="name"]', 1);

            // Fill form and submit
            $coreName = 'SOP Test Core';
            $browser->type('input[name="name"]', $coreName)
                ->type('textarea[name="description"], input[name="description"]', 'Test description for SOP')
                ->click('button[type="submit"]')
                ->waitForText($coreName, 1);

            // Now test SOP creation workflow
            // Wait for SOP tab to be active (should be default)
            $browser->pause(2000);

            // Add content to TinyMCE editor
            $sopContent = '<h1>Test SOP Document</h1><p>This is a test Standard Operating Procedure created during E2E testing.</p><h2>Procedure Steps:</h2><ol><li>Step 1: Login to system</li><li>Step 2: Navigate to process</li><li>Step 3: Follow guidelines</li></ol>';

            $browser->script("
                if (window.tinymce && window.tinymce.get('tinymce')) {
                    window.tinymce.get('tinymce').setContent('{$sopContent}');
                }
            ");

            // Look for Create SOP button and click it
            $browser->pause(1000)
                ->waitFor('button:contains("Create SOP")', 5)
                ->click('button:contains("Create SOP")')
                ->pause(3000); // Wait for version creation

            // Verify version appears in sidebar
            $browser->assertSee('Version 1')
                ->assertSee('Current')
                ->assertSee('Initial SOP creation');
        });
    }
}

