 64	    /**
 65	     * Generates Mermaid diagram code based on the diagram's activities.
 66	     *
 67	     * @return string The generated Mermaid code.
 68	     */
 69	    public function generateMermaidCode(): string
 70	    {
 71	        $mermaidCode = "flowchart TD\n";
 72	        // Load activities with SOP relationships for status indicators
 73	        $activities = $this->activities()->with(['sop.versions' => function ($query) {
 74	            $query->orderBy('version_number', 'desc');
 75	        }])->get();
 76	
 77	        $currentActivities = $activities->where('current_future', 0)->values(); // 0 for Current
 78	        $futureActivities = $activities->where('current_future', 1)->values();  // 1 for Future
 79	
 80	        $nodeCounter = 1; // To ensure unique node IDs
 81	
 82	        if ($currentActivities->isNotEmpty()) {
 83	            $mermaidCode .= "    subgraph Current [\"Current Activities\"]\n";
 84	            foreach ($currentActivities as $index => $activity) {
 85	                $activityId = 'C' . $nodeCounter++;
 86	
 87	                // Get SOP status indicator
 88	                $sopIndicator = $this->getSopIndicatorForActivity($activity);
 89	
 90	                $raciParts = [];
 91	                if ($activity->responsible) $raciParts[] = "R.: " . $activity->responsible;
 92	                if ($activity->accountable) $raciParts[] = "A.: " . $activity->accountable;
 93	                if ($activity->consulted) $raciParts[] = "C.: " . $activity->consulted;
 94	                if ($activity->informed)  $raciParts[] = "I.: " . $activity->informed;
 95	                $raciText = !empty($raciParts) ? " | " . implode(" | ", $raciParts) : '';
 96	
 97	                // Include SOP indicator in the node text
 98	                $nodeText = $activity->name . $sopIndicator . $raciText;
 99	                $mermaidCode .= sprintf("        %s[\"%s\"]\n", $activityId, addslashes($nodeText));
100	
101	                if ($index < $currentActivities->count() - 1) {
102	                    $nextActivityId = 'C' . $nodeCounter; // ID of the next node in this subgraph
103	                    $mermaidCode .= sprintf("        %s --> %s\n", $activityId, $nextActivityId);
104	                }
105	            }
106	            $mermaidCode .= "    end\n\n";
107	        }
108	
109	        $nodeCounter = 1; // Reset for future activities to keep IDs like F1, F2
110	        if ($futureActivities->isNotEmpty()) {
111	            $mermaidCode .= "    subgraph Future [\"Future Activities\"]\n";
112	            foreach ($futureActivities as $index => $activity) {
113	                $activityId = 'F' . $nodeCounter++;
114	
115	                // Get SOP status indicator
116	                $sopIndicator = $this->getSopIndicatorForActivity($activity);
117	
118	                $raciParts = [];
119	                if ($activity->responsible) $raciParts[] = "R.: " . $activity->responsible;
120	                if ($activity->accountable) $raciParts[] = "A.: " . $activity->accountable;
121	                if ($activity->consulted) $raciParts[] = "C.: " . $activity->consulted;
122	                if ($activity->informed)  $raciParts[] = "I.: " . $activity->informed;
123	                $raciText = !empty($raciParts) ? " | " . implode(" | ", $raciParts) : '';
124	
125	                // Include SOP indicator in the node text
126	                $nodeText = $activity->name . $sopIndicator . $raciText;
127	                $mermaidCode .= sprintf("        %s[\"%s\"]\n", $activityId, addslashes($nodeText));
128	
129	                if ($index < $futureActivities->count() - 1) {
130	                    $nextActivityId = 'F' . $nodeCounter; // ID of the next node in this subgraph
131	                    $mermaidCode .= sprintf("        %s --> %s\n", $activityId, $nextActivityId);
132	                }
133	            }
134	            $mermaidCode .= "    end\n";
135	        }
136	
137	        if ($currentActivities->isNotEmpty() && $futureActivities->isNotEmpty()) {
138	            // Connect last current to first future if both exist. Need their IDs.
139	            // This is a simplification; you might want more complex inter-subgraph logic.
140	            $lastCurrentActivityId = 'C' . ($currentActivities->count()); // This assumes sequential C1, C2...
141	            $firstFutureActivityId = 'F1';
142	            $mermaidCode .= sprintf("    %s --> %s\n", $lastCurrentActivityId, $firstFutureActivityId);
143	        }
144	
145	        return $mermaidCode;
146	    }
