148	    /**
149	     * Get SOP status indicator for an activity to display in Mermaid diagram.
150	     */
151	    private function getSopIndicatorForActivity($activity): string
152	    {
153	        if (!$activity->sop || $activity->sop->is_archived) {
154	            return ' [No SOP]';
155	        }
156	
157	        $sop = $activity->sop;
158	        $currentVersion = $sop->getCurrentVersion();
159	
160	        if ($currentVersion) {
161	            return ' [SOP ‚úì]';
162	        }
163	
164	        $latestVersion = $sop->getLatestVersion();
165	        if ($latestVersion) {
166	            switch ($latestVersion->status->value) {
167	                case 0: // WIP
168	                    return ' [SOP ‚è≥]';
169	                case 2: // Future
170	                    return ' [SOP üìã]';
171	                default:
172	                    return ' [SOP ?]';
173	            }
174	        }
175	
176	        return ' [SOP ?]';
177	    }
