import { BusinessProcessActivitiesCard } from '@/components/business-processes/business-process-activities-card';
import { BusinessProcessCore } from '@/types';

interface RaciTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
    };
    activities?: unknown; // Keep for compatibility but not used
}

export function RaciTabContent({ core }: RaciTabContentProps) {
    return (
        <div className="flex h-full flex-col">
            {/* Content Body */}
            <div className="flex-1 overflow-auto">
                <div className="h-full">
                    <div className="flex h-full flex-col bg-white">
                        <div className="p-6">
                            <BusinessProcessActivitiesCard core={core} />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

