   105â†’                    </TableCell>
   106â†’                    <TableCell className="hidden w-24 px-3 py-4 align-top sm:table-cell">
   107â†’                        <Input
   108â†’                            name="accountable"
   109â†’                            value={editableData.accountable || ''}
   110â†’                            onChange={onFieldChange}
   111â†’                            placeholder="A"
   112â†’                            className={clsx('text-sm', editErrors.accountable && 'border-red-500')}
   113â†’                        />
