     3â†’namespace App\Traits;
     4â†’
     5â†’use Illuminate\Support\Facades\Log;
     6â†’use DOMDocument;
     7â†’use DOMXPath;
     8â†’
     9â†’trait HtmlParser
    10â†’{
    11â†’    /**
    12â†’     * Parses HTML content to extract a list of activities with their RACI assignments.
    13â†’     *
    14â†’     * @param string $htmlContent The HTML content to parse.
    15â†’     * @return array A list of activities, where each activity is an associative array.
    16â†’     *               Example structure:
    17â†’     *               [
    18â†’     *                   'name' => 'Activity Name',
    19â†’     *                   'position' => 1,
    20â†’     *                   'current_future' => 0, // 0 for Current, 1 for Future
    21â†’     *                   'responsible' => 'Person R',
    22â†’     *                   'accountable' => 'Person A',
    23â†’     *                   'consulted' => 'Person C',
    24â†’     *                   'informed' => 'Person I',
    25â†’     *                   'process_link' => null,
    26â†’     *                   'description' => null,
    27â†’     *                   'type' => null,
    28â†’     *               ]
    29â†’     */
    30â†’    public function parseHtmlToActivities(string $htmlContent): array
    31â†’    {
    32â†’        Log::debug('[HtmlParser] Starting HTML parsing');
    33â†’
    34â†’        $activities = [];
    35â†’        $position = 0;
    36â†’
    37â†’        // Create a DOMDocument and load the HTML
    38â†’        $dom = new DOMDocument();
    39â†’
    40â†’        // Suppress warnings for malformed HTML
    41â†’        libxml_use_internal_errors(true);
    42â†’
    43â†’        // Load HTML content
    44â†’        $dom->loadHTML($htmlContent, LIBXML_HTML_NOIMPLIED | LIBXML_HTML_NODEFDTD);
    45â†’
    46â†’        // Clear libxml errors
    47â†’        libxml_clear_errors();
    48â†’
    49â†’        $xpath = new DOMXPath($dom);
    50â†’
    51â†’        // Find all step-box elements (activities)
    52â†’        $stepBoxes = $xpath->query("//div[contains(@class, 'step-box')]");
    53â†’
    54â†’        Log::debug('[HtmlParser] Found step boxes', ['count' => $stepBoxes->length]);
    55â†’
    56â†’        foreach ($stepBoxes as $stepBox) {
    57â†’            $position++;
    58â†’
    59â†’            // Extract activity name from step-title
    60â†’            $titleNodes = $xpath->query(".//div[contains(@class, 'step-title')]", $stepBox);
    61â†’            $activityName = $titleNodes->length > 0 ? trim($titleNodes->item(0)->textContent) : '';
    62â†’
    63â†’            // Extract description from step-desc
    64â†’            $descNodes = $xpath->query(".//div[contains(@class, 'step-desc')]", $stepBox);
    65â†’            $description = $descNodes->length > 0 ? trim($descNodes->item(0)->textContent) : null;
    66â†’
    67â†’            // Extract RACI information from raci class
    68â†’            $raciNodes = $xpath->query(".//div[contains(@class, 'raci')]", $stepBox);
    69â†’            $raciText = $raciNodes->length > 0 ? trim($raciNodes->item(0)->textContent) : '';
    70â†’
    71â†’            Log::debug('[HtmlParser] Processing activity', [
    72â†’                'position' => $position,
    73â†’                'name' => $activityName,
    74â†’                'description' => $description,
    75â†’                'raci_text' => $raciText
    76â†’            ]);
    77â†’
    78â†’            // Skip if no activity name
    79â†’            if (empty($activityName)) {
    80â†’                Log::debug('[HtmlParser] Skipping activity with empty name');
    81â†’                continue;
    82â†’            }
    83â†’
    84â†’            // Initialize activity data
    85â†’            $activityData = [
    86â†’                'name' => $activityName,
    87â†’                'position' => $position,
    88â†’                'current_future' => 0, // Default to current
    89â†’                'responsible' => null,
    90â†’                'accountable' => null,
    91â†’                'consulted' => null,
    92â†’                'informed' => null,
    93â†’                'process_link' => null,
    94â†’                'description' => $description,
    95â†’                'type' => null,
    96â†’            ];
    97â†’
    98â†’            // Parse RACI information
    99â†’            if (!empty($raciText)) {
   100â†’                $activityData = $this->parseRaciFromText($raciText, $activityData);
   101â†’            }
   102â†’
   103â†’            Log::debug('[HtmlParser] Final activity data', ['data' => $activityData]);
   104â†’            $activities[] = $activityData;
   105â†’        }
   106â†’
   107â†’        Log::debug('[HtmlParser] Parsing complete. Total activities parsed:', ['count' => count($activities)]);
   108â†’        return $activities;
   109â†’    }
   110â†’
   111â†’    /**
   112â†’     * Parse RACI information from text like "R: Julia | A: Vipul | C: Dale"
   113â†’     *
   114â†’     * @param string $raciText The RACI text to parse
   115â†’     * @param array $activityData The activity data array to update
   116â†’     * @return array Updated activity data with RACI assignments
   117â†’     */
   118â†’    private function parseRaciFromText(string $raciText, array $activityData): array
   119â†’    {
   120â†’        Log::debug('[HtmlParser] Parsing RACI text', ['text' => $raciText]);
   121â†’
   122â†’        // Handle special case for handoff text
   123â†’        if (strpos($raciText, 'Handoff to') !== false) {
   124â†’            Log::debug('[HtmlParser] Skipping handoff text');
   125â†’            return $activityData;
   126â†’        }
   127â†’
   128â†’        // Split by pipe (|) to get individual RACI assignments
   129â†’        $raciParts = explode('|', $raciText);
   130â†’
   131â†’        foreach ($raciParts as $part) {
   132â†’            $part = trim($part);
   133â†’
   134â†’            // Match pattern like "R: Julia" or "A: Vipul"
   135â†’            if (preg_match('/^([RACI])\s*:\s*(.+)$/i', $part, $matches)) {
   136â†’                $role = strtoupper(trim($matches[1]));
   137â†’                $person = trim($matches[2]);
   138â†’
   139â†’                // Skip N/A assignments
   140â†’                if (strtoupper($person) === 'N/A') {
   141â†’                    continue;
   142â†’                }
   143â†’
   144â†’                Log::debug('[HtmlParser] Found RACI assignment', ['role' => $role, 'person' => $person]);
   145â†’
   146â†’                switch ($role) {
   147â†’                    case 'R':
   148â†’                        $activityData['responsible'] = $person;
   149â†’                        break;
   150â†’                    case 'A':
   151â†’                        $activityData['accountable'] = $person;
   152â†’                        break;
   153â†’                    case 'C':
   154â†’                        $activityData['consulted'] = $person;
   155â†’                        break;
   156â†’                    case 'I':
   157â†’                        $activityData['informed'] = $person;
   158â†’                        break;
   159â†’                }
   160â†’            }
   161â†’        }
   162â†’
   163â†’        return $activityData;
   164â†’    }
   165â†’}
