    70â†’
    71â†’    /**
    72â†’     * Get versions sorted by status priority and version number.
    73â†’     * Order: Current (if any), Future (if any), then all Draft (newest to oldest)
    74â†’     */
    75â†’    public function getSortedVersions()
    76â†’    {
    77â†’        return $this->versions()
    78â†’            ->with(['creator'])
    79â†’            ->get()
    80â†’            ->sort(function ($a, $b) {
    81â†’                // Define status priority: Current=3, Archived=2, Draft=1
    82â†’                $statusPriority = [
    83â†’                    'current' => 3,  // Current gets highest priority
    84â†’                    'archived' => 2, // Archived gets second priority (like Future in SOP)
    85â†’                    'draft' => 1,    // Draft gets lowest priority (like WIP in SOP)
    86â†’                ];
    87â†’
    88â†’                $aPriority = $statusPriority[$a->status] ?? 0;
    89â†’                $bPriority = $statusPriority[$b->status] ?? 0;
    90â†’
    91â†’                // First sort by status priority (higher priority first)
    92â†’                if ($aPriority !== $bPriority) {
    93â†’                    return $bPriority - $aPriority;
    94â†’                }
    95â†’
    96â†’                // Within same status, sort by version number (newest first)
    97â†’                return (float)$b->version_number - (float)$a->version_number;
    98â†’            })
    99â†’            ->values(); // Reset array keys
   100â†’    }
