import { Alert, AlertDescription, AlertTitle } from '@/components/ui/alert';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Textarea } from '@/components/ui/textarea';
import { router } from '@inertiajs/react';
import { AlertTriangleIcon, Code, File, FileCheck2 } from 'lucide-react';
import { ChangeEvent, DragEvent, useEffect, useRef, useState } from 'react';

interface HtmlImportDialogProps {
    isOpen: boolean;
    onClose: () => void;
    diagramId: number;
}

export function HtmlImportDialog({ isOpen, onClose, diagramId }: HtmlImportDialogProps) {
    const [selectedFile, setSelectedFile] = useState<File | null>(null);
    const [htmlContent, setHtmlContent] = useState<string>('');
    const [isImporting, setIsImporting] = useState(false);
    const [importError, setImportError] = useState<string | null>(null);
    const [isDragOver, setIsDragOver] = useState(false);
    const [activeTab, setActiveTab] = useState<string>('paste');
    const fileInputRef = useRef<HTMLInputElement>(null);

    useEffect(() => {
        if (!isOpen) {
            setSelectedFile(null);
            setHtmlContent('');
            setImportError(null);
            setIsDragOver(false);
            setActiveTab('paste');
        }
    }, [isOpen]);

    const handleFileChange = (event: ChangeEvent<HTMLInputElement>) => {
        const file = event.target.files?.[0] || null;
        setSelectedFile(file);
        setImportError(null);
    };

    const handleHtmlContentChange = (event: ChangeEvent<HTMLTextAreaElement>) => {
        setHtmlContent(event.target.value);
        setImportError(null);
    };

    const handleDragOver = (event: DragEvent<HTMLDivElement>) => {
        event.preventDefault();
        setIsDragOver(true);
    };

    const handleDragLeave = (event: DragEvent<HTMLDivElement>) => {
        event.preventDefault();
        setIsDragOver(false);
    };

    const handleDrop = (event: DragEvent<HTMLDivElement>) => {
        event.preventDefault();
        setIsDragOver(false);

        const files = event.dataTransfer.files;
        if (files.length > 0) {
            const file = files[0];
            if (file.type === 'text/html' || file.name.endsWith('.html') || file.name.endsWith('.htm')) {
                setSelectedFile(file);
                setImportError(null);
            } else {
                setImportError('Please select an HTML file (.html or .htm)');
            }
        }
    };

    const handleBrowseClick = () => {
        fileInputRef.current?.click();
    };

    const handleImport = () => {
        if (activeTab === 'paste') {
            if (!htmlContent.trim()) {
                setImportError('Please paste HTML content to import.');
                return;
            }

            setIsImporting(true);
            setImportError(null);

            const formData = new FormData();
            formData.append('html_content', htmlContent);

            router.post(route('diagrams.parse-html', diagramId), formData, {
                onSuccess: () => {
                    onClose();
                },
                onError: (errors) => {
                    const firstError = Object.values(errors)[0];
                    setImportError(firstError || 'An unknown error occurred during import.');
                },
                onFinish: () => {
                    setIsImporting(false);
                },
            });
        } else {
            if (!selectedFile) {
                setImportError('Please select an HTML file to import.');
                return;
            }

            setIsImporting(true);
            setImportError(null);

            const formData = new FormData();
            formData.append('html_file', selectedFile);

            router.post(route('diagrams.parse-html', diagramId), formData, {
                onSuccess: () => {
                    onClose();
                },
                onError: (errors) => {
                    const firstError = Object.values(errors)[0];
                    setImportError(firstError || 'An unknown error occurred during import.');
                },
                onFinish: () => {
                    setIsImporting(false);
                },
            });
        }
    };

    const isImportDisabled = activeTab === 'paste' ? !htmlContent.trim() : !selectedFile;

    return (
        <Dialog
            open={isOpen}
            onOpenChange={(open) => {
                if (!open) onClose();
            }}
        >
            <DialogContent className="sm:max-w-2xl">
                <DialogHeader>
                    <DialogTitle className="text-xl font-semibold">Import from HTML</DialogTitle>
                    <p className="mt-2 text-sm text-gray-600 dark:text-gray-400">
                        Upload an HTML file or paste HTML content containing process activities with RACI assignments
                    </p>
                </DialogHeader>

                <div className="space-y-6">
                    {/* Requirements Section */}
                    <div className="space-y-3">
                        <div className="flex items-center gap-2 text-sm font-medium text-gray-700 dark:text-gray-300">
                            <Code className="h-4 w-4" />
                            Requirements
                        </div>
                        <ul className="space-y-1 text-sm text-gray-600 dark:text-gray-400">
                            <li>â€¢ Activities should be in step-box containers</li>
                            <li>
                                â€¢ Include RACI format:{' '}
                                <code className="rounded bg-gray-100 px-1 text-xs dark:bg-gray-800">"R: Person | A: Person | C: Person"</code>
                            </li>
                            <li>â€¢ HTML files up to 2MB or paste content directly</li>
                        </ul>
                    </div>

                    {/* Tabs */}
                    <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
                        <TabsList className="grid w-full grid-cols-2">
                            <TabsTrigger value="paste" className="flex items-center gap-2">
                                <Code className="h-4 w-4" />
                                Paste HTML
                            </TabsTrigger>
                            <TabsTrigger value="upload" className="flex items-center gap-2">
                                <File className="h-4 w-4" />
                                Upload File
                            </TabsTrigger>
                        </TabsList>

                        <TabsContent value="paste" className="space-y-4">
                            <div className="space-y-2">
                                <label className="text-sm font-medium text-gray-700 dark:text-gray-300">HTML Content</label>
                                <Textarea
                                    value={htmlContent}
                                    onChange={handleHtmlContentChange}
                                    placeholder={`Paste your HTML content here...

Example:
<div class='step-box'>
  <h3>Process Step 1</h3>
  <p>R: John Smith | A: Jane Doe | C: Mike Johnson</p>
</div>`}
                                    className="h-[200px] resize-none font-mono text-sm"
                                    disabled={isImporting}
                                />
                                <p className="text-xs text-gray-500 dark:text-gray-400">{htmlContent.length} characters</p>
                            </div>
                        </TabsContent>

                        <TabsContent value="upload" className="space-y-4">
                            {/* Drop Zone */}
                            <div
                                className={`relative rounded-lg border-2 border-dashed p-8 text-center transition-all duration-200 ${
                                    isDragOver
                                        ? 'border-blue-400 bg-blue-50 dark:border-blue-500 dark:bg-blue-900/20'
                                        : selectedFile
                                          ? 'border-gray-400 bg-gray-50 dark:border-gray-500 dark:bg-gray-800/50'
                                          : 'border-gray-300 bg-gray-50 hover:border-gray-400 hover:bg-gray-100 dark:border-gray-600 dark:bg-gray-800/30 dark:hover:border-gray-500 dark:hover:bg-gray-800/50'
                                }`}
                                onDragOver={handleDragOver}
                                onDragLeave={handleDragLeave}
                                onDrop={handleDrop}
                            >
                                <input
                                    ref={fileInputRef}
                                    type="file"
                                    accept=".html,.htm"
                                    onChange={handleFileChange}
                                    disabled={isImporting}
                                    className="absolute inset-0 h-full w-full cursor-pointer opacity-0"
                                />

                                <div className="space-y-4">
                                    {/* Icon */}
                                    <div className="flex justify-center">
                                        {selectedFile ? (
                                            <div className="flex h-16 w-16 items-center justify-center">
                                                <FileCheck2 className="h-8 w-8 text-gray-600 dark:text-gray-400" />
                                            </div>
                                        ) : (
                                            <div className="flex h-16 w-16 items-center justify-center">
                                                <File className="h-8 w-8 text-gray-600 dark:text-gray-400" />
                                            </div>
                                        )}
                                    </div>

                                    {/* Content */}
                                    {selectedFile ? (
                                        <div className="space-y-2">
                                            <p className="text-lg font-medium text-gray-700 dark:text-gray-300">File Ready</p>
                                            <p className="mx-auto max-w-xs truncate text-sm text-gray-600 dark:text-gray-400">{selectedFile.name}</p>
                                            <p className="text-xs text-gray-500 dark:text-gray-500">
                                                {(selectedFile.size / 1024 / 1024).toFixed(2)} MB
                                            </p>
                                        </div>
                                    ) : (
                                        <div className="space-y-3">
                                            <div>
                                                <p className="mb-2 text-lg font-medium text-gray-700 dark:text-gray-300">Drop your HTML file here</p>
                                                <p className="mb-4 text-sm text-gray-500 dark:text-gray-400">or</p>
                                                <Button
                                                    variant="outline"
                                                    className="bg-white dark:bg-gray-800"
                                                    onClick={handleBrowseClick}
                                                    type="button"
                                                >
                                                    Browse Files
                                                </Button>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </TabsContent>
                    </Tabs>

                    {/* Warning */}
                    <div className="rounded-lg border border-amber-200 bg-amber-50 p-4 dark:border-amber-700 dark:bg-amber-900/20">
                        <div className="flex items-start gap-3">
                            <AlertTriangleIcon className="mt-0.5 h-5 w-5 flex-shrink-0 text-amber-600 dark:text-amber-400" />
                            <div>
                                <p className="text-sm font-medium text-amber-800 dark:text-amber-300">Important Notice</p>
                                <p className="mt-1 text-sm text-amber-700 dark:text-amber-400">
                                    This will replace all existing activities in the current RACI chart.
                                </p>
                            </div>
                        </div>
                    </div>

                    {/* Error Alert */}
                    {importError && (
                        <Alert variant="destructive">
                            <AlertTriangleIcon className="h-4 w-4" />
                            <AlertTitle>Import Error</AlertTitle>
                            <AlertDescription>{importError}</AlertDescription>
                        </Alert>
                    )}

                    {/* Actions */}
                    <div className="flex justify-end gap-3 pt-2">
                        <Button variant="outline" onClick={onClose} disabled={isImporting}>
                            Cancel
                        </Button>
                        <Button
                            className="bg-blue-600 hover:bg-blue-700 dark:bg-blue-600 dark:hover:bg-blue-700"
                            disabled={isImportDisabled || isImporting}
                            onClick={handleImport}
                        >
                            {isImporting ? 'Importing...' : 'Import'}
                        </Button>
                    </div>
                </div>
            </DialogContent>
        </Dialog>
    );
}

