    version_label: string;
    status: number;
    change_summary?: string;
    created_at: string;
    updated_at: string;

    // Metadata
    date_of_last_test?: string;
    tested_by?: number;
    test_notes?: string;
    reviewed_by_worker: boolean;
    date_of_review?: string;
    reviewer_id?: number;
    review_notes?: string;
    approval_date?: string;
    approved_by?: number;
    approval_notes?: string;

    creator: {
        id: number;
