        id: number;
        name: string;
    };
    reviewer?: {
        id: number;
        name: string;
    };
    approver?: {
        id: number;
        name: string;
    };

    sop?: SOP;
}

export interface Activity {
    id: number;
    name: string;
    diagram_id: number;
    position: number;
