    business_process_core_id: number;
    activity_category_id?: number;
    name: string;
    position: number;
    responsible?: string;
    accountable?: string;
    consulted?: string;
    informed?: string;
    created_at: string;
    updated_at: string;
    core?: BusinessProcessCore;
    category?: BusinessProcessActivityCategory;
    blueprint?: BusinessProcessBlueprint;
}

export interface BusinessProcessContent {
    id: number;
    core_id: number;
    type: string; // 'sop', 'visual_diagram', 'swimlane'
    creator_id: number;
    updater_id: number | null;
    created_at: string;
    updated_at: string;
    core?: BusinessProcessCore;
    creator?: User;
    updater?: User;
    versions?: BusinessProcessContentVersion[];
    current_version?: BusinessProcessContentVersion;
}

export interface BusinessProcessContentVersion {
    id: number;
    content_id: number;
    title: string;
    content: string;
    version_number: number;
    version_label: string;
    status: number; // 0=Draft, 1=Active, 2=Archived
    change_summary: string | null;
    creator_id: number;
    updater_id: number | null;
    created_at: string;
    updated_at: string;
    content?: BusinessProcessContent;
    creator?: User;
    updater?: User;
}

export interface BusinessProcessBlueprint {
    id: number;
    activity_id: number;
    title: string;
    is_archived: boolean;
    archived_date: string | null;
    archived_by: number | null;
    created_by: number;
    updated_by: number;
    created_at: string;
    updated_at: string;
    activity?: BusinessProcessActivity;
    creator?: User;
    updater?: User;
    archivedBy?: User;
    versions?: BlueprintVersion[];
    current_version?: BlueprintVersion;
}

export interface BlueprintVersion {
    id: number;
    blueprint_id: number;
    content: string;
    version_number: string;
    status: 0 | 1 | 2; // 0=Draft, 1=Current, 2=Future
    change_summary: string | null;
    metadata: Record<string, unknown> | null;
    created_by: number;
    updated_by: number;
    created_at: string;
    updated_at: string;
    blueprint?: BusinessProcessBlueprint;
    creator?: User;
    updater?: User;
}

