                                    <TableRow>
                                        <TableCell colSpan={8} className="px-3 py-12 text-center">
                                            No activities found for this core process.
                                        </TableCell>
                                    </TableRow>
                                )}
                            </SortableContext>
                        </TableBody>
                    </Table>
                </div>
            </DndContext>

            {/* RACI Legend */}
            <div className="mt-8 rounded-xl border border-gray-200 bg-gray-50 p-6">
                <h3 className="mb-4 text-lg font-semibold text-gray-900">RACI Legend</h3>
                <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-blue-200 bg-blue-100 text-blue-800">
                            R
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Responsible: Does the work</span>
                    </div>
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-green-200 bg-green-100 text-green-800">
                            A
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Accountable: Approves the work</span>
                    </div>
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-blue-200 bg-blue-100 text-blue-800">
                            C
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Consulted: Provides input</span>
                    </div>
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-orange-200 bg-orange-100 text-orange-800">
                            I
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Informed: Kept updated</span>
                    </div>
                </div>
            </div>

            {/* Create Activity Dialog */}
            <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
                <DialogContent className="sm:max-w-[625px]">
                    <DialogHeader>
                        <DialogTitle>Create New Activity for {core.name}</DialogTitle>
                        <DialogDescription>Fill in the details below to create a new process activity.</DialogDescription>
                    </DialogHeader>
                    <div className="grid gap-4 py-4">
                        <div>
                            <label className="text-sm font-medium text-gray-700">Activity Name *</label>
                            <Input
                                name="name"
                                value={createActivityData.name || ''}
                                onChange={handleCreateActivityFieldChange}
                                placeholder="Enter activity name"
                                className={createErrors.name ? 'border-red-500' : ''}
                            />
                            {createErrors.name && <p className="mt-1 text-xs text-red-500">{createErrors.name}</p>}
                        </div>
                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <label className="text-sm font-medium text-gray-700">Responsible</label>
                                <Input
                                    name="responsible"
                                    value={createActivityData.responsible || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who does the work"
                                />
                            </div>
                            <div>
                                <label className="text-sm font-medium text-gray-700">Accountable</label>
                                <Input
                                    name="accountable"
                                    value={createActivityData.accountable || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who approves the work"
                                />
                            </div>
                            <div>
                                <label className="text-sm font-medium text-gray-700">Consulted</label>
                                <Input
                                    name="consulted"
                                    value={createActivityData.consulted || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who provides input"
                                />
                            </div>
                            <div>
                                <label className="text-sm font-medium text-gray-700">Informed</label>
                                <Input
                                    name="informed"
                                    value={createActivityData.informed || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who is kept updated"
                                />
                            </div>
                        </div>
                    </div>
                    <DialogFooter>
                        <Button variant="outline" onClick={() => setCreateDialogOpen(false)} disabled={isCreatingActivity}>
                            Cancel
                        </Button>
                        <Button onClick={handleCreateActivity} disabled={isCreatingActivity}>
                            {isCreatingActivity ? 'Creating...' : 'Create Activity'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Delete Confirmation Dialog */}
            <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
                <AlertDialogContent>
                    <AlertDialogHeader>
                        <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                        <AlertDialogDescription>
                            This will permanently delete the activity "{activityToDelete?.name}". This action cannot be undone.
                        </AlertDialogDescription>
                    </AlertDialogHeader>
                    <AlertDialogFooter>
                        <AlertDialogCancel onClick={cancelDeleteActivity} disabled={isDeletingActivity}>
                            Cancel
                        </AlertDialogCancel>
                        <AlertDialogAction onClick={confirmDeleteActivity} disabled={isDeletingActivity}>
                            {isDeletingActivity ? 'Deleting...' : 'Delete'}
                        </AlertDialogAction>
                    </AlertDialogFooter>
                </AlertDialogContent>
            </AlertDialog>
        </div>
    );
}

