    AlertDialogFooter,
    AlertDialogHeader,
    AlertDialogTitle,
} from '@/components/ui/alert-dialog';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Input } from '@/components/ui/input';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { BusinessProcessActivity, BusinessProcessCore } from '@/types';
import { HtmlImportDialog } from '@/components/diagrams/html-import-dialog';
import { closestCenter, DndContext, DragEndEvent, KeyboardSensor, PointerSensor, useSensor, useSensors } from '@dnd-kit/core';
import { arrayMove, SortableContext, sortableKeyboardCoordinates, useSortable, verticalListSortingStrategy } from '@dnd-kit/sortable';
import { CSS } from '@dnd-kit/utilities';
import { router } from '@inertiajs/react';
import { clsx } from 'clsx';
import { FileText, Upload, GripVertical, Pencil, Plus, Save, Trash2, XCircle } from 'lucide-react';
import { ChangeEvent, memo, useEffect, useState } from 'react';

interface BusinessProcessActivitiesCardProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
    };
}

interface EditableActivityData {
    id: number;
    name: string;
    responsible: string;
    accountable: string;
    consulted: string;
    informed: string;
}

interface SortableRowProps {
    activity: BusinessProcessActivity;
    isEditing: boolean;
    editableData: Partial<EditableActivityData>;
    editErrors: Record<string, string>;
    isSavingEdit: boolean;
    onEditClick: (activity: BusinessProcessActivity) => void;
    onSaveEdit: () => void;
    onCancelEdit: () => void;
    onFieldChange: (e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
    onDelete: (id: number) => void;
    onBlueprintClick: (activity: BusinessProcessActivity) => void;
}

// Memoized row component for performance
const SortableRow = memo(function SortableRow({
    activity,
    isEditing,
    editableData,
    editErrors,
    isSavingEdit,
    onEditClick,
    onSaveEdit,
    onCancelEdit,
    onFieldChange,
    onDelete,
    onBlueprintClick,
}: SortableRowProps) {
    const { attributes, listeners, setNodeRef, transform, transition, isDragging } = useSortable({ id: activity.id });

    return (
        <TableRow
            ref={setNodeRef}
            className={clsx('hover:bg-gray-50', isEditing && 'bg-blue-50 dark:bg-blue-900/10', isDragging && 'z-10 opacity-50')}
            style={{
                transform: CSS.Transform.toString(transform),
                transition,
            }}
        >
            <TableCell className="w-8 px-3 py-4 text-center align-top">
                <div {...attributes} {...listeners} className="cursor-grab active:cursor-grabbing">
                    <GripVertical className="inline-block h-4 w-4 text-gray-400" />
                </div>
            </TableCell>
            {isEditing ? (
                <>
                    <TableCell className="px-3 py-4 align-top">
                        <Input
                            name="name"
                            value={editableData.name || ''}
                            onChange={onFieldChange}
                            className={clsx('text-sm', editErrors.name && 'border-red-500')}
                            placeholder="Activity Name"
                        />
                        {editErrors.name && <p className="mt-1 text-xs text-red-500">{editErrors.name}</p>}
                    </TableCell>
                    <TableCell className="hidden w-24 px-3 py-4 align-top sm:table-cell">
                        <Input
                            name="responsible"
                            value={editableData.responsible || ''}
                            onChange={onFieldChange}
                            placeholder="R"
                            className={clsx('text-sm', editErrors.responsible && 'border-red-500')}
                        />
                        {editErrors.responsible && <p className="mt-1 text-xs text-red-500">{editErrors.responsible}</p>}
                    </TableCell>
                    <TableCell className="hidden w-24 px-3 py-4 align-top sm:table-cell">
                        <Input
                            name="accountable"
                            value={editableData.accountable || ''}
                            onChange={onFieldChange}
                            placeholder="A"
                            className={clsx('text-sm', editErrors.accountable && 'border-red-500')}
                        />
                        {editErrors.accountable && <p className="mt-1 text-xs text-red-500">{editErrors.accountable}</p>}
                    </TableCell>
                    <TableCell className="hidden w-24 px-3 py-4 align-top md:table-cell">
                        <Input
                            name="consulted"
                            value={editableData.consulted || ''}
                            onChange={onFieldChange}
                            placeholder="C"
                            className={clsx('text-sm', editErrors.consulted && 'border-red-500')}
                        />
                        {editErrors.consulted && <p className="mt-1 text-xs text-red-500">{editErrors.consulted}</p>}
                    </TableCell>
                    <TableCell className="hidden w-24 px-3 py-4 align-top md:table-cell">
                        <Input
                            name="informed"
                            value={editableData.informed || ''}
                            onChange={onFieldChange}
                            placeholder="I"
                            className={clsx('text-sm', editErrors.informed && 'border-red-500')}
                        />
                        {editErrors.informed && <p className="mt-1 text-xs text-red-500">{editErrors.informed}</p>}
                    </TableCell>
                    <TableCell className="w-24 px-3 py-4 align-top">
                        {activity.blueprint ? (
                            <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => onBlueprintClick(activity)}
                                className="flex h-auto cursor-pointer items-center gap-1 p-1 text-blue-600 hover:text-blue-800"
                            >
                                <FileText className="h-4 w-4" />
                                <span className="text-xs">See SOP</span>
                            </Button>
                        ) : (
                            <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => onBlueprintClick(activity)}
                                className="flex h-auto cursor-pointer items-center gap-1 p-1 text-gray-400 hover:text-gray-600"
                            >
                                <FileText className="h-4 w-4" />
                                <span className="text-xs">Add SOP</span>
                            </Button>
                        )}
                    </TableCell>
                    <TableCell className="w-20 px-3 py-4 text-right align-top">
                        <div className="flex justify-end gap-1">
                            <Button variant="ghost" size="sm" onClick={onSaveEdit} disabled={isSavingEdit}>
                                <Save className="h-4 w-4 text-green-600" />
                            </Button>
                            <Button variant="ghost" size="sm" onClick={onCancelEdit} disabled={isSavingEdit}>
                                <XCircle className="h-4 w-4 text-red-500" />
                            </Button>
                        </div>
                    </TableCell>
                </>
            ) : (
                <>
                    <TableCell className="px-3 py-4 text-sm font-medium">{activity.name}</TableCell>
                    <TableCell className="hidden w-24 truncate px-3 py-4 text-sm sm:table-cell" title={activity.responsible || '-'}>
                        {activity.responsible || '-'}
                    </TableCell>
                    <TableCell className="hidden w-24 truncate px-3 py-4 text-sm sm:table-cell" title={activity.accountable || '-'}>
                        {activity.accountable || '-'}
                    </TableCell>
                    <TableCell className="hidden w-24 truncate px-3 py-4 text-sm md:table-cell" title={activity.consulted || '-'}>
                        {activity.consulted || '-'}
                    </TableCell>
                    <TableCell className="hidden w-24 truncate px-3 py-4 text-sm md:table-cell" title={activity.informed || '-'}>
                        {activity.informed || '-'}
                    </TableCell>
                    <TableCell className="w-24 px-3 py-4 text-sm">
                        {activity.blueprint ? (
                            <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => onBlueprintClick(activity)}
                                className="flex h-auto cursor-pointer items-center gap-1 p-1 text-blue-600 hover:text-blue-800"
                                title="View Blueprint"
                            >
                                <FileText className="h-4 w-4" />
                                <span className="text-xs">See SOP</span>
                            </Button>
                        ) : (
                            <Button
                                variant="ghost"
                                size="sm"
                                onClick={() => onBlueprintClick(activity)}
                                className="flex h-auto cursor-pointer items-center gap-1 p-1 text-gray-400 hover:text-gray-600"
                                title="Create Blueprint"
                            >
                                <FileText className="h-4 w-4" />
                                <span className="text-xs">Add SOP</span>
                            </Button>
                        )}
                    </TableCell>
                    <TableCell className="w-20 px-3 py-4 text-right">
                        <div className="flex justify-end gap-1">
                            <Button variant="ghost" size="sm" onClick={() => onEditClick(activity)}>
                                <Pencil className="h-4 w-4" />
                            </Button>
                            <Button variant="ghost" size="sm" onClick={() => onDelete(activity.id)}>
                                <Trash2 className="h-4 w-4 text-red-500" />
                            </Button>
                        </div>
                    </TableCell>
                </>
            )}
        </TableRow>
    );
});

export function BusinessProcessActivitiesCard({ core }: BusinessProcessActivitiesCardProps) {
    const [activities, setActivities] = useState<BusinessProcessActivity[]>(core.activities || []);

    const [editingActivityId, setEditingActivityId] = useState<number | null>(null);
    const [editableActivityData, setEditableActivityData] = useState<Partial<EditableActivityData>>({});
    const [isSavingEdit, setIsSavingEdit] = useState(false);
    const [editErrors, setEditErrors] = useState<Record<string, string>>({});

    // Delete activity dialog state
    const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
    const [activityToDelete, setActivityToDelete] = useState<BusinessProcessActivity | null>(null);
    const [isDeletingActivity, setIsDeletingActivity] = useState(false);

    // Create activity dialog state
    const [createDialogOpen, setCreateDialogOpen] = useState(false);
    const [createActivityData, setCreateActivityData] = useState<Partial<EditableActivityData>>({
        name: '',
        responsible: '',
        accountable: '',
        consulted: '',
        informed: '',
    });
    const [isCreatingActivity, setIsCreatingActivity] = useState(false);
    const [createErrors, setCreateErrors] = useState<Record<string, string>>({});

    // Import HTML dialog state
    const [importDialogOpen, setImportDialogOpen] = useState(false);

    // Sync local activities state when core.activities changes
    useEffect(() => {
        setActivities(core.activities || []);
    }, [core.activities]);

    const sensors = useSensors(
        useSensor(PointerSensor, {
            activationConstraint: {
                distance: 8,
            },
        }),
        useSensor(KeyboardSensor, {
            coordinateGetter: sortableKeyboardCoordinates,
        }),
    );

    const handleDragEnd = (event: DragEndEvent) => {
        const { active, over } = event;

        if (over && active.id !== over.id) {
            setActivities((items) => {
                const oldIndex = items.findIndex((item) => item.id === active.id);
                const newIndex = items.findIndex((item) => item.id === over.id);

                const newItems = arrayMove(items, oldIndex, newIndex);

                // Make API call to save the new order in the database
                const activityOrders = newItems.map((item, index) => ({
                    id: item.id,
                    order: index + 1, // 1-based indexing for database
                }));

                router.put(
                    route('business-processes.activities.reorder', [core.process?.id, core.id]),
                    {
                        activities: activityOrders,
                    },
                    {
                        preserveScroll: true,
                        onError: (errors) => {
                            console.error('Failed to update activity order:', errors);
                            // Revert the local state if there's an error
                            setActivities(core.activities || []);
                        },
                    },
                );

                return newItems;
            });
        }
    };

    const handleEditClick = (activity: BusinessProcessActivity) => {
        setEditingActivityId(activity.id);
        setEditableActivityData({
            id: activity.id,
            name: activity.name || '',
            responsible: activity.responsible || '',
            accountable: activity.accountable || '',
            consulted: activity.consulted || '',
            informed: activity.informed || '',
        });
        setEditErrors({});
    };

    const handleCancelEdit = () => {
        setEditingActivityId(null);
        setEditableActivityData({});
        setEditErrors({});
    };

    const handleEditableActivityFieldChange = (e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setEditableActivityData((prev) => ({ ...prev, [name]: value }));
        if (editErrors[name]) {
            setEditErrors((prev) => ({ ...prev, [name]: '' }));
        }
    };

    const handleSaveEdit = async () => {
        if (!editableActivityData || editingActivityId === null || editableActivityData.id === undefined) return;

        setIsSavingEdit(true);
        setEditErrors({});

        const payload = {
            ...editableActivityData,
        };
        delete payload.id;

        router.put(route('business-processes.activities.update', editingActivityId), payload as Record<string, string>, {
            preserveScroll: true,
            onSuccess: () => {
                handleCancelEdit();
            },
            onError: (errors) => {
                setEditErrors(errors as Record<string, string>);
            },
            onFinish: () => {
                setIsSavingEdit(false);
            },
        });
    };

    const handleDeleteActivity = (activityId: number) => {
        // Find the activity to delete
        const activity = activities.find((a) => a.id === activityId);
        if (activity) {
            setActivityToDelete(activity);
            setDeleteDialogOpen(true);
        }
    };

    const confirmDeleteActivity = () => {
        if (!activityToDelete) return;

        setIsDeletingActivity(true);

        router.delete(route('business-processes.activities.destroy', activityToDelete.id), {
            preserveScroll: true,
            onSuccess: () => {
                setDeleteDialogOpen(false);
                setActivityToDelete(null);
            },
            onFinish: () => {
                setIsDeletingActivity(false);
            },
        });
    };

    const cancelDeleteActivity = () => {
        setDeleteDialogOpen(false);
        setActivityToDelete(null);
    };

    const handleBlueprintClick = (activity: BusinessProcessActivity) => {
        // Navigate to blueprint page
        router.get(route('business-processes.blueprints.show', [core.process?.id, core.id, activity.id]));
    };

    // Create activity handlers
    const handleCreateActivityFieldChange = (e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        const { name, value } = e.target;
        setCreateActivityData((prev) => ({ ...prev, [name]: value }));
        if (createErrors[name]) {
            setCreateErrors((prev) => ({ ...prev, [name]: '' }));
        }
    };

    const handleCreateActivity = () => {
        setIsCreatingActivity(true);
        setCreateErrors({});

        router.post(route('business-processes.activities.store', [core.process?.id, core.id]), createActivityData as Record<string, string>, {
            preserveScroll: true,
            onSuccess: () => {
                setCreateDialogOpen(false);
                setCreateActivityData({
                    name: '',
                    responsible: '',
                    accountable: '',
                    consulted: '',
                    informed: '',
                });
            },
            onError: (errors) => {
                setCreateErrors(errors as Record<string, string>);
            },
            onFinish: () => {
                setIsCreatingActivity(false);
            },
        });
    };

    const renderActivityRows = () => {
        return activities.map((activity) => (
            <SortableRow
                key={activity.id}
                activity={activity}
                isEditing={editingActivityId === activity.id}
                editableData={editableActivityData}
                editErrors={editErrors}
                isSavingEdit={isSavingEdit}
                onEditClick={handleEditClick}
                onSaveEdit={handleSaveEdit}
                onCancelEdit={handleCancelEdit}
                onFieldChange={handleEditableActivityFieldChange}
                onDelete={handleDeleteActivity}
                onBlueprintClick={handleBlueprintClick}
            />
        ));
    };

    return (
        <div>
            <div className="mb-6">
                <div className="flex flex-col space-y-4 sm:flex-row sm:items-start sm:justify-between sm:space-y-0">
                    <div>
                        <h2 className="text-2xl font-bold">RACI Responsibility Matrix</h2>
                        <p className="mt-2 text-gray-600">
                            Define responsibilities using the RACI model (Responsible, Accountable, Consulted, Informed)
                        </p>
                    </div>
                    <div className="flex flex-col space-y-2 sm:flex-row sm:space-y-0 sm:space-x-2">
                        <Button onClick={() => setCreateDialogOpen(true)} className="w-full sm:w-auto">
                            <Plus className="mr-2 h-4 w-4" />
                            Add Activity
                        </Button>
                        <Button 
                            onClick={() => setImportDialogOpen(true)} 
                            variant="outline" 
                            className="w-full sm:w-auto"
                        >
                            <Upload className="mr-2 h-4 w-4" />
                            Import HTML
                        </Button>
                    </div>
                </div>
            </div>

            <DndContext sensors={sensors} collisionDetection={closestCenter} onDragEnd={handleDragEnd}>
                <div className="overflow-x-auto rounded-lg border border-gray-200 bg-white">
                    <Table>
                        <TableHeader>
                            <TableRow className="border-b border-gray-200 bg-gray-50">
                                <TableHead className="w-8 px-3 py-4 text-left text-sm font-medium text-gray-900"></TableHead>
                                <TableHead className="px-3 py-4 text-left text-sm font-medium text-gray-900">Activity</TableHead>
                                <TableHead className="hidden w-24 px-3 py-4 text-left text-sm font-medium text-gray-900 sm:table-cell">R</TableHead>
                                <TableHead className="hidden w-24 px-3 py-4 text-left text-sm font-medium text-gray-900 sm:table-cell">A</TableHead>
                                <TableHead className="hidden w-24 px-3 py-4 text-left text-sm font-medium text-gray-900 md:table-cell">C</TableHead>
                                <TableHead className="hidden w-24 px-3 py-4 text-left text-sm font-medium text-gray-900 md:table-cell">I</TableHead>
                                <TableHead className="w-24 px-3 py-4 text-left text-sm font-medium text-gray-900">SOP</TableHead>
                                <TableHead className="w-20 px-3 py-4 text-right text-sm font-medium text-gray-900">Actions</TableHead>
                            </TableRow>
                        </TableHeader>
                        <TableBody className="divide-y divide-gray-200 bg-white">
                            <SortableContext items={activities.map((a) => a.id)} strategy={verticalListSortingStrategy}>
                                {activities.length > 0 ? (
                                    renderActivityRows()
                                ) : (
                                    <TableRow>
                                        <TableCell colSpan={8} className="px-3 py-12 text-center">
                                            No activities found for this core process.
                                        </TableCell>
                                    </TableRow>
                                )}
                            </SortableContext>
                        </TableBody>
                    </Table>
                </div>
            </DndContext>

            {/* RACI Legend */}
            <div className="mt-8 rounded-xl border border-gray-200 bg-gray-50 p-6">
                <h3 className="mb-4 text-lg font-semibold text-gray-900">RACI Legend</h3>
                <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-4">
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-blue-200 bg-blue-100 text-blue-800">
                            R
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Responsible: Does the work</span>
                    </div>
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-green-200 bg-green-100 text-green-800">
                            A
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Accountable: Approves the work</span>
                    </div>
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-blue-200 bg-blue-100 text-blue-800">
                            C
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Consulted: Provides input</span>
                    </div>
                    <div className="flex items-center space-x-2">
                        <Badge variant="secondary" className="border-orange-200 bg-orange-100 text-orange-800">
                            I
                        </Badge>
                        <span className="text-sm font-medium text-gray-900">Informed: Kept updated</span>
                    </div>
                </div>
            </div>

            {/* Create Activity Dialog */}
            <Dialog open={createDialogOpen} onOpenChange={setCreateDialogOpen}>
                <DialogContent className="sm:max-w-[625px]">
                    <DialogHeader>
                        <DialogTitle>Create New Activity for {core.name}</DialogTitle>
                        <DialogDescription>Fill in the details below to create a new process activity.</DialogDescription>
                    </DialogHeader>
                    <div className="grid gap-4 py-4">
                        <div>
                            <label className="text-sm font-medium text-gray-700">Activity Name *</label>
                            <Input
                                name="name"
                                value={createActivityData.name || ''}
                                onChange={handleCreateActivityFieldChange}
                                placeholder="Enter activity name"
                                className={createErrors.name ? 'border-red-500' : ''}
                            />
                            {createErrors.name && <p className="mt-1 text-xs text-red-500">{createErrors.name}</p>}
                        </div>
                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <label className="text-sm font-medium text-gray-700">Responsible</label>
                                <Input
                                    name="responsible"
                                    value={createActivityData.responsible || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who does the work"
                                />
                            </div>
                            <div>
                                <label className="text-sm font-medium text-gray-700">Accountable</label>
                                <Input
                                    name="accountable"
                                    value={createActivityData.accountable || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who approves the work"
                                />
                            </div>
                            <div>
                                <label className="text-sm font-medium text-gray-700">Consulted</label>
                                <Input
                                    name="consulted"
                                    value={createActivityData.consulted || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who provides input"
                                />
                            </div>
                            <div>
                                <label className="text-sm font-medium text-gray-700">Informed</label>
                                <Input
                                    name="informed"
                                    value={createActivityData.informed || ''}
                                    onChange={handleCreateActivityFieldChange}
                                    placeholder="Who is kept updated"
                                />
                            </div>
                        </div>
                    </div>
                    <DialogFooter>
                        <Button variant="outline" onClick={() => setCreateDialogOpen(false)} disabled={isCreatingActivity}>
                            Cancel
                        </Button>
                        <Button onClick={handleCreateActivity} disabled={isCreatingActivity}>
                            {isCreatingActivity ? 'Creating...' : 'Create Activity'}
                        </Button>
                    </DialogFooter>
                </DialogContent>
            </Dialog>

            {/* Delete Confirmation Dialog */}
            <AlertDialog open={deleteDialogOpen} onOpenChange={setDeleteDialogOpen}>
                <AlertDialogContent>
                    <AlertDialogHeader>
                        <AlertDialogTitle>Are you sure?</AlertDialogTitle>
                        <AlertDialogDescription>
                            This will permanently delete the activity "{activityToDelete?.name}". This action cannot be undone.
                        </AlertDialogDescription>
                    </AlertDialogHeader>
                    <AlertDialogFooter>
                        <AlertDialogCancel onClick={cancelDeleteActivity} disabled={isDeletingActivity}>
                            Cancel
                        </AlertDialogCancel>
                        <AlertDialogAction onClick={confirmDeleteActivity} disabled={isDeletingActivity}>
                            {isDeletingActivity ? 'Deleting...' : 'Delete'}
                        </AlertDialogAction>
                    </AlertDialogFooter>
                </AlertDialogContent>
            </AlertDialog>

            {/* HTML Import Dialog */}
            <HtmlImportDialog
                open={importDialogOpen}
                onOpenChange={setImportDialogOpen}
                onImport={(htmlContent: string) => {
                    // Make API call to import HTML
                    router.post(
                        route('business-processes.cores.import-html-raci', [core.process?.id, core.id]),
                        { html_content: htmlContent },
                        {
                            preserveScroll: true,
                            onSuccess: () => {
                                setImportDialogOpen(false);
                                // Page will be refreshed automatically by Inertia
                            },
                            onError: (errors) => {
                                console.error('Import failed:', errors);
                                // Error handling will be done by the dialog
                            },
                        }
                    );
                }}
                mode="raci"
                title="Import HTML to RACI Activities"
                description="Upload an HTML file containing process activities. The system will extract activity categories and RACI assignments, replacing all existing activities in this core process."
            />
        </div>
    );
}

