file.content:
export interface Comment {
    id: string;
    text: string;
    author: string;
    timestamp: string;
    resolved: boolean;
    replies: Comment[];
}

export interface TinyMCEEditor {
    annotator?: {
        register: (name: string, config: {
            persistent: boolean;
            decorate: (uid: string, data: Record<string, unknown>) => {
                attributes: Record<string, string>;
                classes?: string[];
            };
        }) => void;
        annotate: (name: string, data: { uid: string; [key: string]: unknown }) => void;
        remove: (name: string) => void;
        getAll: (name: string) => Record<string, Element[]>;
        annotationChanged: (name: string, callback: (state: boolean, name: string, obj?: { uid: string; nodes: Element[] }) => void) => void;
    };
    ui: {
        registry: {
            addButton: (name: string, config: {
                text: string;
                icon: string;
                tooltip: string;
                onAction: () => void;
            }) => void;
            addSidebar: (name: string, config: {
                tooltip: string;
                icon: string;
                onSetup: () => () => void;
                onShow: (api: { element: () => HTMLElement }) => () => void;
                onHide: () => void;
            }) => void;
        };
    };
    selection: {
        getContent: (options: { format: string }) => string;
        setRng: (range: Range) => void;
        getRng: () => Range;
    };
    notificationManager: {
        open: (config: { text: string; type: string }) => void;
    };
    on: (event: string, callback: (e?: { target?: HTMLElement }) => void) => void;
    getContainer: () => HTMLElement;
    getContent: () => string;
    setContent: (content: string) => void;
    dom: {
        select: (selector: string) => HTMLElement[];
        createRng: () => Range;
    };
}

export interface WindowWithCommentFunctions extends Window {
    toggleCommentResolution: (annotationId: string) => void;
    replyToComment: (annotationId: string) => void;
    removeComment: (annotationId: string) => void;
    renderCommentsSidebar?: () => void;
}

export interface RichTextEditorProps {
    content: string;
    onChange?: (content: string) => void;
    placeholder?: string;
    className?: string;
    editable?: boolean;
    currentUser?: {
        id: string;
        name: string;
        email?: string;
    };
    onAutoSave?: () => void;
}

export interface SidebarApi {
    element: () => HTMLElement;
}

