import { BusinessProcessCore } from '@/types';
import { Eye, FileText, Calendar, User } from 'lucide-react';
import { useMemo } from 'react';

interface VisualTabContentProps {
    core: BusinessProcessCore & {
        process: { id: number; name: string };
    };
}

export function VisualTabContent({ core }: VisualTabContentProps) {
    // Find the visual diagram content
    const visualContent = useMemo(() => {
        return core.contents?.find(content => content.type === 'visual_diagram');
    }, [core.contents]);

    // Get the current/latest version of the visual content
    const currentVersion = useMemo(() => {
        if (!visualContent) return null;
        return visualContent.current_version || visualContent.versions?.[0];
    }, [visualContent]);

    const hasContent = !!currentVersion?.content;

    if (!hasContent) {
        return (
            <div className="flex h-full flex-col">
                {/* Content Body */}
                <div className="flex-1 overflow-auto">
                    <div className="h-full">
                        <div className="flex h-full flex-col bg-white">
                            {/* Placeholder Content */}
                            <div className="flex h-full flex-1 items-center justify-center p-6">
                                <div className="text-center">
                                    <Eye className="mx-auto mb-4 h-16 w-16 text-gray-300" />
                                    <h3 className="mb-2 text-lg font-medium text-gray-900">Visual Diagram for {core.name}</h3>
                                    <p className="text-gray-500 max-w-md mx-auto">
                                        No visual content available. Import HTML content through the RACI tab to automatically generate visual diagrams here.
                                    </p>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="flex h-full flex-col">
            {/* Content Header with Version Info */}
            {currentVersion && (
                <div className="border-b border-gray-200 bg-gray-50 px-6 py-4">
                    <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-4">
                            <div className="flex items-center space-x-2">
                                <FileText className="h-5 w-5 text-gray-400" />
                                <span className="text-sm font-medium text-gray-900">
                                    Visual Diagram - Version {currentVersion.version_number}
                                </span>
                            </div>
                            {currentVersion.change_summary && (
                                <span className="text-sm text-gray-600">
                                    {currentVersion.change_summary}
                                </span>
                            )}
                        </div>
                        <div className="flex items-center space-x-4 text-sm text-gray-500">
                            {currentVersion.creator && (
                                <div className="flex items-center space-x-1">
                                    <User className="h-4 w-4" />
                                    <span>{currentVersion.creator.name}</span>
                                </div>
                            )}
                            {currentVersion.created_at && (
                                <div className="flex items-center space-x-1">
                                    <Calendar className="h-4 w-4" />
                                    <span>{new Date(currentVersion.created_at).toLocaleDateString()}</span>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            )}

            {/* Content Body */}
            <div className="flex-1 overflow-auto">
                <div className="h-full">
                    <div className="flex h-full flex-col bg-white">
                        {/* HTML Content Display */}
                        <div className="flex-1 p-6">
                            <div 
                                className="prose prose-sm max-w-none"
                                dangerouslySetInnerHTML={{ __html: currentVersion.content }}
                                style={{
                                    fontSize: '14px',
                                    lineHeight: '1.6',
                                }}
                            />
                        </div>
                    </div>
                </div>
            </div>

            {/* Content Footer with Actions */}
            <div className="border-t border-gray-200 bg-gray-50 px-6 py-3">
                <div className="flex items-center justify-between">
                    <div className="text-sm text-gray-500">
                        {currentVersion?.status === 1 ? (
                            <span className="inline-flex items-center rounded-full bg-green-100 px-2.5 py-0.5 text-xs font-medium text-green-800">
                                Current
                            </span>
                        ) : (
                            <span className="inline-flex items-center rounded-full bg-gray-100 px-2.5 py-0.5 text-xs font-medium text-gray-800">
                                Draft
                            </span>
                        )}
                    </div>
                    <div className="text-sm text-gray-500">
                        Imported from HTML via RACI tab
                    </div>
                </div>
            </div>
        </div>
    );
}

