// Let's see the exact structure of the visual content
use App\\Models\\BusinessProcessCore;

\$core = BusinessProcessCore::with(['contents'])->find(2);
\$visualContent = \$core->contents->where('type', 'visual_diagram')->first();

if (\$visualContent) {
    echo 'Visual content structure:' . PHP_EOL;
    echo 'ID: ' . \$visualContent->id . PHP_EOL;
    echo 'Type: ' . \$visualContent->type . PHP_EOL;
    echo 'Title: ' . \$visualContent->title . PHP_EOL;
    echo 'Has content field: ' . (isset(\$visualContent->content) ? 'YES' : 'NO') . PHP_EOL;
    echo 'Content length: ' . (\$visualContent->content ? strlen(\$visualContent->content) : 'NULL') . PHP_EOL;
    echo 'Has versions: ' . \$visualContent->versions->count() . PHP_EOL;

    if (\$visualContent->versions->count() > 0) {
        \$version = \$visualContent->versions->first();
        echo 'Version content length: ' . strlen(\$version->content) . PHP_EOL;
        echo 'Version vs Content match: ' . ((\$visualContent->content ?? '') === \$version->content ? 'YES' : 'NO') . PHP_EOL;
    }

    // Check the actual attributes
    echo 'Attributes: ' . json_encode(array_keys(\$visualContent->getAttributes())) . PHP_EOL;
} else {
    echo 'No visual content found.' . PHP_EOL;
}
"

