/**
 * Extracts CSS and clean HTML from a full HTML document.
 * Removes DOCTYPE, html, head tags but preserves internal CSS styles.
 *
 * @param string $htmlContent Full HTML document
 * @return string Clean HTML with preserved CSS in style tags
 */
public function extractCleanHtmlWithStyles(string $htmlContent): string
{
    Log::debug('[HtmlParser] Extracting clean HTML with styles');

    if (!$htmlContent || (!str_contains($htmlContent, '<!DOCTYPE') && !str_contains($htmlContent, '<html'))) {
        // Already clean HTML
        return $htmlContent;
    }

    $dom = new DOMDocument();
    libxml_use_internal_errors(true);
    $dom->loadHTML($htmlContent, LIBXML_HTML_NOIMPLIED | LIBXML_HTML_NODEFDTD);
    libxml_clear_errors();

    $cleanContent = '';

    // Extract CSS from head
    $styles = $dom->getElementsByTagName('style');
    foreach ($styles as $style) {
        $cleanContent .= '<style>' . $style->textContent . '</style>';
    }

    // Extract body content
    $body = $dom->getElementsByTagName('body')->item(0);
    if ($body) {
        $cleanContent .= $body->innerHTML;
    }

    Log::debug('[HtmlParser] Clean HTML extracted', [
        'original_length' => strlen($htmlContent),
        'clean_length' => strlen($cleanContent)
    ]);

    return $cleanContent;
}

