   166→    /**
   167→     * Display a listing of cores for a specific process
   168→     */
   169→    public function index(BusinessProcessProcess $process): Response
   170→    {
   171→        // Eager load the category for breadcrumbs
   172→        $process->load('category');
   173→
   174→        // Retrieve cores related to the specific process
   175→        // and belonging to the authenticated user, then paginate.
   176→        $cores = $process->cores() // Access the HasMany relationship
   177→            ->where('user_id', Auth::id()) // Filter by the current user
   178→            ->withCount('activities') // Add activities count
   179→            ->orderBy('updated_at', 'desc')
   180→            ->paginate(10);
   181→
   182→        // Transform cores to diagrams for frontend compatibility
   183→        $cores->getCollection()->transform(function ($core) {
   184→            $coreArray = $core->toArray();
   185→            return $coreArray;
   186→        });
   187→
   188→        // The $process model instance is already available.
   189→        // We pass the filtered and paginated $cores to the view.
   190→        return Inertia::render('business-processes/cores/index', [
   191→            'process' => $process,
   192→            'cores' => $cores,
   193→        ]);
   194→    }
