   196→    /**
   197→     * Store a newly created core
   198→     */
   199→    public function store(Request $request, BusinessProcessProcess $process): RedirectResponse
   200→    {
   201→        // Check if user can create cores
   202→        $this->authorize('create', BusinessProcessCore::class);
   203→
   204→        $validatedData = $request->validate([
   205→            'name' => 'required|string|max:255',
   206→            'description' => 'nullable|string',
   207→            'version' => 'nullable|string|max:10',
   208→        ]);
   209→
   210→        $core = $process->cores()->create([
   211→            'name' => $validatedData['name'],
   212→            'description' => $validatedData['description'],
   213→            'version' => $validatedData['version'] ?? '1.0',
   214→            'owner' => Auth::user()->name, // Set authenticated user as owner
   215→            'status' => 0,     // Default status (e.g., Draft)
   216→            'user_id' => Auth::id()
   217→        ]);
   218→
   219→        return redirect()->route('business-processes.cores.show', [$process->id, $core->id])->with('success', 'Core created successfully.');
