   352→    /**
   353→     * Update content for SOP, Visual, or Swimlane
   354→     */
   355→    public function updateContent(Request $request, BusinessProcessProcess $process, BusinessProcessCore $core, string $type): RedirectResponse
   356→    {
   357→        $this->authorize('update', $core);
   358→
   359→        // Validate content type
   360→        if (!in_array($type, ['sop', 'visual_diagram', 'swimlane'])) {
   361→            abort(400, 'Invalid content type');
   362→        }
   363→
   364→        $validated = $request->validate([
   365→            'title' => 'required|string|max:255',
   366→            'content' => 'required|string',
   367→            'change_summary' => 'nullable|string',
   368→            'metadata' => 'nullable|array',
   369→        ]);
   370→
   371→        DB::transaction(function () use ($core, $type, $validated) {
   372→            // Get or create content record
   373→            $content = $core->contents()->where('type', $type)->first();
   374→
   375→            if (!$content) {
   376→                $content = $core->contents()->create([
   377→                    'type' => $type,
   378→                    'title' => $validated['title'],
   379→                    'created_by' => Auth::id(),
   380→                    'updated_by' => Auth::id(),
   381→                ]);
   382→            } else {
   383→                $content->update([
   384→                    'title' => $validated['title'],
   385→                    'updated_by' => Auth::id(),
   386→                ]);
   387→            }
   388→
   389→            // Create new version
   390→            $latestVersion = $content->versions()->orderBy('version_number', 'desc')->first();
   391→            $nextVersionNumber = $latestVersion ?
   392→                (float)$latestVersion->version_number + 0.1 :
   393→                1.0;
   394→
   395→            $content->versions()->create([
   396→                'content' => $validated['content'],
   397→                'version_number' => number_format($nextVersionNumber, 1),
   398→                'status' => 1, // 1 = current
   399→                'change_summary' => $validated['change_summary'] ?? 'Content updated',
   400→                'metadata' => $validated['metadata'] ?? [],
   401→                'created_by' => Auth::id(),
   402→                'updated_by' => Auth::id(),
   403→            ]);
   404→
   405→            // Mark previous versions as archived
   406→            $content->versions()
   407→                ->where('version_number', '!=', number_format($nextVersionNumber, 1))
   408→                ->update(['status' => 2]); // 2 = archived
   409→        });
   410→
   411→        return redirect()->back()->with('success', ucfirst($type) . ' content updated successfully.');
   412→    }
