   414→    /**
   415→     * Store new content (SOP, Visual, or Swimlane)
   416→     */
   417→    public function storeContent(Request $request, BusinessProcessProcess $process, BusinessProcessCore $core): RedirectResponse
   418→    {
   419→        $this->authorize('update', $core);
   420→
   421→        try {
   422→            // Log incoming request data
   423→            Log::info('storeContent - Incoming request', [
   424→                'all_data' => $request->all(),
   425→                'content_length' => strlen($request->get('content', '')),
   426→                'content_preview' => substr($request->get('content', ''), 0, 100),
   427→            ]);
   428→
   429→            $validated = $request->validate([
   430→                'content_type' => 'required|string|in:sop,visual_diagram,swimlane',
   431→                'title' => 'required|string|max:255',
   432→                'content' => 'required|string|min:1', // Content is required and must not be empty
   433→                'change_summary' => 'nullable|string',
   434→                'version_label' => 'nullable|string|max:50',
   435→                'metadata' => 'nullable|array',
   436→                'core_id' => 'nullable|integer', // Frontend sends this but we don't need it since we have $core
   437→            ]);
   438→
   439→            Log::info('storeContent - Validation passed', [
   440→                'validated_content_length' => strlen($validated['content']),
   441→                'validated_content_preview' => substr($validated['content'], 0, 100),
   442→            ]);
   443→
   444→            DB::transaction(function () use ($core, $validated) {
   445→                Log::info('storeContent - Starting transaction');
   446→
   447→                // Get or create content record using the relationship
   448→                $content = $core->contents()->where('type', $validated['content_type'])->first();
   449→                Log::info('storeContent - Content lookup', ['found_existing' => !!$content]);
   450→
   451→                if (!$content) {
   452→                    $content = $core->contents()->create([
   453→                        'type' => $validated['content_type'],
   454→                        'title' => $validated['title'],
   455→                        'created_by' => Auth::id(),
   456→                        'updated_by' => Auth::id(),
   457→                    ]);
   458→                    Log::info('storeContent - Created new content', ['content_id' => $content->id]);
   459→                } else {
   460→                    $content->update([
   461→                        'title' => $validated['title'],
   462→                        'updated_by' => Auth::id(),
   463→                    ]);
   464→                    Log::info('storeContent - Updated existing content', ['content_id' => $content->id]);
   465→                }
   466→
   467→                // Determine next version number as string
   468→                $latestVersion = $content->versions()->orderBy('version_number', 'desc')->first();
   469→                $nextVersionNumber = $latestVersion ?
   470→                    ((float)$latestVersion->version_number + 1.0) :
   471→                    1.0;
   472→
   473→                Log::info('storeContent - Creating version', [
   474→                    'latest_version' => $latestVersion?->version_number,
   475→                    'next_version' => $nextVersionNumber
   476→                ]);
   477→
   478→                $newVersion = $content->versions()->create([
   479→                    'content' => $validated['content'] ?? '', // Handle null content
   480→                    'version_number' => (string)$nextVersionNumber,
   481→                    'status' => 'draft', // Use string status
   482→                    'change_summary' => $validated['change_summary'] ?? 'New content created',
   483→                    'metadata' => $validated['metadata'] ?? [],
   484→                    'created_by' => Auth::id(),
   485→                    'updated_by' => Auth::id(),
   486→                ]);
   487→
   488→                Log::info('storeContent - Created version successfully', ['version_id' => $newVersion->id]);
   489→                Log::info('storeContent - Transaction completed successfully');
   490→            });
   491→
   492→            return redirect()->back()->with('success', ucfirst($validated['content_type']) . ' content created successfully.');
   493→        } catch (\Illuminate\Validation\ValidationException $e) {
   494→            Log::error('Validation failed in storeContent', ['errors' => $e->errors(), 'request' => $request->all()]);
   495→            return redirect()->back()->withErrors($e->errors())->withInput();
   496→        } catch (\Exception $e) {
   497→            Log::error('Error in storeContent', ['error' => $e->getMessage(), 'request' => $request->all()]);
   498→            return redirect()->back()->with('error', 'Failed to save content: ' . $e->getMessage());
   499→        }
   500→    }
